INSERT INTO astalavista_module_repository VALUES (213, 6, '{TEXT}\r\n<b><br>\r\nKundenkontakt gew&uuml;nscht - Erm&ouml;glichen Sie Ihren Besuchern eine unkomplizierte \r\nKontaktaufnahme</b><br>\r\n<br>\r\nStellen Sie Ihren Besuchern ein Kontaktformular zur Verf&uuml;gung. Der Inhalt \r\ndes Formulars wird direkt an eine beliebige Email-Adresse gesendet.<br>\r\n<br>\r\n<br>\r\n<TABLE cellPadding=2 width="100%" border=0 cellSpacing=0>\r\n  <TBODY> \r\n  <TR> \r\n    <TD width="25%" bgColor=#e9e9e9><B>Ihre Anschrift</B> </TD>\r\n    <TD width="25%" bgColor=#e9e9e9> \r\n      <div align="right">&nbsp;&nbsp;</div>\r\n    </TD>\r\n    <TD width="5%">&nbsp;</TD>\r\n    <TD width="25%">&nbsp;</TD>\r\n  </TR>\r\n  <TR> \r\n    <TD colspan="2"><BR>\r\n      <FORM name="ContactForm" action="index.php?section=contact" method="post">\r\n        <TABLE cellSpacing=1 cellPadding=2 width="100%" border=0>\r\n          <TBODY> \r\n          <TR> \r\n            <TD width="30%">&nbsp; </TD>\r\n            <TD width="70%"> \r\n              <input type=radio value=Frau name=anrede>\r\n              Frau&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; \r\n              <INPUT type=radio value=Herr name=anrede>\r\n              Herr</TD>\r\n          </TR>\r\n          </TBODY> \r\n        </TABLE>\r\n        <TABLE cellSpacing=1 cellPadding=2 width="100%" border=0>\r\n          <TBODY> \r\n          <TR> \r\n            <TD width="30%">Name<FONT color=#000000 size=-1>*</FONT></TD>\r\n            <TD width="70%"> \r\n              <INPUT class=input size=40 name=name>\r\n            </TD>\r\n          </TR>\r\n          <TR> \r\n            <TD width="30%">Vorname</TD>\r\n            <TD width="70%"> \r\n              <INPUT class=input size=40 name=vorname>\r\n            </TD>\r\n          </TR>\r\n          <TR> \r\n            <TD width="30%">Adresse<FONT size=-1>*</FONT></TD>\r\n            <TD width="70%"> \r\n              <input class=input size=40 name=adresse>\r\n            