INSERT INTO astalavista_module_repository VALUES (423, 20, '<script language="JavaScript">\r\n\r\nfunction CheckForm() {\r\nwith( document.editForm ) {\r\nif (name.value == "" || nickname.value == "" || email.value == "" || (password.value.length < 6 && password.value.length > 0)) \r\n{\r\nalert ("\\n{TXT_FIELD_REQUIRED}");       \r\nreturn false;\r\n}  \r\nif (!isEmailValid(email.value))\r\n{\r\nalert ("\\n{TXT_EMAIL_REQUIRED}");       \r\nreturn false;\r\n}  	\r\nreturn true;\r\n}\r\nfunction isEmailValid(checkThisEmail)\r\n{\r\nvar myEMailIsValid = true;\r\nvar myAtSymbolAt = checkThisEmail.indexOf(\'@\');\r\nvar myLastDotAt = checkThisEmail.lastIndexOf(\'.\');\r\nvar mySpaceAt = checkThisEmail.indexOf(\' \');\r\nvar myLength = checkThisEmail.length;\r\nif (myAtSymbolAt < 1 ) \r\n{myEMailIsValid = false}\r\nif (myLastDotAt < myAtSymbolAt) \r\n{myEMailIsValid = false}\r\nif (myLength - myLastDotAt <= 2) \r\n{myEMailIsValid = false}\r\nif (mySpaceAt != -1) \r\n{myEMailIsValid = false}\r\nreturn myEMailIsValid\r\n}\r\n}\r\n</SCRIPT>\r\n<table width="100%" border="0">\r\n<tr> \r\n<td width="50%"><font color="#FF0000">{FORUM_STATUS}</font></td>\r\n<td width="50%"> \r\n<div align="right">{FORUM_LOGIN}</div>\r\n</td>\r\n</tr>\r\n</table>\r\n<form name="editForm" method="post" action="?section=forum" onsubmit="return CheckForm()">\r\n<table width="100%" cellspacing="1" cellpadding="1" border="0" align="top" bgcolor="#999999">\r\n<tr bgcolor="#E8E8E8"> \r\n<td nowrap colspan="2" height="24"><b>{TXT_EDITUSER}</b></td>\r\n</tr>\r\n<tr bgcolor="white"> \r\n<td width="13%">{TXT_NAME}:<font color="red">*</font></td>\r\n<td nowrap width="88%"> <b> \r\n<input type="text" name="name" maxlength="50" size="40" value="{FORUM_USER_EDIT_NAME}">\r\n</b></td>\r\n</tr>\r\n<tr bgcolor="white"> \r\n<td width="13%">{TXT_NICKNAME}:<font color="red">*</font></td>\r\n<td nowrap width="88%"> <b> \r\n<input type="text" name="nickname" maxlength="30" size="40" value="{FORUM_USER_EDIT_NICKNAME}">\r\n</b></td>\r\n</tr>\r\n<tr bgcolor="white"> \r\n<td width="13%">{TXT_PASSWORD}:</td>\r\n<td nowrap width="88%"><b> \r\n<input type="password" name="password" maxlength="20" size="40">\r\n<font color="red">**</font> {TXT_PASSWORD_MINIMAL_CHARACTERS}</b></td>\r\n</tr>\r\n<tr bgcolor="white"> \r\n<td width="13%">{TXT_EMAIL}:<font color="red">*</font></td>\r\n<td nowrap width="88%"><b> \r\n<input type="text" name="email" maxlength="30" size="40" value="{FORUM_USER_EDIT_EMAIL}">\r\n</b></td>\r\n</tr>\r\n</table>\r\n<br>\r\n<input type="reset" name="Reset" value="{TXT_RESET}">\r\n<input type="submit" name="useredit" value="{TXT_FUNCTION}">\r\n<br>\r\n<br>\r\n[ <font color="red">*</font> ] {TXT_ALL_FIELDS_REQUIRED}. <br>\r\n[<font color="red">&nbsp;**&nbsp;</font>]&nbsp;{TXT_PASSWORD_FIELD_EMPTY} <br>\r\n</form>', 'Benutzer Bearbeiten', 'useredit', 'y', 416, 'off', 2, '1');
