INSERT INTO `contrexx_module_repository` VALUES (439, 12, '{STATUS}\r\n<!-- BEGIN login -->\r\n<form name=\\"addEntry\\" enctype=\\"multipart/form-data\\" method=\\"post\\" action=\\"?section=directory&cmd=add\\" onSubmit=\\"return CheckForm()\\">\r\n  <table border=\\"0\\" cellpadding=\\"3\\" cellspacing=\\"0\\" align=\\"center\\" width=\\"100%\\">\r\n    <tr> \r\n      <td width=\\"150\\">{TXT_USERNAME}:</td>\r\n      <td><input name=\\"username\\"  style=\\"width:220px;\\"></td>\r\n    </tr>\r\n    <tr> \r\n      <td width=\\"150\\">{TXT_PW}:</td>\r\n      <td><input type=\\"password\\" name=\\"password\\"  style=\\"width:220px;\\"></td>\r\n    </tr>\r\n    <tr> \r\n      <td width=\\"150\\">&nbsp;</td>\r\n      <td><input type=\\"submit\\" name=\\"login\\" value=\\"{TXT_LOGIN}\\"></td>\r\n    </tr>\r\n    <tr> \r\n      <td width=\\"150\\">&nbsp;</td>\r\n      <td><a href=\\"?section=directory&cmd=lostpw\\">{TXT_LOST_PW}</a></td>\r\n    </tr>\r\n    <tr> \r\n      <td width=\\"150\\">&nbsp;</td>\r\n      <td><br /></td>\r\n    </tr>\r\n    <tr> \r\n      <td width=\\"150\\">&nbsp;</td>\r\n      <td>{TXT_NOLOGIN}</td>\r\n    </tr>\r\n</table>\r\n</form>\r\n<br />\r\n<!-- END login -->\r\n<!-- BEGIN newFeed -->\r\n<script language=\\"JavaScript\\">\r\nfunction CheckForm() {\r\n  with( document.addEntry ) {\r\n    if (new_description.value == \\"\\" || new_name.value == \\"\\") \r\n    {\r\n        alert (\\"{TXT_FIELDS_REQUIRED}\\");       \r\n        return false;\r\n    }   	\r\n    return true;\r\n  }\r\n}\r\n\r\nfunction hideRow(type)\r\n{ \r\n	if (type==\\''file\\'')\r\n	{\r\n		document.getElementById(\\''hiddenfile\\'').style.display = \\''block\\'';\r\n		document.getElementById(\\''hiddenlink\\'').style.display = \\''none\\'';\r\n		document.getElementById(\\''hiddenrss\\'').style.display = \\''none\\'';\r\n		document.addEntry.linkname.value = \\''http://\\'';\r\n		document.addEntry.rssname.value = \\''http://\\'';\r\n		return true;\r\n	}\r\n	else if (type==\\''rss\\'')\r\n	{\r\n		document.getElementById(\\''hiddenlink\\'').style.display = \\''none\\'';\r\n		document.getElementById(\\''hiddenfile\\'').style.display = \\''none\\'';\r\n		document.getElementById(\\''hiddenrss\\'').style.display = \\''block\\'';\r\n		document.addEntry.linkname.value = \\''http://\\'';\r\n		return true;\r\n	}\r\n	else\r\n	{\r\n		document.getElementById(\\''hiddenlink\\'').style.display = \\''block\\'';\r\n		document.getElementById(\\''hiddenfile\\'').style.display = \\''none\\'';\r\n		document.getElementById(\\''hiddenrss\\'').style.display = \\''none\\'';\r\n		document.addEntry.rssname.value = \\''http://\\'';\r\n		return true;\r\n	}\r\n}\r\n</script>\r\n<form name=\\"addEntry\\" enctype=\\"multipart/form-data\\" method=\\"post\\" action=\\"?section=directory&cmd=add\\" onSubmit=\\"return CheckForm()\\">\r\n  <table border=\\"0\\" cellpadding=\\"3\\" cellspacing=\\"0\\" align=\\"center\\" width=\\"100%\\">\r\n    <tr class=\\"row1\\"> \r\n      <td width=\\"150\\">{TXT_FILETYPE}:</td>\r\n      <td> \r\n         <input type=\\"radio\\" name=\\"type\\" value=\\"rss\\" onClick=\\"javascript:hideRow(\\''rss\\'')\\" checked>\r\n        {TXT_RSSLINK}\r\n        <input type=\\"radio\\" name=\\"type\\" value=\\"file\\" onClick=\\"javascript:hideRow(\\''file\\'')\\">\r\n        {TXT_FILE} \r\n        <input type=\\"radio\\" name=\\"type\\" value=\\"link\\" onClick=\\"javascript:hideRow(\\''link\\'')\\">\r\n        {TXT_LINK}</td>\r\n    </tr>\r\n    <tr class=\\"row1\\"> \r\n      <td width=\\"150\\">{TXT_CATEGORY}:</td>\r\n      <td>\r\n        <select name=\\"inputValue[catid]\\" style=\\"width:300px;\\">\r\n        {CATEGORY}\r\n        </select>\r\n      </td>\r\n    </tr>\r\n  </table>  \r\n  <div id=\\"hiddenlink\\" style=\\"display:none;\\">\r\n    <table border=\\"0\\" cellpadding=\\"3\\" cellspacing=\\"0\\" align=\\"center\\" width=\\"100%\\">\r\n	   <tr class=\\"row1\\" > \r\n	      <td width=\\"150\\" height=\\"26\\">{TXT_LINK}:<font color=\\"red\\">*</font></td>\r\n	      <td><input name=\\"linkname\\" style=\\"width:300px;\\" value=\\"http://\\"></td>\r\n	    </tr>\r\n	</table>\r\n  </div>\r\n  <div id=\\"hiddenrss\\">\r\n    <table border=\\"0\\" cellpadding=\\"3\\" cellspacing=\\"0\\" align=\\"center\\" width=\\"100%\\">\r\n	   <tr class=\\"row1\\" > \r\n	      <td width=\\"150\\" height=\\"26\\">{TXT_RSSLINK}:</td>\r\n	      <td><input name=\\"rssname\\"  style=\\"width:300px;\\" value=\\"http://\\"></td>\r\n	    </tr>\r\n	</table>\r\n  </div>\r\n  <div id=\\"hiddenfile\\" style=\\"display:none;\\">\r\n   <table border=\\"0\\" cellpadding=\\"3\\" cellspacing=\\"0\\" align=\\"center\\" width=\\"100%\\">  \r\n    <tr class=\\"row1\\" > \r\n      <td width=\\"150\\" height=\\"26\\">{TXT_FILE}:<font color=\\"red\\">*</font></td>\r\n      <td> \r\n        <input type=\\"file\\" name=\\"fileName\\" value=\\"\\" size=\\"37\\" style=\\"width:300px;\\">\r\n        </td>\r\n    </tr>\r\n   </table>\r\n  </div>\r\n<table border=\\"0\\" cellpadding=\\"3\\" cellspacing=\\"0\\" align=\\"center\\" width=\\"100%\\"  class=\\"adminlist\\">\r\n   	<!-- BEGIN inputfieldsOutput -->\r\n    <tr class=\\"{FIELD_ROW}\\"> \r\n      <td width=\\"150\\" valign=\\"top\\">{FIELD_NAME}:</td>\r\n      <td >{FIELD_VALUE}</td>\r\n    </tr>\r\n    <!-- END inputfieldsOutput -->\r\n   <tr class=\\"row1\\"> \r\n      <td width=\\"150\\">&nbsp;</td>\r\n      <td><input type=\\"submit\\" name=\\"new_submit\\" value=\\"{TXT_ADD}\\">&nbsp;<font color=\\"red\\">*</font> = {TXT_REQUIRED_FIELDS}</td>\r\n    </tr>\r\n</table>\r\n<input type=\\"hidden\\" name=\\"inputValue[addedby]\\" value=\\"{ADDED_BY_ID}\\" size=\\"37\\" style=\\"width:300px;\\">\r\n</form>\r\n<br />\r\n<!-- END newFeed -->', 'neuer Eintrag', 'add', 'y', 435, 'on', 'system', 3, '1');
