INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`) VALUES (110,37,'<script type=\"text/javascript\">\r\n/* <![CDATA[ */\r\nfields = new Array();\r\nfields[\'name\'] = Array(\r\n	\'Name\',\r\n	\'1\',\r\n	\'.*\',\r\n	\'text\');\r\nfields[\'vorname\'] = Array(\r\n	\'Vorname\',\r\n	\'1\',\r\n	\'.*\',\r\n	\'text\');\r\nfields[\'strasse\'] = Array(\r\n	\'Strasse\',\r\n	\'1\',\r\n	\'.*\',\r\n	\'text\');\r\nfields[\'postleitzahl\'] = Array(\r\n	\'Postleitzahl\',\r\n	\'1\',\r\n	\'^[0-9]*$\',\r\n	\'text\');\r\nfields[\'ortschaft\'] = Array(\r\n	\'Ortschaft\',\r\n	\'1\',\r\n	\'^[A-Za-z\\\\ ]*$\',\r\n	\'text\');\r\nfields[\'email\'] = Array(\r\n	\'Email\',\r\n	\'1\',\r\n	\'^[_a-zA-Z0-9-]+(\\\\.[_a-zA-Z0-9-]+)*@[a-zA-Z0-9-]+(.[a-zA-Z0-9-]+)*\\\\.(([0-9]{1,3})|([a-zA-Z]{2,3})|(aero|coop|info|museum|name))$\',\r\n	\'text\');\r\nfields[\'fongeschaeft\'] = Array(\r\n	\'Fon Gesch&auml;ft\',\r\n	\'0\',\r\n	\'.*\',\r\n	\'text\');\r\nfields[\'fonprivat\'] = Array(\r\n	\'Fon Privat\',\r\n	\'1\',\r\n	\'.*\',\r\n	\'text\');\r\nfields[\'fonmobil\'] = Array(\r\n	\'Fon Mobil\',\r\n	\'0\',\r\n	\'.*\',\r\n	\'text\');\r\nfields[\'wirwuenschen\'] = Array(\r\n	\'Wir w&uuml;nschen\',\r\n	\'0\',\r\n	\'.*\',\r\n	\'label\');\r\nfields[\'dokuperpost\'] = Array(\r\n	\'Dokumentation per Post\',\r\n	\'0\',\r\n	\'.*\',\r\n	\'checkbox\');\r\nfields[\'beratungfinanzierung\'] = Array(\r\n	\'Beratung Finanzierung\',\r\n	\'0\',\r\n	\'.*\',\r\n	\'checkbox\');\r\nfields[\'besichtigung\'] = Array(\r\n	\'Kontakt-Aufnahme Besichtigung\',\r\n	\'0\',\r\n	\'.*\',\r\n	\'checkbox\');\r\nfields[\'kontakttelefon\'] = Array(\r\n	\'Kontakt-Aufnahme per Telefon\',\r\n	\'0\',\r\n	\'.*\',\r\n	\'checkbox\');\r\nfields[\'bemerkungen\'] = Array(\r\n	\'Bemerkungen\',\r\n	\'0\',\r\n	\'.*\',\r\n	\'textarea\');\r\nfields[\'immoid\'] = Array(\r\n	\'immoid\',\r\n	\'0\',\r\n	\'.*\',\r\n	\'hidden\');\r\nvar readBefore = false;\r\nvar borderBefore = \"\";\r\n\r\nfunction checkAllFields() {\r\n	var isOk = true;\r\n	for (var field in fields) { \r\n		if (!readBefore) {\r\n			borderBefore = document.getElementsByName(\'contactFormField_\' + field)[0].style.border;\r\n			readBefore = true;\r\n		}\r\n\r\n		var type = fields[field][3];\r\n		if (type == \'text\' || type == \'file\' || type == \'password\' || type == \'textarea\') {\r\n			value = document.getElementsByName(\'contactFormField_\' + field)[0].value;\r\n			if (value == \"\" && isRequiredNorm(fields[field][1], value)) {\r\n				isOk = false;\r\n				document.getElementsByName(\'contactFormField_\' + field)[0].style.border = \"red 1px solid\"; \r\n			} else if (value != \"\" && !matchType(fields[field][2], value)) {\r\n				isOk = false;\r\n				document.getElementsByName(\'contactFormField_\' + field)[0].style.border = \"red 1px solid\"; \r\n			} else {\r\n				document.getElementsByName(\'contactFormField_\' + field)[0].style.border = borderBefore; \r\n			}\r\n		} else if (type == \'checkbox\') {\r\n			if (!isRequiredCheckbox(fields[field][1], field)) {\r\n				isOk = false;\r\n			}\r\n		} else if (type == \'checkboxGroup\') {\r\n			if (!isRequiredCheckBoxGroup(fields[field][1], field)) {\r\n				isOk = false;\r\n			}\r\n		} else if (type == \'radio\') {\r\n			if (!isRequiredRadio(fields[field][1], field)) {\r\n				isOk = false;\r\n			}\r\n		}\r\n	}\r\n\r\n	if (!isOk) {\r\n		document.getElementById(\'contactFormError\').style.display = \"block\";\r\n	}\r\n	return isOk;\r\n} \r\n\r\nfunction isRequiredNorm(required, value) {\r\n	if (required == 1) {\r\n		if (value == \"\") { \r\n			return true; \r\n		} \r\n	} \r\n	return false; \r\n} \r\n\r\nfunction matchType(pattern, value) {\r\n	var reg = new RegExp(pattern);\r\n	if (value.match(reg)) {\r\n		return true;\r\n	}\r\n	return false;\r\n} \r\n\r\nfunction isRequiredCheckbox(required, field) {\r\n	if (required == 1) {\r\n		if (!document.getElementsByName(\'contactFormField_\' + field)[0].checked) {\r\n			document.getElementsByName(\'contactFormField_\' + field)[0].style.border = \"red 1px solid\"; \r\n			return false;\r\n		}\r\n	}\r\n	document.getElementsByName(\'contactFormField_\' + field)[0].style.border = borderBefore; \r\n	return true;\r\n}\r\n\r\nfunction isRequiredCheckBoxGroup(required, field) {\r\n	if (required) {\r\n		var boxes = document.getElementsByName(\'contactFormField_\' + field + \'[]\');\r\n		var checked = false;\r\n		for (var i = 0; i < boxes.length; i++) { \r\n 			if (boxes[i].checked) {\r\n				checked = true;\r\n			}\r\n		}\r\n		if (checked) {\r\n			setListBorder(\'contactFormField_\' + field + \'[]\', borderBefore);\r\n			return true;\r\n		} else {\r\n			setListBorder(\'contactFormField_\' + field + \'[]\', \'1px red solid\');\r\n			return false;\r\n		}\r\n	} else { \r\n		return true;\r\n	}\r\n}\r\n\r\nfunction isRequiredRadio(required, field) {\r\n	if (required) {\r\n		var buttons = document.getElementsByName(\'contactFormField_\' + field);\r\n		var checked = false;\r\n		for (var i = 0; i < buttons.length; i++) {\r\n			if (buttons[i].checked) {\r\n				checked = true;\r\n			}\r\n		}\r\n		if (checked) {\r\n			setListBorder(\'contactFormField_\' + field, borderBefore);\r\n			return true;\r\n		} else { \r\n			setListBorder(\'contactFormField_\' + field, \'1px red solid\');\r\n			return false;\r\n		}\r\n	} else { \r\n		return true;\r\n	}\r\n}\r\n\r\nfunction setListBorder(field, borderColor) {\r\n	var boxes = document.getElementsByName(field);\r\n	for (var i = 0; i < boxes.length; i++) {\r\n		boxes[i].style.border = borderColor;\r\n	}\r\n}\r\n\r\n/* ]]> */\r\n</script> \r\n\r\n<span>{CONTACT_FEEDBACK_TEXT}</span>\r\n<br /><br />\r\n<ul>\r\n    <li>Mit&nbsp;diesem Formular geben wir Ihnen die M&ouml;glichkeit, uns 24 Stunden am Tag zu erreichen.</li>\r\n    <li>Der entsprechende Sachbearbeiter wird mit Ihnen gerne Kontakt aufnehmen .</li>\r\n</ul>\r\n<div style=\"color: red; display: none;\" id=\"contactFormError\">Bitte &uuml;berpr&uuml;fen Sie ihre Eingabe!</div>\r\n<br />\r\n<form id=\"contactForm\" onsubmit=\"return checkAllFields();\" enctype=\"multipart/form-data\" method=\"post\" action=\"index.php?section=immo&amp;cmd=interest\">\r\n    <table border=\"0\">\r\n        <tbody>\r\n            <tr>\r\n                <td style=\"width: 100px;\">Name <span style=\"color: red;\">*</span></td>\r\n                <td><input type=\"text\" value=\"\" name=\"contactFormField_name\" style=\"width: 300px;\" /> </td>\r\n            </tr>\r\n            <tr>\r\n                <td style=\"width: 100px;\">Vorname <span style=\"color: red;\">*</span></td>\r\n                <td><input type=\"text\" value=\"\" name=\"contactFormField_vorname\" style=\"width: 300px;\" /> </td>\r\n            </tr>\r\n            <tr>\r\n                <td style=\"width: 100px;\">Strasse <span style=\"color: red;\">*</span></td>\r\n                <td><input type=\"text\" value=\"\" name=\"contactFormField_strasse\" style=\"width: 300px;\" /> </td>\r\n            </tr>\r\n            <tr>\r\n                <td style=\"width: 100px;\">Postleitzahl <span style=\"color: red;\">*</span></td>\r\n                <td><input type=\"text\" value=\"\" name=\"contactFormField_postleitzahl\" style=\"width: 300px;\" /> </td>\r\n            </tr>\r\n            <tr>\r\n                <td style=\"width: 100px;\">Ortschaft <span style=\"color: red;\">*</span></td>\r\n                <td><input type=\"text\" value=\"\" name=\"contactFormField_ortschaft\" style=\"width: 300px;\" /> </td>\r\n            </tr>\r\n            <tr>\r\n                <td style=\"width: 100px;\">Email <span style=\"color: red;\">*</span></td>\r\n                <td><input type=\"text\" value=\"\" name=\"contactFormField_email\" style=\"width: 300px;\" /> </td>\r\n            </tr>\r\n            <tr>\r\n                <td style=\"width: 100px;\">Fon Gesch&auml;ft </td>\r\n                <td><input type=\"text\" value=\"\" name=\"contactFormField_fongeschaeft\" style=\"width: 300px;\" /> </td>\r\n            </tr>\r\n            <tr>\r\n                <td style=\"width: 100px;\">Fon Privat <span style=\"color: red;\">*</span></td>\r\n                <td><input type=\"text\" value=\"\" name=\"contactFormField_fonprivat\" style=\"width: 300px;\" /> </td>\r\n            </tr>\r\n            <tr>\r\n                <td style=\"width: 100px;\">Fon Mobil </td>\r\n                <td><input type=\"text\" value=\"\" name=\"contactFormField_fonmobil\" style=\"width: 300px;\" /> </td>\r\n            </tr>\r\n            <tr>\r\n                <td style=\"width: 100px;\"><br />&nbsp;Wir w&uuml;nschen<br /><br /> </td>\r\n                <td> </td>\r\n            </tr>\r\n            <tr>\r\n                <td style=\"float: right;\"><input type=\"checkbox\" value=\"1\" name=\"contactFormField_dokuperpost\" /> </td>\r\n                <td>Dokumentation per Post </td>\r\n            </tr>\r\n            <tr>\r\n                <td style=\"float: right;\"><input type=\"checkbox\" value=\"1\" name=\"contactFormField_beratungfinanzierung\" /> </td>\r\n                <td>Beratung Finanzierung </td>\r\n            </tr>\r\n            <tr>\r\n                <td style=\"float: right;\"><input type=\"checkbox\" value=\"1\" name=\"contactFormField_besichtigung\" /> </td>\r\n                <td>Kontakt-Aufnahme Besichtigung </td>\r\n            </tr>\r\n            <tr>\r\n                <td style=\"float: right;\"><input type=\"checkbox\" value=\"1\" name=\"contactFormField_kontakttelefon\" /> </td>\r\n                <td>Kontakt-Aufnahme per Telefon </td>\r\n            </tr>\r\n            <tr>\r\n                <td style=\"width: 100px;\">Bemerkungen </td>\r\n                <td><textarea name=\"contactFormField_bemerkungen\" style=\"width: 300px; height: 100px;\"></textarea> </td>\r\n            </tr>\r\n            <tr>\r\n                <td style=\"width: 100px;\">&nbsp; </td>\r\n                <td><input type=\"hidden\" value=\"{IMMO_ID}\" name=\"contactFormField_immoid\" /> </td>\r\n            </tr>\r\n            <tr>\r\n                <td>&nbsp;</td>\r\n                <td> <input type=\"reset\" value=\"Löschen\" /> <input type=\"submit\" value=\"Absenden\" name=\"submitContactForm\" /> </td>\r\n            </tr>\r\n        </tbody>\r\n    </table>\r\n</form>','Interesse an Objekt','interest','y',109,'on','sys_update',1);
INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`) VALUES (111,37,'<script type=\"text/javascript\">\r\n//<![CDATA[\r\n	{IMMO_DETAILS_JAVASCRIPT}\r\n	\r\nvar fields=new Array(\"name\",\"telephone\",\"email\");\r\n\r\nvar checkAllFields = function(){\r\n   for(i in fields){\r\n     if(trim(document.getElementById(fields[i]).value)==\'\'){\r\n      alert(\"{TXT_IMMO_FIELDS_REQUIRED}\"); \r\n      return false;\r\n     }\r\n   }\r\n}\r\nfunction trim(s) {\r\n  while (s.substring(0,1) == \' \') {\r\n    s = s.substring(1,s.length);\r\n  }\r\n  while (s.substring(s.length-1,s.length) == \' \') {\r\n    s = s.substring(0,s.length-1);\r\n  }\r\n  return s;\r\n}\r\n\r\n//]]>\r\n</script>\r\n<br />\r\n{TXT_IMMO_STATUS}\r\n<div id=\"immo\">\r\n<form id=\"contactForm\" onsubmit=\"return checkAllFields();\" method=\"post\" action=\"index.php?section=immo&amp;cmd=getPDF\">\r\n    <table border=\"0\">\r\n        <tbody>\r\n            <tr>\r\n                <td class=\"contact\"> {TXT_IMMO_NAME}<span style=\"color: red;\">*</span></td>\r\n                <td><input type=\"text\" value=\"\" id=\"name\" name=\"name\" style=\"width: 300px;\" /> </td>\r\n            </tr>\r\n            <tr>\r\n                <td class=\"contact\">{TXT_IMMO_FIRSTNAME}<span style=\"color: red;\">*</span></td>\r\n                <td><input type=\"text\" value=\"\" id=\"firstname\" name=\"firstname\" style=\"width: 300px;\" /> </td>\r\n            </tr>\r\n            <tr>\r\n                <td class=\"contact\">{TXT_IMMO_COMPANY}</td>\r\n                <td><input type=\"text\" value=\"\" id=\"company\" name=\"company\" style=\"width: 300px;\" /> </td>\r\n            </tr>\r\n            <tr>\r\n                <td class=\"contact\">{TXT_IMMO_STREET}<span style=\"color: red;\">*</span></td>\r\n                <td><input type=\"text\" value=\"\" id=\"street\" name=\"street\" style=\"width: 300px;\" /> </td>\r\n            </tr>\r\n            <tr>\r\n                <td class=\"contact\">{TXT_IMMO_ZIP}<span style=\"color: red;\">*</span></td>\r\n                <td><input type=\"text\" value=\"\" id=\"zip\" name=\"zip\" style=\"width: 300px;\" /> </td>\r\n            </tr>\r\n            <tr>\r\n                <td class=\"contact\">{TXT_IMMO_LOCATION}<span style=\"color: red;\">*</span></td>\r\n                <td><input type=\"text\" value=\"\" id=\"location\" name=\"location\" style=\"width: 300px;\" /> </td>\r\n            </tr>\r\n            <tr>\r\n                <td class=\"contact\">E-Mail <span style=\"color: red;\">*</span></td>\r\n                <td><input type=\"text\" value=\"\" id=\"email\" name=\"email\" style=\"width: 300px;\" /> </td>\r\n            </tr>\r\n            <tr>\r\n                <td class=\"contact\">{TXT_IMMO_TELEPHONE}<span style=\"color: red;\">*</span></td>\r\n                <td><input type=\"text\" value=\"\" id=\"telephone\" name=\"telephone\" style=\"width: 300px;\" /> </td>\r\n            </tr>\r\n            <tr>\r\n                <td class=\"contact\">{TXT_IMMO_TELEPHONE_OFFICE}</td>\r\n                <td><input type=\"text\" value=\"\" id=\"telephone_office\" name=\"telephone_office\" style=\"width: 300px;\" /> </td>\r\n            </tr>\r\n            <tr>\r\n                <td class=\"contact\">{TXT_IMMO_TELEPHONE_MOBILE}</td>\r\n                <td><input type=\"text\" value=\"\" id=\"telephone_mobile\" name=\"telephone_mobile\" style=\"width: 300px;\" /> </td>\r\n            </tr>\r\n            <tr>\r\n                <td class=\"contact\"><label for=\"purchase\">{TXT_IMMO_PURCHASE}</label></td>\r\n                <td><input type=\"checkbox\" name=\"purchase\" id=\"purchase\" /></td>\r\n            </tr>\r\n            <tr>\r\n                <td class=\"contact\"><label for=\"funding\">{TXT_IMMO_FUNDING}</label></td>\r\n                <td><input type=\"checkbox\" name=\"funding\" id=\"funding\" /></td>\r\n            </tr>\r\n            <tr>\r\n                <td class=\"contact\">{TXT_IMMO_COMMENTS}</td>\r\n                <td><textarea id=\"comment\" name=\"comment\" style=\"width: 300px;\" /> </textarea></td>\r\n            </tr>\r\n\r\n            <tr>\r\n                <td>&nbsp;</td>\r\n                <td> <input type=\"submit\" value=\"Absenden\" name=\"submitContactForm\" /> <input type=\"reset\" value=\"{TXT_IMMO_DELETE}\" />  </td>\r\n            </tr>\r\n        </tbody>\r\n    </table>\r\n<input type=\"hidden\" name=\"immo_id\" value=\"{IMMO_ID}\" />\r\n<input type=\"hidden\" name=\"field_id\" value=\"{IMMO_FIELD_ID}\" />\r\n</form>\r\n</div>','Kontaktdetails für PDF','getPDF','y',109,'on','system',1);
