INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`) VALUES (600,20,'<div id=\"forum\">\r\n	<div class=\"text\">\r\n		You can search for all the keywords in the forum.. <br />\r\n		If you want to extend your search to the discussion\'s content, you caan\r\n                activate the checkbox next to the search field.<br /> <br />\r\n	</div>\r\n	<!-- BEGIN forumError -->\r\n		<div class=\"message_error\">{FORUM_ERROR}</div>\r\n	<!-- END forumError -->\r\n	<div class=\"searchform\">\r\n		<form action=\"index.php\" method=\"get\">\r\n			<input type=\"hidden\" value=\"forum\" name=\"section\">\r\n			<input type=\"hidden\" value=\"searchTags\" name=\"cmd\">\r\n			<input style=\"width: 250px;\" type=\"text\" name=\"term\" value=\"{FORUM_SEARCH_TERM}\">  <input type=\"submit\" name=\"search\" value=\"Search\" /><br /><br />\r\n			<input type=\"checkbox\" value=\"1\" id=\"srchContent\" name=\"searchContent\" {FORUM_SEARCH_CONTENT_CHECKED} /><label for=\"srchContent\">Search content, too</label>\r\n		</form>\r\n	</div>\r\n	<div class=\"threads\">\r\n		<ul>\r\n			<!-- BEGIN threadList -->\r\n				<li style=\"list-item: none;\"><a title=\"{FORUM_THREAD_SUBJECT}\" href=\"{FORUM_THREAD_LINK}\"> {FORUM_THREAD_SUBJECT} </a><br />\r\n				Keywords: {FORUM_THREAD_KEYWORDS} <br /> \r\n				Content: {FORUM_THREAD_CONTENT}<br /><br /></li>\r\n			<!-- END threadList -->\r\n		</ul>\r\n	</div>\r\n</div>','Search tags','searchTags','y',596,'on','system',3);
INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`) VALUES (601,20,'{FORUM_JAVASCRIPT}\r\n{FORUM_JAVASCRIPT_GOTO}\r\n<div id=\"forum\">\r\n	<!-- <div class=\"links\">{FORUM_COMMUNITY_LINKS}</div> -->\r\n	<!-- <div class=\"dropdown\">{FORUM_DROPDOWN}</div> -->\r\n	<div class=\"navtree\">{FORUM_TREE}{FORUM_NAME}</div>\r\n	<div class=\"forums\">\r\n		<h2>{FORUM_MAINCATEGORY_NAME}</h2>\r\n		<table cellspacing=\"0\">\r\n			<tr>\r\n				<th width=\"99%\">{TXT_FORUM}</td>\r\n				<th width=\"1%\" nowrap=\"nowrap\">{TXT_LASTPOST}</td>\r\n				<th width=\"1%\" align=\"center\" nowrap=\"nowrap\">{TXT_THREADS}</td>\r\n				<th width=\"1%\" align=\"center\" nowrap=\"nowrap\">{TXT_POSTINGS}</td>\r\n			</tr>\r\n			<!-- BEGIN forumSubCategory -->\r\n			<tr class=\"row{FORUM_SUBCATEGORY_ROWCLASS}\">\r\n				<td><div style=\"padding-left: {FORUM_SUBCATEGORY_SPACER}px;\"><a href=\"index.php?section=forum&amp;cmd=board&amp;id={FORUM_SUBCATEGORY_ID}\" title=\"{FORUM_SUBCATEGORY_NAME}\"><b>{FORUM_SUBCATEGORY_NAME}</b></a><br />{FORUM_SUBCATEGORY_DESC}</div></td>\r\n				<td nowrap=\"nowrap\">{FORUM_SUBCATEGORY_LASTPOST_TITLE}<br />{FORUM_SUBCATEGORY_LASTPOST_DATE}</td>\r\n				<td align=\"center\">{FORUM_SUBCATEGORY_THREADS}</td>\r\n				<td align=\"center\">{FORUM_SUBCATEGORY_POSTINGS}</td>\r\n			</tr>\r\n			<!-- END forumSubCategory -->\r\n		</table>\r\n	</div>\r\n</div>','Show category','cat','y',596,'off','system',99);
INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`) VALUES (602,20,'{FORUM_JAVASCRIPT_GOTO}\r\n{FORUM_JAVASCRIPT_DELETE} \r\n{FORUM_JAVASCRIPT_SCROLLTO}\r\n{FORUM_JAVASCRIPT_INSERT_TEXT}\r\n<div id=\"forum\">\r\n    <!-- BEGIN threadDisplay -->\r\n	<!-- <div class=\"links\">{FORUM_COMMUNITY_LINKS}</div> -->\r\n	<!-- <div class=\"dropdown\">{FORUM_DROPDOWN}</div> -->\r\n	<div class=\"navtree\">\r\n		{FORUM_TREE}{FORUM_NAME}\r\n		<span style=\"color: rgb(255, 0, 0);\">{TXT_FORUM_ERROR}&nbsp;</span>\r\n		<span style=\"color: #006900;\">{TXT_FORUM_SUCCESS}&nbsp;</span>\r\n	</div>\r\n	<!-- BEGIN addPostAnchor -->\r\n	<div class=\"anchor\" style=\"float: left; width: 45%; clear: left;\">\r\n        <input onclick=\"location.href=\'#post\';\" value=\"{TXT_FORUM_CREATE_POST}\" type=\"button\">\r\n    </div>                \r\n	<!-- END addPostAnchor -->\r\n    <!-- BEGIN threadActionsSelect -->\r\n    <div style=\"text-align:right; float: right; width: 45%; margin-bottom: 4px;\">\r\n        <select onchange=\"doAction(this.options[this.options.selectedIndex].value);\" name=\"thread_actions\">\r\n             <option value=\"\">{TXT_FORUM_THREAD_ACTION_DESC}</option>\r\n             <option value=\"sticky\" {FORUM_THREAD_ACTIONS_DISABLED_STICKY}>{TXT_FORUM_THREAD_ACTION_STICKY}</option>\r\n             <option value=\"move\" {FORUM_THREAD_ACTIONS_DISABLED_MOVE}>{TXT_FORUM_THREAD_ACTION_MOVE}</option>\r\n             <option value=\"close\" {FORUM_THREAD_ACTIONS_DISABLED_CLOSE}>{TXT_FORUM_THREAD_ACTION_CLOSE}</option>\r\n             <option value=\"delete\" {FORUM_THREAD_ACTIONS_DISABLED_DELETE}>{TXT_FORUM_THREAD_ACTION_DELETE}</option>\r\n        </select>\r\n    </div>\r\n    <!-- END threadActionsSelect -->\r\n	<div class=\"posts\">\r\n		<table cellspacing=\"0\" cellpadding=\"0\" border=\"0\">\r\n			<tbody>\r\n				<!-- BEGIN forumPosts -->\r\n				<tr class=\"row{FORUM_POST_ROWCLASS}\">\r\n					<th width=\"99%\"><span style=\"padding-right: 60px;\">{FORUM_USER_NAME}, {FORUM_POST_DATE} </span>\r\n						<span class=\"rating\"> {TXT_FORUM_RATING}:		{FORUM_POST_RATING}	</span>\r\n							<!-- BEGIN rating -->\r\n							<span id=\"forum_rating_{FORUM_RATING_POST_ID}\">\r\n								<a title=\"+1\" href=\"javascript:void(0);\" onclick=\"ratePost({FORUM_RATING_POST_ID}, 1);\" >\r\n								[\r\n								<span class=\"rate\">\r\n									+\r\n								</span>\r\n								]\r\n								</a>\r\n								<a title=\"-1\" href=\"javascript:void(0);\" onclick=\"ratePost({FORUM_RATING_POST_ID}, -1);\">\r\n								[\r\n								<span class=\"rate\">\r\n									-\r\n								</span>\r\n								]\r\n								</a>\r\n							</span>\r\n						<!-- END rating -->\r\n					</th>\r\n					<th width=\"1%\">{FORUM_POST_NUMBER}</th>\r\n				</tr>\r\n				<tr class=\"row{FORUM_POST_ROWCLASS}\">\r\n					<td colspan=\"2\"><h3>{FORUM_POST_SUBJECT} {FORUM_POST_ICON}</h3>{FORUM_POST_MESSAGE}  </td>\r\n				</tr>\r\n				<!-- BEGIN attachment -->\r\n				<tr class=\"row_{FORUM_POST_ROWCLASS}\">\r\n					<td colspan=\"2\">\r\n						{TXT_FORUM_FILE_ATTACHMENT}:\r\n						<a href=\"{FORUM_POST_ATTACHMENT_LINK}\">\r\n							<img alt=\"{FORUM_POST_ATTACHMENT_FILENAME}\" src=\"{FORUM_POST_ATTACHMENT_ICON}\" />\r\n							{FORUM_POST_ATTACHMENT_FILENAME} ({FORUM_POST_ATTACHMENT_FILESIZE} Bytes)\r\n						</a>\r\n					</td>\r\n				</tr>\r\n        			<!-- END attachment -->\r\n				<tr class=\"row{FORUM_POST_ROWCLASS}\">\r\n					<td colspan=\"2\" align=\"right\">\r\n						 <!-- BEGIN postEdit --><input onclick=\"location.href=\'?section=forum&amp;cmd=thread&amp;id={FORUM_THREAD_ID}&amp;act=edit&amp;postid={FORUM_POST_ID}\';\" value=\"{TXT_FORUM_EDIT}\" type=\"button\">&nbsp;<!-- END postEdit -->\r\n						  <!-- BEGIN postQuote --><input onclick=\"location.href=\'?section=forum&amp;cmd=thread&amp;id={FORUM_THREAD_ID}&amp;act=quote&amp;postid={FORUM_POST_ID}\';\" value=\"{TXT_FORUM_QUOTE}\" type=\"button\">&nbsp;<!-- END postQuote -->               \r\n						  <!-- BEGIN postDelete --><input onclick=\"deletePost(\'{FORUM_THREAD_ID}\', \'{FORUM_POST_ID}\'); return false;\" value=\"{TXT_FORUM_DELETE}\" type=\"button\">&nbsp;<!-- END postDelete --> \r\n					</td>\r\n				</tr>\r\n				<!-- END forumPosts -->\r\n			</tbody>\r\n		</table>\r\n	</div>\r\n	<div class=\"paging\">\r\n		{FORUM_POSTS_PAGING}\r\n	</div>\r\n	<!-- BEGIN addThread --><!-- END addThread -->\r\n	<!-- BEGIN addPost -->\r\n	<a name=\"post\"></a>\r\n	<div class=\"addpost\">\r\n		<fieldset> \r\n            <legend>{TXT_FORUM_ADD_POST}</legend>\r\n			<form enctype=\"multipart/form-data\" name=\"frm_thread\" action=\"?section=forum&amp;cmd=thread&amp;id={FORUM_THREAD_ID}\" method=\"post\" onsubmit=\"setScrollPos();\">\r\n				<input type=\"hidden\" name=\"post_edit_user_id\" value=\"{FORUM_POST_EDIT_USERID}\" /> 	\r\n				<input type=\"hidden\" name=\"post_id\" value=\"{FORUM_EDIT_POST_ID}\" /> 	\r\n				<input type=\"hidden\" name=\"thread_id\" value=\"{FORUM_THREAD_ID}\" /> 	\r\n				<input type=\"hidden\" name=\"category_id\" value=\"{FORUM_CATEGORY_ID}\" /> 	\r\n				<input type=\"hidden\" name=\"pos\" value=\"{FORUM_PAGING_POS}\" />\r\n				<!-- <p>\r\n					<label>{TXT_FORUM_ICON}</label> -->\r\n					<!-- BEGIN icons -->\r\n					<!-- <input type=\"radio\" id=\"icons_{FORUM_ICON_VALUE}\" name=\"icons\" value=\"{FORUM_ICON_VALUE}\" /><label class=\"description\" for=\"icons_{FORUM_ICON_VALUE}\"><img src=\"{FORUM_ICON_SRC}\" alt=\"{FORUM_ICON_ALT}\" title=\"{FORUM_ICON_TITLE}\" /></label><br /> -->\r\n					<!-- END icons -->\r\n				<!-- </p> -->\r\n				<p><label for=\"thread_subject\">{TXT_FORUM_SUBJECT}</label><input type=\"text\" name=\"subject\" value=\"{FORUM_SUBJECT}\" /></p>\r\n				<p><label for=\"keywords\">{TXT_FORUM_KEYWORDS}</label><input onfocus=\"showToolTip(\'{TXT_FORUM_COMMA_SEPARATED_KEYWORDS}\', this, \'forumKeywordsHelp\')\" onblur=\"hideToolTip(\'forumKeywordsHelp\')\" title=\"{TXT_FORUM_COMMA_SEPARATED_KEYWORDS}\"  type=\"text\" name=\"keywords\" value=\"{FORUM_KEYWORDS}\" /></p>\r\n				<p><label for=\"forum_attachment\">{TXT_FORUM_FILE_ATTACHMENT}</label><input class=\"inputtext\" type=\"file\" value=\"{FORUM_ATTACHMENT_VALUE}\" name=\"forum_attachment\" />\r\n            		<!-- BEGIN delAttachment -->\r\n            			    <br /><input type=\"checkbox\" id=\"delAttachment\" name=\"forum_delete_attachment\" value=\"1\" />\r\n            				<label for=\"delAttachment\">{TXT_FORUM_DELETE_ATTACHMENT}</label>\r\n            				<input type=\"hidden\" name=\"forum_attachment_oldname\" value=\"{FORUM_ATTACHMENT_OLDNAME}\" />\r\n            		<!-- END delAttachment -->\r\n                </p>\r\n                <p><a href=\"javascript:void(0);\" onclick=\"showAllowedExtensions();\">Allowed file extensions</a></p>\r\n				<p><label for=\"thread_message\">{TXT_FORUM_MESSAGE}<br />\r\n                                    <a style=\"font-size:0.9em;\" href=\"http://en.wikipedia.org/wiki/BBCode\" title=\"BBCode\">more about BB code</a></label>{FORUM_MESSAGE_INPUT}</p>\r\n				<!-- BEGIN captcha -->\r\n				<p>\r\n					<label for=\"captcha\">Captcha code</label><img src=\"{FORUM_CAPTCHA_IMAGE_URL}\" title=\"{FORUM_CAPTCHA_IMAGE_ALT}\" alt=\"{FORUM_CAPTCHA_IMAGE_ALT}\" /><br />\r\n					<input type=\"text\" name=\"captcha\" /><input type=\"hidden\" name=\"offset\" value=\"{FORUM_CAPTCHA_OFFSET}\" />\r\n				</p>\r\n				<!-- END captcha -->\r\n				<!-- BEGIN notificationRow -->\r\n					<p><input id=\"forum_notify\" type=\"checkbox\" name=\"notification\" value=\"notify\" {FORUM_NOTIFICATION_CHECKBOX_CHECKED} /><label class=\"description\" for=\"forum_notify\">{TXT_FORUM_NOTIFY_NEW_POSTS}</label></p>\r\n				<!-- END notificationRow -->\r\n				<p>\r\n					<input type=\"reset\" value=\"{TXT_FORUM_RESET}\" />&nbsp;\r\n					<!-- BEGIN createPost --><input type=\"submit\" name=\"create\" value=\"{TXT_FORUM_CREATE_POST}\" />&nbsp;<!-- END createPost -->                \r\n					<!-- BEGIN updatePost --><input type=\"submit\" name=\"update\" value=\"{TXT_FORUM_UPDATE_POST}\" />&nbsp;<!-- END updatePost -->                  \r\n					<!-- BEGIN previewNewPost --><input type=\"submit\" name=\"preview_new\" value=\"{TXT_FORUM_PREVIEW}\" />&nbsp;<!-- END previewNewPost -->                 \r\n					<!-- BEGIN previewEditPost --><input type=\"submit\" name=\"preview_edit\" value=\"{TXT_FORUM_PREVIEW}\" />&nbsp;<!-- END previewEditPost --> 	\r\n				</p>\r\n			</form>\r\n		</fieldset> \r\n	</div>\r\n    <!-- END addPost -->\r\n    <!-- END threadDisplay -->\r\n    <!-- BEGIN threadActions -->\r\n      <br /><span style=\"color: rgb(255, 0, 0);\">{TXT_THREAD_ACTION_ERROR}&nbsp;</span>\r\n      <br /><span style=\"color: #006900;\">{TXT_THREAD_ACTION_SUCCESS}&nbsp;</span>\r\n      <!-- BEGIN moveForm -->\r\n      <form action=\"index.php?section=forum&amp;cmd=thread&amp;action=move&amp;id={FORUM_THREAD_ID}\" method=\"POST\" name=\"frmThreadMove\">\r\n      <select name=\"moveToThread\" size=\"32\" style=\"width:225px;\">\r\n        {FORUM_THREADS}\r\n      </select>\r\n      <br />\r\n      <input type=\"submit\" value=\"{TXT_FORUM_THREAD_ACTION_MOVE}\" />\r\n      </form>\r\n      <!-- END moveForm -->\r\n    <!-- END threadActions -->\r\n</div>','Show thread','thread','y',596,'off','system',99);
