INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`) VALUES (787,16,'Hier k&ouml;nnen Sie Ihre eigenen Allgemeinen Gesch&auml;ftsbedingungen hineinschreiben. <br />\r\nEnter your terms and conditions here.<br />\r\n<br />\r\n<br />\r\n<strong>{TXT_SHOP_SHIPMENT_CONDITIONS}</strong><br />\r\n<table width=\"100%\" cellspacing=\"0\" cellpadding=\"2\" border=\"0\">\r\n    <tbody>\r\n        <!-- BEGIN shopShipper -->\r\n        <tr>\r\n            <td>&nbsp;</td>\r\n        </tr>\r\n        <tr>\r\n            <td width=\"20%\" nowrap=\"nowrap\"><strong>{TXT_SHIPPING_METHOD}</strong></td>\r\n            <td width=\"20%\" nowrap=\"nowrap\"><strong>{TXT_SHOP_SHIPMENT_COUNTRIES}</strong></td>\r\n            <td width=\"20%\" nowrap=\"nowrap\"><strong>{TXT_SHIPPING_MAX_WEIGHT}</strong></td>\r\n            <td width=\"20%\" nowrap=\"nowrap\"><strong>{TXT_FREE_OF_CHARGE}</strong></td>\r\n            <td width=\"20%\" nowrap=\"nowrap\"><strong>{TXT_FEE}</strong></td>\r\n        </tr>\r\n        <tr>\r\n            <td nowrap=\"nowrap\">{SHOP_SHIPPER}</td>\r\n            <td>{SHOP_COUNTRIES}</td>\r\n            <td colspan=\"3\">&nbsp;</td>\r\n        </tr>\r\n        <!-- BEGIN shopShipment -->\r\n        <tr>\r\n            <td colspan=\"2\">&nbsp;</td>\r\n            <td nowrap=\"nowrap\">{SHOP_MAX_WEIGHT}</td>\r\n            <td nowrap=\"nowrap\">{SHOP_COST_FREE}</td>\r\n            <td nowrap=\"nowrap\">{SHOP_COST}&nbsp;{SHOP_UNIT}</td>\r\n        </tr>\r\n        <!-- END shopShipment -->         <!-- END shopShipper -->\r\n    </tbody>\r\n</table>','Allgemeine Geschäftsbedingungen','terms','y',778,'off','system',99);
INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`) VALUES (788,16,'<div id=\"shop\">\r\n  <div class=\"message_error\">{SHOP_ACCOUNT_STATUS}</div>\r\n  <form action=\"index.php?section=shop&amp;cmd=payment\" name=\"shopForm\" method=\"post\">\r\n    <div class=\"payment\">\r\n      <table summary=\"\" cellspacing=\"0\" width=\"100%\">\r\n        <tr>\r\n          <th colspan=\"3\" align=\"left\">{TXT_PRODUCTS}</th>\r\n        </tr>\r\n        <tr class=\"row1\">\r\n          <td width=\"40%\"><strong>{TXT_TOTALLY_GOODS}</strong>&nbsp;{SHOP_TAX_PRODUCTS_TXT}</td>\r\n          <td width=\"40%\">{SHOP_TOTALITEM}&nbsp;{TXT_PRODUCT_S}</td>\r\n          <td width=\"20%\" align=\"right\"><strong>{SHOP_TOTALPRICE}&nbsp;{SHOP_UNIT}</strong></td>\r\n        </tr>\r\n        <!-- BEGIN shopShipment -->\r\n        <tr class=\"row2\">\r\n          <td>\r\n            <strong>{TXT_SHIPPING_METHODS}</strong>\r\n            <!-- BEGIN shopWeight -->\r\n            &nbsp;({TXT_TOTAL_WEIGHT}&nbsp;{SHOP_TOTAL_WEIGHT})\r\n            <!-- END shopWeight -->\r\n          </td>\r\n          <td>{SHOP_SHIPMENT_MENU}&nbsp;{TXT_SHIPPER_NO_GOOD}</td>\r\n          <td align=\"right\">{SHOP_SHIPMENT_PRICE}&nbsp;{SHOP_UNIT}</td>\r\n        </tr>\r\n        <!-- END shopShipment -->\r\n        <!-- BEGIN shopPayment -->\r\n        <tr class=\"row1\">\r\n          <td><strong>{TXT_PAYMENT_TYPES}</strong></td>\r\n          <td>{SHOP_PAYMENT_MENU}</td>\r\n          <td align=\"right\">{SHOP_PAYMENT_PRICE}&nbsp;{SHOP_UNIT}</td>\r\n        </tr>\r\n        <!-- END shopPayment -->\r\n        <!-- BEGIN shopTax -->\r\n        <tr class=\"row2\">\r\n          <td colspan=\"2\"><strong>{TXT_TAX_PREFIX}</strong></td>\r\n          <td align=\"right\">{SHOP_TAX_PRICE}</strong></td>\r\n        </tr>\r\n        <!-- END shopTax -->\r\n        <!-- BEGIN lsv -->\r\n          <tr class=\"row1\">\r\n            <td><strong>{TXT_PAYMENT_LSV}</strong></td>\r\n            <td colspan=\"2\">\r\n              <p><label>{TXT_ACCOUNT_HOLDER}</label><input type=\"text\" value=\"{SHOP_ACCOUNT_HOLDER}\" name=\"account_holder\" /></p>\r\n              <p><label>{TXT_ACCOUNT_BANK}</label><input type=\"text\" value=\"{SHOP_ACCOUNT_BANK}\" name=\"account_bank\" /></p>\r\n              <p><label>{TXT_ACCOUNT_BLZ}</label><input type=\"text\" value=\"{SHOP_ACCOUNT_BLZ}\" name=\"account_blz\" /></p>\r\n            </td>\r\n          </tr>\r\n        <!-- END lsv -->\r\n        <tr class=\"row3\">\r\n          <td colspan=\"2\"><strong>{TXT_TOTAL_PRICE}</strong>&nbsp;{SHOP_TAX_GRAND_TXT}</td>\r\n          <td align=\"right\"><strong>{SHOP_GRAND_TOTAL} {SHOP_UNIT}</strong></td>\r\n        </tr>\r\n        <tr class=\"row1\">\r\n          <td colspan=\"3\">&nbsp;</td>\r\n        </tr>\r\n        <tr class=\"row1\">\r\n          <td valign=\"top\">{TXT_COMMENTS}</td>\r\n          <td colspan=\"2\"><textarea name=\"customer_note\" rows=\"4\" cols=\"52\">{SHOP_CUSTOMERNOTE}</textarea></td>\r\n        </tr>\r\n      </table>\r\n    </div>\r\n    <br />\r\n    <div class=\"message\">\r\n      <p><input type=\"checkbox\" value=\"checked\" name=\"agb\" {SHOP_AGB} />\r\n        <label class=\"description\">{TXT_ACCEPT_TAC}</label></p>\r\n      <p><input type=\"submit\" value=\"{TXT_UPDATE}\" name=\"refresh\" />&nbsp;<input type=\"submit\" value=\"{TXT_NEXT}\" name=\"check\" /></p>\r\n    </div>\r\n  </form>\r\n</div>','Bezahlung und Versand','payment','y',778,'off','system',99);
INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`) VALUES (789,16,'<div id=\"shop\">\r\n  <div class=\"message_error\">{SHOP_STATUS}</div>\r\n  <!-- BEGIN shopConfirm -->\r\n  <div class=\"order\">\r\n    <form action=\"index.php?section=shop&amp;cmd=confirm\" name=\"shopForm\" method=\"post\">\r\n      <h2>{TXT_ORDER_INFOS}</h2>\r\n      <table cellspacing=\"2\">\r\n        <tr>\r\n          <th width=\"1%\" nowrap=\"nowrap\">{TXT_ID}</th>\r\n          <th width=\"94%\" align=\"left\">{TXT_PRODUCT}</th>\r\n          <th width=\"1%\" nowrap=\"nowrap\" align=\"right\">{TXT_UNIT_PRICE}</th>\r\n          <th width=\"1%\" nowrap=\"nowrap\" align=\"right\">{TXT_QUANTITY}</th>\r\n          <th width=\"1%\" nowrap=\"nowrap\" align=\"right\">{TXT_WEIGHT}</th>\r\n          <th width=\"1%\" nowrap=\"nowrap\" colspan=\"2\">{TXT_TAX_PREFIX}</th>\r\n          <th width=\"1%\" nowrap=\"nowrap\" align=\"right\">{TXT_TOTAL}</th>\r\n        </tr>\r\n          <!-- BEGIN shopCartRow -->\r\n          <tr class=\"row2\">\r\n            <td nowrap=\"nowrap\">{SHOP_PRODUCT_ID}</td>\r\n            <td>{SHOP_PRODUCT_TITLE}</td>\r\n            <td nowrap=\"nowrap\" align=\"right\">{SHOP_PRODUCT_ITEMPRICE} {SHOP_UNIT}</td>\r\n            <td nowrap=\"nowrap\" align=\"right\">{SHOP_PRODUCT_QUANTITY}</td>\r\n            <td nowrap=\"nowrap\" align=\"right\">{SHOP_PRODUCT_WEIGHT}</td>\r\n            <td nowrap=\"nowrap\" align=\"right\">{SHOP_PRODUCT_TAX_RATE}</td>\r\n            <td nowrap=\"nowrap\" align=\"right\">{SHOP_PRODUCT_TAX_AMOUNT}</td>\r\n            <td nowrap=\"nowrap\" align=\"right\">{SHOP_PRODUCT_PRICE} {SHOP_UNIT}</td>\r\n          </tr>\r\n          <!-- END shopCartRow -->\r\n        <tr class=\"row3\">\r\n          <td colspan=\"3\" valign=\"top\"><strong>{TXT_INTER_TOTAL}</strong> {SHOP_TAX_PRODUCTS_TXT}</td>\r\n          <td colspan=\"4\" valign=\"top\" nowrap=\"nowrap\">{SHOP_TOTALITEM} {TXT_PRODUCT_S}</td>\r\n          <td valign=\"top\" nowrap=\"nowrap\" align=\"right\"><strong>{SHOP_TOTALPRICE} {SHOP_UNIT}</strong></td>\r\n        </tr>\r\n          <!-- BEGIN shiprow -->\r\n          <tr class=\"row2\" valign=\"top\">\r\n            <td colspan=\"2\"><strong>{TXT_SHIPPING_METHOD}</strong></td>\r\n            <td colspan=\"5\">{SHOP_SHIPMENT}</td>\r\n            <td nowrap=\"nowrap\" align=\"right\">{SHOP_SHIPMENT_PRICE} {SHOP_UNIT}</td>\r\n          </tr>\r\n          <!-- END shiprow -->\r\n        <tr class=\"row2\" valign=\"top\">\r\n          <td colspan=\"2\"><strong>{TXT_PAYMENT_TYPE}</strong></td>\r\n          <td colspan=\"5\">{SHOP_PAYMENT}</td>\r\n          <td nowrap=\"nowrap\" align=\"right\">{SHOP_PAYMENT_PRICE} {SHOP_UNIT}</td>\r\n        </tr>\r\n          <!-- BEGIN taxrow -->\r\n          <tr class=\"row2\" valign=\"top\">\r\n            <td colspan=\"7\"><strong>{TXT_TAX_PREFIX}</strong></td>\r\n            <td nowrap=\"nowrap\" align=\"right\">{TXT_TAX_PART} {SHOP_TAX_PRICE} {SHOP_UNIT}</td>\r\n          </tr>\r\n          <!-- END taxrow -->\r\n        <tr class=\"row3\">\r\n          <td colspan=\"7\" valign=\"top\" nowrap=\"nowrap\"><strong>{TXT_TOTAL_PRICE}</strong> {SHOP_TAX_GRAND_TXT}</td>\r\n          <td nowrap=\"nowrap\" align=\"right\"><strong>{SHOP_GRAND_TOTAL} {SHOP_UNIT}</strong></td>\r\n        </tr>\r\n      </table>\r\n      <br />\r\n      <table cellspacing=\"0\">\r\n        <tr>\r\n          <th width=\"50%\" align=\"left\">{TXT_ADDRESS_CUSTOMER}</th>\r\n          <th width=\"50%\" align=\"left\">{TXT_SHIPPING_ADDRESS}</th>\r\n        </tr>\r\n        <tr>\r\n          <td nowrap=\"nowrap\" valign=\"top\">\r\n            {SHOP_COMPANY}<br />\r\n            {SHOP_PREFIX} {SHOP_FIRSTNAME} {SHOP_LASTNAME}<br />\r\n            {SHOP_ADDRESS}<br />\r\n            {SHOP_ZIP} {SHOP_CITY}<br />\r\n            {SHOP_COUNTRY}<br />\r\n            {SHOP_PHONE}<br />\r\n            {SHOP_FAX}<br />\r\n            {SHOP_EMAIL}\r\n          </td>\r\n          <td nowrap=\"nowrap\" valign=\"top\">\r\n            <!-- BEGIN shopShipmentAddress -->\r\n            {SHOP_COMPANY2}<br />\r\n            {SHOP_PREFIX2} {SHOP_FIRSTNAME2} {SHOP_LASTNAME2}<br />\r\n            {SHOP_ADDRESS2}<br />\r\n            {SHOP_ZIP2} {SHOP_CITY2}<br />\r\n            {SHOP_COUNTRY2}<br />\r\n            {SHOP_PHONE}<br />\r\n            {SHOP_FAX2}<br />\r\n            {SHOP_EMAIL2}\r\n            <!-- END shopShipmentAddress -->\r\n          </td>\r\n        </tr>\r\n      </table>\r\n      <br />\r\n      <!-- BEGIN shopComment -->\r\n      <table cellspacing=\"0\">\r\n        <tr class=\"row3\">\r\n          <td colspan=\"2\"><strong>{TXT_COMMENTS}<strong></td>\r\n        </tr>\r\n        <tr class=\"row2\">\r\n          <td colspan=\"2\">{SHOP_CUSTOMERNOTE}</td>\r\n        </tr>\r\n      </table><br />\r\n      <br />\r\n      <!-- END shopComment -->\r\n      <div align=\"right\"><input type=\"submit\" value=\"{TXT_ORDER_NOW}\" name=\"process\" /></div>\r\n    </form>\r\n  </div>\r\n  <!-- END shopConfirm -->\r\n  <!-- BEGIN shopProcess -->\r\n  <div class=\"message\">{TXT_ORDER_PREPARED}<br/>{SHOP_PAYMENT_PROCESSING}</div>\r\n  <!-- END shopProcess -->\r\n</div>\r\n','Bestellen','confirm','y',778,'off','system',99);
INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`) VALUES (790,16,'<script language=\"JavaScript\" type=\"text/javascript\">\r\n// <![CDATA[\r\nfunction shopCopyText()\r\n{\r\n  with (document.account) {\r\n    if (equalAddress.checked) {\r\n      prefix2.value = prefix.value;\r\n      company2.value = company.value;\r\n      lastname2.value = lastname.value;\r\n      firstname2.value = firstname.value;\r\n      address2.value = address.value;\r\n      zip2.value = zip.value;\r\n      city2.value = city.value;\r\n      phone2.value = phone.value;\r\n      countryId.value = countryId2.value;\r\n      document.getElementById(\'shippingaddress\').style.display = \"none\";\r\n    } else {\r\n      document.getElementById(\'shippingaddress\').style.display = \"block\";\r\n    }\r\n  }\r\n}\r\n// ]]>\r\n</script>\r\n<div id=\"shop\">\r\n  <div class=\"message_error\">{SHOP_ACCOUNT_STATUS}</div>\r\n  <form name=\"account\" action=\"{SHOP_ACCOUNT_ACTION}\" method=\"post\">\r\n    <div class=\"customer_address\">\r\n      <h2>{TXT_CUSTOMER_ADDRESS}</h2>\r\n      <p><label>{TXT_COMPANY}</label>\r\n        <input type=\"text\" value=\"{SHOP_ACCOUNT_COMPANY}\" name=\"company\" tabindex=\"1\" /></p>\r\n      <p><label>{TXT_GREETING}<font color=\"#ff0000\">&nbsp;*</font></label>\r\n        <input type=\"text\" maxlength=\"50\" value=\"{SHOP_ACCOUNT_PREFIX}\" name=\"prefix\" tabindex=\"2\" /></p>\r\n      <p><label>{TXT_SURNAME}<font color=\"#ff0000\">&nbsp;*</font></label>\r\n        <input type=\"text\" value=\"{SHOP_ACCOUNT_LASTNAME}\" name=\"lastname\" tabindex=\"3\" /></p>\r\n      <p><label>{TXT_FIRSTNAME}<font color=\"#ff0000\">&nbsp;*</font></label>\r\n        <input type=\"text\" value=\"{SHOP_ACCOUNT_FIRSTNAME}\" name=\"firstname\" tabindex=\"4\" /></p>\r\n      <p><label>{TXT_ADDRESS}<font color=\"#ff0000\">&nbsp;*</font></label>\r\n        <input type=\"text\" value=\"{SHOP_ACCOUNT_ADDRESS}\" name=\"address\" tabindex=\"5\" /></p>\r\n      <p><label>{TXT_POSTALE_CODE}<font color=\"#ff0000\">&nbsp;*</font></label>\r\n        <input type=\"text\" value=\"{SHOP_ACCOUNT_ZIP}\" name=\"zip\" tabindex=\"6\" /></p>\r\n      <p><label>{TXT_CITY}<font color=\"#ff0000\">&nbsp;*</font></label>\r\n        <input type=\"text\" value=\"{SHOP_ACCOUNT_CITY}\" name=\"city\" tabindex=\"7\" /></p>\r\n      <p><label>{TXT_COUNTRY}</label>\r\n        <select name=\"countryId\" id=\"countryId\" tabindex=\"8\">\r\n          {SHOP_ACCOUNT_COUNTRY_MENUOPTIONS}\r\n        </select>\r\n      </p>\r\n      <p><label>{TXT_PHONE_NUMBER}<font color=\"#ff0000\">&nbsp;*</font></label>\r\n        <input type=\"text\" value=\"{SHOP_ACCOUNT_PHONE}\" name=\"phone\" tabindex=\"9\" /></p>\r\n      <p><label>{TXT_FAX_NUMBER}</label>\r\n        <input type=\"text\" value=\"{SHOP_ACCOUNT_FAX}\" name=\"fax\" tabindex=\"11\" /></p>\r\n    </div>\r\n<!-- BEGIN shopShipmentAddress -->\r\n<!-- Shown only if some products need to be shipped -->\r\n    <div class=\"customer_address\">\r\n      <p>\r\n        <input type=\"checkbox\" value=\"checked\"\r\n            name=\"equalAddress\" id=\"equalAddress\" onClick=\"shopCopyText();\"\r\n            {SHOP_ACCOUNT_EQUAL_ADDRESS} tabindex=\"21\" />\r\n        <label for=\"equalAddress\" class=\"description\">{TXT_SAME_BILLING_ADDRESS}</label>\r\n      </p>\r\n    </div>\r\n    <div id=\"shippingaddress\" class=\"shipping_address\" style=\"display: {SHOP_SHIPPING_ADDRESS_DISPLAY};\">\r\n      <h2>{TXT_SHIPPING_ADDRESS}</h2>\r\n      <p><label>{TXT_COMPANY}</label>\r\n        <input type=\"text\" value=\"{SHOP_ACCOUNT_COMPANY2}\" name=\"company2\" tabindex=\"22\" /></p>\r\n      <p><label>{TXT_GREETING}<font color=\"#ff0000\">&nbsp;*</font></label>\r\n        <input type=\"text\" maxlength=\"50\" value=\"{SHOP_ACCOUNT_PREFIX2}\" name=\"prefix2\" tabindex=\"23\" /></p>\r\n      <p><label>{TXT_SURNAME}<font color=\"#ff0000\">&nbsp;*</font></label>\r\n        <input type=\"text\" value=\"{SHOP_ACCOUNT_LASTNAME2}\" name=\"lastname2\" tabindex=\"24\" /></p>\r\n      <p><label>{TXT_FIRSTNAME}<font color=\"#ff0000\">&nbsp;*</font></label>\r\n        <input type=\"text\" value=\"{SHOP_ACCOUNT_FIRSTNAME2}\" name=\"firstname2\" tabindex=\"25\" /></p>\r\n      <p><label>{TXT_ADDRESS}<font color=\"#ff0000\">&nbsp;*</font></label>\r\n        <input type=\"text\" value=\"{SHOP_ACCOUNT_ADDRESS2}\" name=\"address2\" tabindex=\"26\" /></p>\r\n      <p><label>{TXT_POSTALE_CODE}<font color=\"#ff0000\">&nbsp;*</font></label>\r\n        <input type=\"text\" size=\"6\" value=\"{SHOP_ACCOUNT_ZIP2}\" name=\"zip2\" tabindex=\"27\" /></p>\r\n      <p><label>{TXT_CITY}<font color=\"#ff0000\">&nbsp;*</font></label>\r\n        <input type=\"text\" value=\"{SHOP_ACCOUNT_CITY2}\" name=\"city2\" tabindex=\"28\" /></p>\r\n      <p><label>{TXT_COUNTRY}</label>\r\n        <input type=\"hidden\" name=\"countryId2\" id=\"countryId2\"\r\n            value=\"{SHOP_ACCOUNT_COUNTRY2_ID}\" />{SHOP_ACCOUNT_COUNTRY2}\r\n      </p>\r\n      <p><label>{TXT_PHONE_NUMBER}<font color=\"#ff0000\">&nbsp;*</font></label>\r\n      <input type=\"text\" value=\"{SHOP_ACCOUNT_PHONE2}\" name=\"phone2\" tabindex=\"29\" /></p>\r\n    </div>\r\n<!-- END shopShipmentAddress -->\r\n<!-- BEGIN account_details -->\r\n      <div class=\"account_details\">\r\n        <h2>{TXT_YOUR_ACCOUNT_DETAILS}</h2>\r\n        <p><label>{TXT_EMAIL}<font color=\"#ff0000\">&nbsp;*</font></label>\r\n          <input type=\"text\" value=\"{SHOP_ACCOUNT_EMAIL}\" name=\"email\" tabindex=\"31\" />\r\n        </p>\r\n        <p><label>{TXT_PASSWORD}<font color=\"#ff0000\">&nbsp;*</font></label>\r\n          <input type=\"password\" value=\"\" name=\"password\" tabindex=\"32\" />\r\n        </p>\r\n      </div>\r\n<!-- END account_details -->\r\n    <p>\r\n      <input type=\"reset\" value=\"{TXT_RESET}\" name=\"reset\" tabindex=\"42\" />\r\n      <input type=\"submit\" value=\"{