INSERT INTO `contrexx_log_entry` (`id`, `action`, `logged_at`, `version`, `object_id`, `object_class`, `data`, `username`) VALUES (4,'create','2013-05-30 00:00:00',1,'4','Cx\\Core\\ContentManager\\Model\\Entity\\Page','a:31:{s:14:\"nodeIdShadowed\";i:4;s:4:\"lang\";s:1:\"1\";s:4:\"type\";s:7:\"content\";s:7:\"caching\";s:1:\"0\";s:9:\"updatedAt\";O:8:\"DateTime\":3:{s:4:\"date\";s:19:\"2013-05-30 00:00:00\";s:13:\"timezone_type\";i:3;s:8:\"timezone\";s:12:\"Europe/Paris\";}s:9:\"updatedBy\";s:6:\"system\";s:5:\"title\";s:17:\"Was ist Contrexx?\";s:10:\"linkTarget\";s:0:\"\";s:12:\"contentTitle\";s:47:\"Contrexx® Web Content Management System (WCMS)\";s:4:\"slug\";s:16:\"Was-ist-Contrexx\";s:7:\"content\";s:2421:\"Dank dem modular aufgebauten Contrexx Web Content Management System wird das Verwalten von anwendungsreichen Webseiten stark vereinfacht. Die &uuml;bersichtliche Administrationsoberfl&auml;che gew&auml;hrt Firmen, Vereinen sowie Privatpersonen die M&ouml;glichkeit, die komplette Webseite auch ohne besondere Computerkenntnisse selber zu bearbeiten.<br />\n<br />\nDoch Contrexx bietet weit mehr, als eine leicht verst&auml;ndliche Verwaltung Ihres Internetauftritts. Die Software ist ein Gesamtpaket, welches alle wichtigen Anwendungen beinhaltet, die eine moderne Webseite heutzutage braucht &ndash; und dies egal in welcher Branche der Nutzer t&auml;tig ist.<br />\n<h3>\n	Was unterscheidet Contrexx von einem herk&ouml;mmlichen CMS?</h3>\nUm Ihr Zielpublikum zu erweitern, k&ouml;nnen Sie dank Contrexx auf effiziente Art und Weise ein <strong>mehrsprachiger</strong> Webauftritt erstellen. Durch den<strong> Multichannel</strong> erreichen Sie Ihre Kunden durch kompatible Anzeigearten auch &uuml;ber Smartphones und Tablets, denn Ihre Webseite passt sich dem Ger&auml;t des Nutzers an. App- und Printansicht sind weitere M&ouml;glichkeiten davon. Mit der Anwendung <strong>E-Commerce</strong> steht ein kompletter Online Shop zu Ihrer Verf&uuml;gung, in dem sich das Hinzuf&uuml;gen von Kategorien und Produkten schnell und eifach gestaltet. &Uuml;berweisungen aller Art k&ouml;nnen direkt &uuml;ber die Anwendung <strong>Online Zahlung</strong> vorgenommen werden. Weiter ist zum Versenden von Newsletter die Anwendung <strong>E-Mail-Marketing</strong> in Contrexx integriert. Dank dem <strong>Unified User Management</strong> werden die Kundendaten vom Online Shop, von Online Zahlungen sowie von den Newsletter Abonnenten alle im selben Verzeichnis zusammengef&uuml;hrt. Schliesslich erm&ouml;glicht das<strong> Media Asset Management</strong> eine logisch organisierte Ablage der Bilder, Grafiken, Dokumenten und Videos Ihrer Webseite.<br />\nAlle weiteren Anwendungen von Contrexx sind <a href=\"{NODE_5}\">hier</a> zu finden.\n<h3>\n	Professioneller und innovativer Vertrieb</h3>\nContrexx&reg; wird weltweit &uuml;ber ein Partnernetzwerk vertrieben. Die Contrexx&reg; L&ouml;sungspartner sind autorisierte Partner welche ausgewiesene Kompetenzen im den Bereichen WebDesign, Grafik oder Internetmarketing besitzen. Damit erhalten Sie als Kunde professionelle Unterst&uuml;tzung mit hohen Kompetenzen aus Ihrer N&auml;he.\";s:10:\"sourceMode\";b:0;s:13:\"customContent\";s:0:\"\";s:7:\"cssName\";s:13:\"informationen\";s:10:\"cssNavName\";s:0:\"\";s:4:\"skin\";s:1:\"0\";s:9:\"metatitle\";s:47:\"Contrexx® Web Content Management System (WCMS)\";s:8:\"metadesc\";s:47:\"Contrexx® Web Content Management System (WCMS)\";s:8:\"metakeys\";s:47:\"Contrexx® Web Content Management System (WCMS)\";s:10:\"metarobots\";s:5:\"index\";s:5:\"start\";N;s:3:\"end\";N;s:13:\"editingStatus\";s:0:\"\";s:10:\"protection\";s:1:\"0\";s:16:\"frontendAccessId\";s:1:\"0\";s:15:\"backendAccessId\";s:1:\"0\";s:7:\"display\";s:1:\"1\";s:6:\"active\";s:1:\"1\";s:6:\"target\";s:0:\"\";s:6:\"module\";s:0:\"\";s:3:\"cmd\";s:0:\"\";}','{\"id\":1,\"name\":\"system\"}');
INSERT INTO `contrexx_log_entry` (`id`, `action`, `logged_at`, `version`, `object_id`, `object_class`, `data`, `username`) VALUES (5,'create','2013-05-30 00:00:00',1,'5','Cx\\Core\\ContentManager\\Model\\Entity\\Page','a:31:{s:14:\"nodeIdShadowed\";i:4;s:4:\"lang\";s:1:\"2\";s:4:\"type\";s:8:\"fallback\";s:7:\"caching\";s:1:\"0\";s:9:\"updatedAt\";O:8:\"DateTime\":3:{s:4:\"date\";s:19:\"2013-05-30 00:00:00\";s:13:\"timezone_type\";i:3;s:8:\"timezone\";s:12:\"Europe/Paris\";}s:9:\"updatedBy\";s:6:\"system\";s:5:\"title\";s:17:\"Was ist Contrexx?\";s:10:\"linkTarget\";s:0:\"\";s:12:\"contentTitle\";s:47:\"Contrexx® Web Content Management System (WCMS)\";s:4:\"slug\";s:16:\"Was-ist-Contrexx\";s:7:\"content\";s:1394:\"<p>The <a href=\"http://www.contrexx.com\" class=\"out\">Contrexx&reg; Web Content Management System (WCMS)</a> provides you a fast, user friendly and safe way in creating intranet, extranet and internet websites. Using <a href=\"http://www.contrexx.com/en/index.php?page=1679\" class=\"out\">standardized modules</a> of Contrexx&reg; you are able to quickly realize individual requirements, even supported by professionals where needed.<br />\nThe web based system has been optimized for the management of flexible internet and intranet solutions.<br />\n<br />\nThe download version comes with an integrated web based installer as well as several modules like the Link Directory, Podcast Directory, Online Counter, Address Management, Sitemap, Photo Gallery, Newsletter, Guestbook, RSS Feed Directory, News System, Survey System, Online Shop, Block System and the ability to protect selected sections from public access.</p>\n<h2>Professional and Innovative Distribution</h2>\nContrexx&reg; is distributed worldwide through a network of partner companies. <a aiotitle=\"Contrexx&reg; Solutions Partner\" href=\"http://www.contrexx.com/en/index.php?page=1623\" class=\"out\">Contrexx&reg; Solutions Partners</a> are authorized partner who have a comprehensive knowledge in the domains of web design, graphics and e-commerce. This provides you as a customer with professional support out of your surrounding area.\";s:10:\"sourceMode\";b:0;s:13:\"customContent\";s:0:\"\";s:7:\"cssName\";s:13:\"informationen\";s:10:\"cssNavName\";s:0:\"\";s:4:\"skin\";s:1:\"0\";s:9:\"metatitle\";s:47:\"Contrexx® Web Content Management System (WCMS)\";s:8:\"metadesc\";s:47:\"Contrexx® Web Content Management System (WCMS)\";s:8:\"metakeys\";s:47:\"Contrexx® Web Content Management System (WCMS)\";s:10:\"metarobots\";s:5:\"index\";s:5:\"start\";N;s:3:\"end\";N;s:13:\"editingStatus\";s:0:\"\";s:10:\"protection\";s:1:\"0\";s:16:\"frontendAccessId\";s:1:\"0\";s:15:\"backendAccessId\";s:1:\"0\";s:7:\"display\";s:1:\"1\";s:6:\"active\";s:1:\"1\";s:6:\"target\";s:0:\"\";s:6:\"module\";s:0:\"\";s:3:\"cmd\";s:0:\"\";}','{\"id\":1,\"name\":\"system\"}');
INSERT INTO `contrexx_log_entry` (`id`, `action`, `logged_at`, `version`, `object_id`, `object_class`, `data`, `username`) VALUES (6,'create','2013-05-30 00:00:00',1,'6','Cx\\Core\\ContentManager\\Model\\Entity\\Page','a:31:{s:14:\"nodeIdShadowed\";i:5;s:4:\"lang\";s:1:\"1\";s:4:\"type\";s:7:\"content\";s:7:\"caching\";s:1:\"0\";s:9:\"updatedAt\";O:8:\"DateTime\":3:{s:4:\"date\";s:19:\"2013-05-30 00:00:00\";s:13:\"timezone_type\";i:3;s:8:\"timezone\";s:12:\"Europe/Paris\";}s:9:\"updatedBy\";s:6:\"system\";s:5:\"title\";s:22:\"Übersicht Anwendungen\";s:10:\"linkTarget\";s:0:\"\";s:12:\"contentTitle\";s:22:\"Übersicht Anwendungen\";s:4:\"slug\";s:20:\"bersicht-Anwendungen\";s:7:\"content\";s:4814:\"Contrexx beinhaltet &uuml;ber 20 Anwendungen, die meisten davon k&ouml;nnen auf der Website aktiviert und deaktiviert werden. Die gel&auml;ufigsten dieser Anwendungen sind bereits in diesem Template ersichtlich.<br />\n<br />\nAlle Anwendungen, welche Contrexx unterst&uuml;tzt:<br />\n<h2 style=\"font-size: 21px;\">\n	Hauptanwendungen, die fix in Contrexx integriert sind und nicht deaktiviert werden k&ouml;nnen</h2>\n<h3>\n	Access</h3>\nErm&ouml;glicht das Erstellen von verschiedenen Benutzerbereichen. Beinhaltet Zugriffsschutz, Benutzerkontos und Benutzerregistrierung.<br />\n<h3>\n	Contact</h3>\nHier werden Formulare mit selbst erstellen Feldern erstellt. Dies bietet Kunden zB eine bequeme Kontaktm&ouml;glichkeit. Neue Eintr&auml;ge werden an eine definierte Email Adresse zugesandt sowie im System abgelegt.<br />\n<h3>\n	News</h3>\nErm&ouml;glicht das kategorisierte Erstellen von Newsmeldungen<br />\n<h3>\n	Search</h3>\nErm&ouml;glicht eine Volltextsuche &uuml;ber alle Seiten und das News Modul.<br />\n<h3>\n	Sitemap</h3>\nStellt automatisch anhand der Seiten-Struktur eine Sitemap im Windows Explorer Stil zur Verf&uuml;gung.<br />\n<br />\n<h2>\n	Optionale Anwendungen von Contrexx, die nach Bedarf aktiviert werden k&ouml;nnen</h2>\n<h3>\n	Blog</h3>\nErm&ouml;glicht das Erfassen und Verwalten von Blog-Eintr&auml;gen mit Kategorisierung und Schl&uuml;sselw&ouml;rter.<br />\n<h3>\n	Datenlisten</h3>\nErm&ouml;glicht das Anzeigen von kategorisierten Datenlisten mittels Platzhaltersystem auf beliebigen Stellen der Webseite.<br />\n<h3>\n	Directory</h3>\nHier k&ouml;nnen Eintr&auml;ge erstellt, zu Kategorien zugewiesen und Dateien eingebunden werden.<br />\n<h3>\n	Dokumentensystem</h3>\nHier k&ouml;nnen Dokumente nach bestimmten Kategorien ver&ouml;ffentlicht werden.<br />\n<h3>\n	Downloads</h3>\nNach Kategorien strukturierte Downloadliste mit Dateininformationen (Beschreibung, Gr&ouml;sse, Screenshot, Bewertung).<br />\n<h3>\n	E-Card</h3>\nBeinhaltet das Versenden von digitalen Postkarten mit selbst ausgew&auml;hlten Motiven.<br />\n<h3>\n	E-Government</h3>\nDer Gemeinde-Schalter erm&ouml;glicht es, Gesch&auml;fte (Bestellungen, Antr&auml;ge, Ab/Anmeldungen usw.) mit der Gemeinde direkt &uuml;ber das Internet abzuwickeln.<br />\n<h3>\n	Forum</h3>\nBietet eine Plattform zum Diskutieren von bestimmten Themen, mehrere Benutzer k&ouml;nnen so asynchron (d.h. nicht in Echtzeit) miteinander diskutieren.<br />\n<h3>\n	Galerie</h3>\nBeinhaltet eine Bildergalerie mit Kategorien und Unterkategorien, automatisch generierten Vorschaubildern sowie einem aktivierbaren Bewertungs - und Kommentarsystem.<br />\n<h3>\n	G&auml;stebuch</h3>\nStellt ein G&auml;stebuch auf der Webseite bereit.<br />\n<h3>\n	Jobs</h3>\nBietet die Grundlage f&uuml;r Job Ausschreibungen mit Kategorien. In Verbindung mit dem Kontakt Modul k&ouml;nnen Bewerbungen durch ein Web Formular gemacht werden.<br />\n<h3>\n	Kalender</h3>\nKann als interner oder &ouml;ffentlicher Veranstaltungskalender genutzt werden, Teilnehmer k&ouml;nnen sich optional online anmelden.<br />\n<h3>\n	Kleinanzeigemarkt</h3>\nHier k&ouml;nnen Inserate kategorisiert und zeitlich begrenzt ver&ouml;ffentlicht werden.<br />\n<h3>\n	Knowledge</h3>\nHier kann das Wissen von Redaktoren abgelegt, mit Stichworten indexiert und Internetbesuchern zug&auml;nglich gemacht werden, zB. in Form einer Wissensdatenbank oder einem Glossar.<br />\n<h3>\n	Livecam</h3>\nStellt ein Webcam Archiv mit aktuellen Bildern sowie eine Tages&uuml;bersicht und Archivfunktionen und zur Verf&uuml;gung.<br />\n<h3>\n	Media Directory</h3>\nVerbesserte Version des Directory Moduls. Inhalte werden mit Ebenen und Kategorien versehen, beinhaltet eine unbegrenzte Anzahl von Eingabefeldern.<br />\n<h3>\n	Media Manager</h3>\nErm&ouml;glicht Dokumente und andere Dateien in verschiedenen Medienarchiven f&uuml;r Kunden oder Aussendienstmitarbeiter zum Herunterladen zur Verf&uuml;gung zu stellen.<br />\n<h3>\n	Mitgliederverwaltung</h3>\nErm&ouml;glicht eine Adressen - und Mitgliederverwaltung.<br />\n<h3>\n	Newsletter</h3>\nBeinhaltet ein gruppenbasiertes Newsletter System.<br />\n<h3>\n	Online Zahlung</h3>\nEinfache L&ouml;sung zur Abwicklung von Transaktionen &uuml;ber Yellowpay.<br />\n<h3>\n	Podcast</h3>\nErm&ouml;glicht das Anbieten von Audio- oder Videodateien.<br />\n<h3>\n	RRS</h3>\nBeinhaltet das RSS-Feed Verzeichnis.<br />\n<h3>\n	Seitenempfehlung</h3>\nHier kann die Verlinkung einer Seite der Website per E-Mail mit Beschreibung versendet und weiterempfohlen werden.<br />\n<h3>\n	Shop</h3>\nBeinhaltet einen kompletten Online Shop.<br />\n<h3>\n	User to User</h3>\nErm&ouml;glicht das Versenden von Nachrichten zwischen Benutzerkontos der Mitgliederverwaltung.<br />\n<h3>\n	Voting</h3>\nErm&ouml;glicht das Erstellen und Verwalten von Umfragen. Das System erlaubt nur eine Stimme je Computer und die Auswertungen werden grafisch dargestellt.\";s:10:\"sourceMode\";b:0;s:13:\"customContent\";s:0:\"\";s:7:\"cssName\";s:0:\"\";s:10:\"cssNavName\";s:0:\"\";s:4:\"skin\";s:1:\"0\";s:9:\"metatitle\";s:22:\"Übersicht Anwendungen\";s:8:\"metadesc\";s:22:\"Übersicht Anwendungen\";s:8:\"metakeys\";s:22:\"Übersicht Anwendungen\";s:10:\"metarobots\";s:1:\"1\";s:5:\"start\";N;s:3:\"end\";N;s:13:\"editingStatus\";s:0:\"\";s:10: