INSERT INTO `contrexx_log_entry` (`id`, `action`, `logged_at`, `version`, `object_id`, `object_class`, `data`, `username`) VALUES (439,'update','2013-12-06 13:23:20',13,'146','Cx\\Core\\ContentManager\\Model\\Entity\\Page','a:1:{s:7:\"content\";s:1947:\"<p><a href=\"http://de.wikipedia.org/wiki/Allgemeine_Gesch%C3%A4ftsbedingungen\">Allgemeine Gesch&auml;ftsbedingungen</a> (abgek&uuml;rzt &bdquo;AGB&ldquo;, nicht-standardsprachlich auch oft &bdquo;AGBs&ldquo;,&bdquo;AGB&#39;s&ldquo; oder &bdquo;AGBen&ldquo;) sind alle f&uuml;r eine Vielzahl von Vertr&auml;gen vorformulierten Vertragsbedingungen, die eine Vertragspartei (der Verwender) der anderen Vertragspartei bei Abschluss eines Vertrages stellt.</p>\r\n\r\n<p>Dabei ist es gleichg&uuml;ltig, ob die Bestimmung einen &auml;u&szlig;erlich gesonderten Bestandteil des Vertrags (umgangssprachlich &bdquo;das Kleingedruckte&ldquo; genannt) bilden oder in die Vertragsurkunde selbst aufgenommen werden.</p>\r\n\r\n<br /><br /><br />\r\n\r\n<p><a href=\"http://de.wikipedia.org/wiki/Allgemeine_Gesch%C3%A4ftsbedingungen\">General Terms and Conditions of Business</a> (abbreviated as “GTC”, often also as the non-standard abbreviations “GTCs” or “GTC’s”) are defined as all contractual conditions which are pre-formulated for a large number of contracts and which a contracting party (the user) imposes on the other contracting party when concluding a contract.</p>\r\n\r\n<p>In this context it is irrelevant whether the provisions form an outwardly separate element of the contract (colloquially known as “small print”) or are included in the contractual document itself.</p>\r\n\r\n<br /><br /><br />\r\n\r\n<p><a href=\"http://de.wikipedia.org/wiki/Allgemeine_Gesch%C3%A4ftsbedingungen\">Le Condizioni generali</a> (sigla \r\n  “CG“) sono condizioni contrattuali formulate in precedenza per diversi contratti che una delle parti (l’utilizzatore) presenta all’altra parte nel momento della stipula del contratto.</p>\r\n\r\n<p>Non ha alcuna rilevanza se le disposizioni costituiscono una parte separata del contratto (disposizioni familiarmente note come “clausole scritte in piccolo”) oppure se sono state integrate nel documento contrattuale.</p>\r\n\";}','{\"id\":1,\"name\":\"system@comvation.com\"}');
