INSERT INTO `contrexx_log_entry` (`id`, `action`, `logged_at`, `version`, `object_id`, `object_class`, `data`, `username`) VALUES (489,'update','2013-12-12 09:58:11',3,'168','Cx\\Core\\ContentManager\\Model\\Entity\\Page','a:3:{s:4:\"type\";s:11:\"application\";s:9:\"updatedAt\";O:8:\"DateTime\":3:{s:4:\"date\";s:19:\"2013-12-12 10:58:11\";s:13:\"timezone_type\";i:3;s:8:\"timezone\";s:13:\"Europe/Zurich\";}s:7:\"content\";s:2008:\"<p><a href=\"http://de.wikipedia.org/wiki/Allgemeine_Gesch%C3%A4ftsbedingungen\">Allgemeine Gesch&auml;ftsbedingungen</a> (abgek&uuml;rzt &bdquo;AGB&ldquo;, nicht-standardsprachlich auch oft &bdquo;AGBs&ldquo;,&bdquo;AGB&#39;s&ldquo; oder &bdquo;AGBen&ldquo;) sind alle f&uuml;r eine Vielzahl von Vertr&auml;gen vorformulierten Vertragsbedingungen, die eine Vertragspartei (der Verwender) der anderen Vertragspartei bei Abschluss eines Vertrages stellt.</p>\r\n\r\n<p>Dabei ist es gleichg&uuml;ltig, ob die Bestimmung einen &auml;u&szlig;erlich gesonderten Bestandteil des Vertrags (umgangssprachlich &bdquo;das Kleingedruckte&ldquo; genannt) bilden oder in die Vertragsurkunde selbst aufgenommen werden.</p>\r\n<br />\r\n<br />\r\n&nbsp;\r\n<p><a href=\"http://de.wikipedia.org/wiki/Allgemeine_Gesch%C3%A4ftsbedingungen\">General Terms and Conditions of Business</a> (abbreviated as &ldquo;GTC&rdquo;, often also as the non-standard abbreviations &ldquo;GTCs&rdquo; or &ldquo;GTC&rsquo;s&rdquo;) are defined as all contractual conditions which are pre-formulated for a large number of contracts and which a contracting party (the user) imposes on the other contracting party when concluding a contract.</p>\r\n\r\n<p>In this context it is irrelevant whether the provisions form an outwardly separate element of the contract (colloquially known as &ldquo;small print&rdquo;) or are included in the contractual document itself.</p>\r\n<br />\r\n<br />\r\n&nbsp;\r\n<p><a href=\"http://de.wikipedia.org/wiki/Allgemeine_Gesch%C3%A4ftsbedingungen\">Le Condizioni generali</a> (sigla&nbsp; &ldquo;CG&ldquo;) sono condizioni contrattuali formulate in precedenza per diversi contratti che una delle parti (l&rsquo;utilizzatore) presenta all&rsquo;altra parte nel momento della stipula del contratto.</p>\r\n\r\n<p>Non ha alcuna rilevanza se le disposizioni costituiscono una parte separata del contratto (disposizioni familiarmente note come &ldquo;clausole scritte in piccolo&rdquo;) oppure se sono state integrate nel documento contrattuale.</p>\r\n\";}','{\"id\":1,\"name\":\"system@comvation.com\"}');
