INSERT INTO `contrexx_module_news_locale` (`news_id`, `lang_id`, `is_active`, `title`, `text`, `teaser_text`) VALUES (5,1,1,'MaxMuster AG begrüsst neue Mitarbeiterin','Das MaxMuster Team heisst die neue Mitarbeiterin, Jessica Parker, herzlich willkommen!<br />\r\n<br />\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" style=\"margin: 0 0 15px 0;\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<img alt=\"\" src=\"/images/content/news/beispielbild01.jpg\" style=\"width: 300px; height: 200px; \" /><br />\r\n				<strong>Jessica Parker</strong><br />\r\n				Administration</td>\r\n			<td style=\"vertical-align: top; \">\r\n				Name: Jessica Parker<br />\r\n				<br />\r\n				Zust&auml;ndig: Administration<br />\r\n				<br />\r\n				Ausbildung: Kaufm&auml;nnische Lehre mit Berufsmaturit&auml;t 2005, seit 7 Jahren Berufserfahrung</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<br />\r\nJessica Parker wird mit ihrer 7 j&auml;hrigen Berufserfahrung unser Administrationsteam bestens erg&auml;nzen und verst&auml;rken.<br />\r\n<br />\r\nWir w&uuml;nschen ihr einen guten Start, viel Erfolg und Spass bei der Arbeit.<br />\r\n<br />\r\nDas MaxMuster Team','Wir freuen uns, eine neue Mitarbeiterin, Jessica Parker, begrüssen zu dürfen!');
INSERT INTO `contrexx_module_news_locale` (`news_id`, `lang_id`, `is_active`, `title`, `text`, `teaser_text`) VALUES (5,2,1,'The new employee of MaxMuster AG ','The team of MaxMuster AG welcomes the new employee Jessica Parker.<br />\r\n<br />\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" style=\"margin: 0 0 15px 0;\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<img alt=\"\" src=\"/images/content/news/beispielbild01.jpg\" style=\"width: 300px; height: 200px; \" /><br />\r\n				<strong>Jessica Parker</strong><br />\r\n				administration</td>\r\n			<td style=\"vertical-align: top; \">\r\n				name: Jessica Parker<br />\r\n				<br />\r\n				responsible: administration<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\nWe wish her all the best!<br />\r\n<br />\r\nMaxMuster AG','');
