INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`) VALUES (11,8,'<h3>Online Einbindung mittels RSS-Newsfeed</h3>\r\n\r\n<p><a class=\"rssfeed\" href=\"{NEWS_RSS_FEED_URL}\" target=\"_blank\" title=\"RSS Newsfeed von {NEWS_HOSTNAME}\"><img alt=\"Der Feed dieser Website kann über diesen Link aufgerufen werden\" border=\"0\" height=\"74\" src=\"images/content/icons/rss_bg.gif\" style=\"margin: 5px 20px 20px; float: right;\" width=\"48\" /></a>RSS ist ein elektronisches Nachrichtenformat, das dem Nutzer erm&ouml;glicht, die Inhalte einer Website &ndash; oder Teile davon &ndash; als sogenannte RSS-Feeds zu abonnieren oder in andere Websites zu integrieren.</p>\r\n\r\n<h3>Online Einbindung mittels Javascript</h3>\r\nDer Feed dieser Website kann auch ganz einfach auf Ihrer Website dargestellt werden. Dazu m&uuml;ssen Sie lediglich den folgenden Code in Ihre eigene Webseite einf&uuml;gen:<br />\r\n&nbsp;\r\n<form action=\"\"><textarea cols=\"30\" name=\"code\" rows=\"18\" style=\"width: 98%; font-size: 95%;\">{NEWS_RSS2JS_CODE}</textarea><br />\r\n<br />\r\n<input name=\"button\" onclick=\"javascript:this.form.code.focus();this.form.code.select();\" type=\"button\" value=\"Alles markieren\" />&nbsp;</form>\r\n','News Feed','feed','',8,'on','system',29);
INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`) VALUES (12,8,'<style type=\"text/css\">\r\n    #social-media { display: none; }\r\n</style>\r\n\r\n<script type=\"text/javascript\">\r\n    $J(document).ready(function () {\r\n        $J(\"#news-toggle\").click(function () {\r\n            $J(\"#news-comment-form\").toggle();\r\n        });\r\n    });\r\n</script>\r\n\r\n<div id=\"news-date\">\r\n    {NEWS_DATE}&nbsp;{NEWS_TIME} | {NEWS_AUTHOR}<!-- BEGIN news_comments_count --> | {NEWS_COUNT_COMMENTS}<!-- END news_comments_count -->\r\n</div>\r\n\r\n<!-- BEGIN news_use_teaser_text -->\r\n<div id=\"news-teaser-text\">\r\n    {NEWS_TEASER_TEXT}\r\n</div>\r\n<!-- END news_use_teaser_text -->\r\n\r\n<div id=\"news-content\">\r\n    <!-- BEGIN news_text -->{NEWS_TEXT}<!-- END news_text -->\r\n    <!-- BEGIN news_redirect -->{TXT_NEWS_REDIRECT_INSTRUCTION} <a href=\"{NEWS_REDIRECT_URL}\" target=\"_blank\">{NEWS_REDIRECT_URL}</a><!-- END news_redirect -->\r\n</div>\r\n\r\n<div id=\"news-links\">\r\n    <ul class=\"horizontal-list\">\r\n        <li><a href=\"{PRINT_URL}\">Version zum Drucken</a></li>\r\n        <li><a href=\"index.php?section=recommend\">Per E-Mail versenden</a></li>\r\n        <li class=\"last\"><a href=\"index.php?section=newsletter\">Newsletter abonnieren</a></li>\r\n    </ul>\r\n</div>\r\n\r\n<ul id=\"news-social-media\" class=\"horizontal-list\">\r\n    <li class=\"google-plus\"><div class=\"g-plusone\"></div></li>\r\n    <li class=\"facebook\"><div class=\"fb-like\" data-send=\"false\" data-layout=\"button_count\" data-width=\"450\" data-show-faces=\"false\" data-action=\"recommend\" data-font=\"segoe ui\"></div></li>\r\n    <li class=\"twitter\"><a href=\"https://twitter.com/share\" class=\"twitter-share-button\" data-lang=\"de\">Twittern</a></li>\r\n    <li class=\"xing\"><script type=\"XING/Share\" data-counter=\"right\" data-lang=\"de\"></script></li>\r\n    <li class=\"rss-feed last\"><a href=\"feed/news_de.xml\"></a></li>\r\n</ul>\r\n\r\n<!-- BEGIN news_comments -->\r\n    <!-- BEGIN news_add_comment -->\r\n        <input id=\"news-toggle\" type=\"submit\" value=\"Kommentar schreiben\" />\r\n        <form id=\"news-comment-form\" method=\"post\" action=\"index.php?section=news&amp;cmd=details&amp;newsid={NEWS_ID}\">\r\n            <fieldset> \r\n                <legend>{TXT_NEWS_ADD_COMMENT}</legend>\r\n\r\n                <!-- BEGIN news_add_comment_error -->\r\n                <div class=\"news-error\">{NEWS_ADD_COMMENT_ERROR}</div>\r\n                <!-- END news_add_comment_error -->\r\n\r\n                <!-- BEGIN news_add_comment_name -->\r\n                <p>\r\n                    <label for=\"news_comment_name\">{TXT_NEWS_NAME}</label>\r\n                    <input type=\"text\" name=\"news_comment_name\" id=\"news_comment_name\" value=\"{NEWS_COMMENT_NAME}\" />\r\n                </p>\r\n                <!-- END news_add_comment_name -->\r\n                <p>\r\n                    <label for=\"news_comment_title\">{TXT_NEWS_TITLE}</label>\r\n                    <input type=\"text\" name=\"news_comment_title\" id=\"news_comment_title\" value=\"{NEWS_COMMENT_TITLE}\" />\r\n                </p>\r\n                <p>\r\n                    <label for=\"news_comment_message\">{TXT_NEWS_COMMENT}</label>\r\n                    <textarea name=\"news_comment_message\" id=\"news_comment_message\" cols=\"40\" rows=\"7\">{NEWS_COMMENT_MESSAGE}</textarea>\r\n                </p>\r\n\r\n                <!-- BEGIN news_add_comment_captcha -->\r\n                <p>\r\n                    <label>{TXT_NEWS_CAPTCHA}</label>\r\n                    {NEWS_COMMENT_CAPTCHA_CODE}\r\n                </p>\r\n                <!-- END news_add_comment_captcha -->\r\n\r\n                <p><input name=\"news_add_comment\" type=\"submit\" value=\"{TXT_NEWS_ADD}\" /></p>\r\n            </fieldset>\r\n        </form>\r\n    <!-- END news_add_comment -->\r\n\r\n    <!-- BEGIN news_no_comment -->\r\n    <div id=\"news-no-comments\">{TXT_NEWS_COMMENTS_NONE_EXISTING}</div>\r\n    <!-- END news_no_comment -->\r\n\r\n    <!-- BEGIN news_comment_list -->\r\n        <!-- BEGIN news_comment -->\r\n        <div class=\"news-comment\">\r\n            <div class=\"news-comment-poster\">{NEWS_COMMENTS_LONG_DATE} | {NEWS_COMMENTS_POSTER}</div>\r\n            <div class=\"news-comment-title\"><strong>{NEWS_COMMENTS_TITLE}</strong></div>\r\n            <div class=\"news-comment-message\">{NEWS_COMMENTS_MESSAGE}</div>\r\n        </div>\r\n        <!-- END news_comment -->\r\n    <!-- END news_comment_list -->\r\n<!-- END news_comments -->\r\n\r\n<script type=\"text/javascript\">\r\n    window.___gcfg = {lang: \'de\'};\r\n\r\n    (function () {\r\n        var po = document.createElement(\'script\'); po.type = \'text/javascript\'; po.async = true;\r\n        po.src = \'https://apis.google.com/js/plusone.js\';\r\n        var s = document.getElementsByTagName(\'script\')[0]; s.parentNode.insertBefore(po, s);\r\n    })();\r\n\r\n    (function (d, s, id) {\r\n        var js, fjs = d.getElementsByTagName(s)[0];\r\n        if (d.getElementById(id)) return;\r\n        js = d.createElement(s); js.id = id;\r\n        js.src = \"//connect.facebook.net/de_DE/all.js#xfbml=1\";\r\n        fjs.parentNode.insertBefore(js, fjs);\r\n    }(document, \'script\', \'facebook-jssdk\'));\r\n\r\n    !function (d,s,id) {\r\n        var js,fjs = d.getElementsByTagName(s)[0];\r\n        if (!d.getElementById(id)) {\r\n            js = d.createElement(s);\r\n            js.id=id;js.src=\"//platform.twitter.com/widgets.js\";\r\n            fjs.parentNode.insertBefore(js,fjs);\r\n        }\r\n    }(document,\"script\",\"twitter-wjs\");\r\n\r\n    ;(function (d, s) {\r\n        var x = d.createElement(s),\r\n        s = d.getElementsByTagName(s)[0];\r\n        x.src =\'https://www.xing-share.com/js/external/share.js\';\r\n        s.parentNode.insertBefore(x, s);\r\n    })(document, \'script\');\r\n</script>','Newsmeldung','details','y',8,'','system',31);
