INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`) VALUES (30,16,'<div id=\"shop\">\r\n    <!-- BEGIN core_message -->\r\n    <div class=\"{MESSAGE_CLASS}\">{MESSAGE_TEXT}</div>\r\n    <!-- END core_message -->\r\n    <!-- BEGIN shopCart -->\r\n    <form action=\"index.php?section=shop&amp;cmd=cart\" name=\"shopForm\" method=\"post\">\r\n        <table summary=\"\" cellspacing=\"0\">\r\n            <tr valign=\"top\" id=\"th_bg\">\r\n                <th width=\"50%\">{TXT_PRODUCT}</th>\r\n                <th width=\"12%\">{TXT_UNIT_PRICE}</th>\r\n                <th width=\"12%\">{TXT_QUANTITY}</th>\r\n                <th width=\"14%\" colspan=\"2\">{TXT_TAX_PREFIX}</th>\r\n                <th width=\"12%\" class=\"a_right\">{TXT_TOTAL}</th>\r\n            </tr>\r\n            <!-- BEGIN shopCartRow -->\r\n            <tr valign=\"top\" class=\"{SHOP_PRODUCT_ROW}\">\r\n                <td>\r\n                    <a href=\"index.php?section=shop&amp;cmd=details&amp;referer=cart&amp;productId={SHOP_PRODUCT_CART_ID}\">\r\n                    {SHOP_PRODUCT_TITLE}\r\n                    </a><!-- BEGIN attributes -->\r\n                    <br />{SHOP_PRODUCT_OPTIONS}\r\n                    <!-- END attributes -->\r\n                </td>\r\n                <td>\r\n                    {SHOP_PRODUCT_ITEMPRICE}&nbsp;{SHOP_PRODUCT_ITEMPRICE_UNIT}\r\n                </td>\r\n                <td>\r\n                    <a class=\"delete-icon\" href=\"#\" title=\"{TXT_SHOP_REMOVE_ITEM}\" onclick=\"deleteProduct({SHOP_PRODUCT_CART_ID}); return false;\">\r\n                        <img style=\"height: 18px;\" alt=\"{TXT_SHOP_REMOVE_ITEM}\" src=\"images/modules/shop/trash.png\" />\r\n                    </a>\r\n                    <input type=\"text\" name=\"quantity[{SHOP_PRODUCT_CART_ID}]\" value=\"{SHOP_PRODUCT_QUANTITY}\" id=\"quantity-{SHOP_PRODUCT_CART_ID}\" class=\"shop_quantity\" />\r\n                </td>\r\n                <td>{SHOP_PRODUCT_TAX_RATE}</td>\r\n                <td>{SHOP_PRODUCT_TAX_AMOUNT}</td>\r\n                <td class=\"a_right\">\r\n                    {SHOP_PRODUCT_PRICE}&nbsp;{SHOP_PRODUCT_PRICE_UNIT}\r\n                </td>\r\n            </tr>\r\n            <!-- END shopCartRow -->\r\n            <!-- BEGIN totalExclTaxRow -->\r\n            <tr class=\"row1\">\r\n                <td colspan=\"5\">{TXT_NET_PRICE}</td>\r\n                <td style=\"text-align: right;\">\r\n                    {SHOP_GRAND_TOTAL_EXCL_TAX}\r\n                </td>\r\n            </tr>\r\n            <!-- END totalExclTaxRow -->\r\n            <!-- BEGIN shopVat -->\r\n            <tr class=\"row1\">\r\n                <td colspan=\"5\" valign=\"top\">\r\n                    {TXT_TAX_PREFIX}\r\n                </td>\r\n                <td valign=\"top\" nowrap=\"nowrap\" class=\"a_right\">\r\n                    {SHOP_TOTAL_TAX_AMOUNT}\r\n                </td>\r\n            </tr>\r\n            <!-- END shopVat -->\r\n            <!-- BEGIN shopCouponTotal -->\r\n            <tr valign=\"top\" class=\"row3\">\r\n                <td colspan=\"5\">{SHOP_DISCOUNT_COUPON_TOTAL}</td>\r\n                <td class=\"a_right\">\r\n                    {SHOP_DISCOUNT_COUPON_TOTAL_AMOUNT}&nbsp;{SHOP_PRICE_UNIT}\r\n                </td>\r\n            </tr>\r\n            <!-- END shopCouponTotal -->\r\n            <tr class=\"row3\">\r\n                <td colspan=\"5\" valign=\"top\">\r\n                    <strong>{TXT_INTER_TOTAL}</strong>\r\n                </td>\r\n                <td valign=\"top\" nowrap=\"nowrap\" class=\"a_right\">\r\n                    <strong>{SHOP_PRODUCT_TOTALPRICE_PLUS_VAT} {SHOP_PRODUCT_TOTALPRICE_UNIT}</strong>\r\n                </td>\r\n            </tr>\r\n            <!-- BEGIN shopCoupon -->\r\n            <tr>\r\n                <td colspan=\"6\">&nbsp;</td>\r\n            </tr>\r\n            <tr class=\"row3\">\r\n                <td>\r\n                    {TXT_SHOP_DISCOUNT_COUPON_CODE}\r\n                </td>\r\n                <td colspan=\"5\" class=\"a_right\">\r\n                    <input type=\"text\" maxlength=\"20\" name=\"coupon_code\" value=\"{SHOP_DISCOUNT_COUPON_CODE}\" class=\"shop_cart_input\" onchange=\"document.forms[\'shopForm\'].submit();\" />\r\n                </td>\r\n            </tr>\r\n            <!-- END shopCoupon -->\r\n            <!-- BEGIN shopShippingCountry -->\r\n            <tr class=\"row3\">\r\n                <td valign=\"top\">\r\n                    <label>{TXT_SHIP_COUNTRY}</label>\r\n                </td>\r\n                <td colspan=\"5\" valign=\"top\" class=\"a_right\">\r\n                    <select name=\"countryId2\" class=\"shop_cart_input\" onchange=\"document.forms[\'shopForm\'].submit()\">\r\n                    {SHOP_COUNTRIES_MENUOPTIONS}\r\n                    </select>\r\n                </td>\r\n            </tr>\r\n            <!-- END shopShippingCountry -->\r\n        </table>\r\n        <div class=\"cart-buttons\">\r\n            <input type=\"submit\" name=\"update\" value=\"{TXT_UPDATE}\" />&nbsp;\r\n            <input type=\"submit\" name=\"continue\" class=\"continue-button\" value=\"{TXT_NEXT} &gt;&gt;\" />\r\n        </div>\r\n    </form>\r\n    <!-- END shopCart -->\r\n    <div class=\"cart-links\">\r\n        <a href=\"index.php?section=shop\" title=\"{TXT_CONTINUE_SHOPPING}\">{TXT_CONTINUE_SHOPPING}</a><br />\r\n        <a href=\"index.php?section=shop&amp;act=destroy\" title=\"{TXT_EMPTY_CART}\">{TXT_EMPTY_CART}</a>\r\n    </div>\r\n</div>','Ihr Warenkorb','cart','y',42,'on','system',50);
INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`) VALUES (31,16,'<div id=\"shop\">\r\n<!-- BEGIN core_message -->\r\n  <span class=\"{MESSAGE_CLASS}\">{MESSAGE_TEXT}</span>\r\n<!-- END core_message -->\r\n  <div class=\"lsv_form\">\r\n    <form action=\"#\" name=\"LSVForm\" method=\"get\">\r\n      <h2>{TXT_SHOP_LSV_EE_RECIPIENT}</h2>\r\n      {SHOP_COMPANY}<br />\r\n      {SHOP_ADDRESS}<br />\r\n      {TXT_SHOP_LSV_EE_FAX}&nbsp;{SHOP_FAX}\r\n      <h2>{TXT_SHOP_LSV_EE_REASON}</h2>\r\n      <p><label>{TXT_SHOP_LSV_EE_DESCRIPTION}</label><textarea rows=\"1\" cols=\"1\">{SHOP_LSV_EE_PRODUCTS}&nbsp;</textarea></p>\r\n      <h2>{TXT_SHOP_LSV_EE_CUSTOMER}</h2>\r\n      <p><label>{TXT_SHOP_LSV_EE_FIRST_NAME}</label><input type=\"text\" value=\"{SHOP_CUSTOMER_FIRST_NAME}\" /></p>\r\n      <p><label>{TXT_SHOP_LSV_EE_LAST_NAME}</label><input type=\"text\" value=\"{SHOP_CUSTOMER_LAST_NAME}\" /></p>\r\n      <p><label>{TXT_SHOP_LSV_EE_STREETNO}</label><input type=\"text\" value=\"{SHOP_CUSTOMER_ADDRESS}\" /></p>\r\n      <p><label>{TXT_SHOP_LSV_EE_ZIP}</label><input type=\"text\" value=\"{SHOP_CUSTOMER_ZIP}\" /></p>\r\n      <p><label>{TXT_SHOP_LSV_EE_CITY}</label><input type=\"text\" value=\"{SHOP_CUSTOMER_CITY}\" /></p>\r\n      <p><label>{TXT_SHOP_LSV_EE_PHONE}</label><input type=\"text\" value=\"{SHOP_CUSTOMER_PHONE}\" /></p>\r\n      <p><label>{TXT_SHOP_LSV_EE_FAXNO}</label><input type=\"text\" value=\"{SHOP_CUSTOMER_FAX}\" /></p>\r\n      <p><label>{TXT_SHOP_LSV_EE_EMAIL}</label><input type=\"text\" value=\"{SHOP_CUSTOMER_EMAIL}\" /></p>\r\n      <p><label>{TXT_SHOP_LSV_EE_BANK}</label><input type=\"text\" value=\"{SHOP_CUSTOMER_BANK}\" /></p>\r\n      <p><label>{TXT_SHOP_LSV_EE_BANKCODE}</label><input type=\"text\" value=\"{SHOP_CUSTOMER_BANKCODE}\" /></p>\r\n      <p><label>{TXT_SHOP_LSV_EE_ACCOUNT}</label><input type=\"text\" value=\"{SHOP_CUSTOMER_ACCOUNT}\" /></p>\r\n      <p><label>{TXT_SHOP_LSV_EE_DATE}</label>{SHOP_DATE}</p>\r\n      <br />\r\n      <p><label>{TXT_SHOP_LSV_EE_SIGNATURE}</label><br /></p>\r\n    </form>\r\n  </div>\r\n</div>\r\n','Einzugsermächtigung','lsv','y',42,'','system',52);
INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`) VALUES (32,16,'<div id=\"shop\">\r\n    <!-- BEGIN core_message -->\r\n    <span class=\"{MESSAGE_CLASS}\">{MESSAGE_TEXT}</span><!-- END core_message -->\r\n    <div id=\"shop_acc_data\">\r\n        <form name=\"account\" action=\"{SHOP_ACCOUNT_ACTION}\" method=\"post\" onsubmit=\"copy_address()\" onreset=\"return shopReset()\">\r\n            <div class=\"customer_address\">\r\n                <h2>{TXT_CUSTOMER_ADDRESS}</h2>\r\n                <div class=\"shop_text\">\r\n                    <p><label>{TXT_COMPANY}</label>\r\n                        <input type=\"text\" tabindex=\"1\" name=\"company\" value=\"{SHOP_ACCOUNT_COMPANY}\" />\r\n                    </p>\r\n                    <p><label>{TXT_GREETING}<font color=\"#ff0000\">&nbsp;*</font></label>\r\n                        <select tabindex=\"2\" name=\"gender\">{SHOP_ACCOUNT_PREFIX}</select>\r\n                    </p>\r\n                    <p><label>{TXT_SURNAME}<font color=\"#ff0000\">&nbsp;*</font></label>\r\n                        <input type=\"text\" tabindex=\"3\" name=\"lastname\" value=\"{SHOP_ACCOUNT_LASTNAME}\" />\r\n                    </p>\r\n                    <p><label>{TXT_FIRSTNAME}<font color=\"#ff0000\">&nbsp;*</font></label>\r\n                        <input type=\"text\" tabindex=\"4\" name=\"firstname\" value=\"{SHOP_ACCOUNT_FIRSTNAME}\" />\r\n                    </p>\r\n                    <p><label>{TXT_ADDRESS}<font color=\"#ff0000\">&nbsp;*</font></label>\r\n                        <input type=\"text\" tabindex=\"5\" name=\"address\" value=\"{SHOP_ACCOUNT_ADDRESS}\" />\r\n                    </p>\r\n                    <p><label>{TXT_POSTALE_CODE}<font color=\"#ff0000\">&nbsp;*</font></label>\r\n                        <input type=\"text\" tabindex=\"6\" name=\"zip\" value=\"{SHOP_ACCOUNT_ZIP}\" />\r\n                    </p>\r\n                    <p><label>{TXT_CITY}<font color=\"#ff0000\">&nbsp;*</font></label>\r\n                        <input type=\"text\" tabindex=\"7\" name=\"city\" value=\"{SHOP_ACCOUNT_CITY}\" />\r\n                    </p>\r\n                    <p><label>{TXT_COUNTRY}</label>\r\n                        <select name=\"countryId\" id=\"countryId\" tabindex=\"8\">\r\n                        {SHOP_ACCOUNT_COUNTRY_MENUOPTIONS}\r\n                        </select>\r\n                    </p>\r\n                    <p><label>{TXT_PHONE_NUMBER}<font color=\"#ff0000\">&nbsp;*</font></label>\r\n                        <input type=\"text\" tabindex=\"9\" name=\"phone\" value=\"{SHOP_ACCOUNT_PHONE}\" />\r\n                    </p>\r\n                    <p><label>{TXT_FAX_NUMBER}</label>\r\n                        <input type=\"text\" tabindex=\"10\" name=\"fax\" value=\"{SHOP_ACCOUNT_FAX}\" />\r\n                    </p>\r\n                </div>\r\n            </div>\r\n            <!-- BEGIN shipping_address -->\r\n            <div class=\"shipping_address\">\r\n                <h2>{TXT_SHIPPING_ADDRESS}</h2>\r\n                <p><input type=\"checkbox\" tabindex=\"21\" value=\"1\" onclick=\"copy_address();\"\r\n                    id=\"equal_address\" name=\"equal_address\" {SHOP_EQUAL_ADDRESS_CHECKED} />\r\n                    <label class=\"description\" for=\"equal_address\">{TXT_SAME_BILLING_ADDRESS}</label>\r\n                </p>\r\n            </div>\r\n            <div id=\"shipping_address\" style=\"display: {SHOP_EQUAL_ADDRESS_DISPLAY};\">\r\n                <div class=\"shop_text\">\r\n                    <p><label>{TXT_COMPANY}</label>\r\n                        <input type=\"text\" tabindex=\"31\" name=\"company2\" value=\"{SHOP_ACCOUNT_COMPANY2}\" />\r\n                    </p>\r\n                    <p><label>{TXT_GREETING}<font color=\"#ff0000\">&nbsp;*</font></label>\r\n                        <select tabindex=\"32\" name=\"gender2\">{SHOP_ACCOUNT_PREFIX2}</select>\r\n                    </p>\r\n                    <p><label>{TXT_SURNAME}<font color=\"#ff0000\">&nbsp;*</font></label>\r\n                        <input type=\"text\" tabindex=\"33\" name=\"lastname2\" value=\"{SHOP_ACCOUNT_LASTNAME2}\" />\r\n                    </p>\r\n                    <p><label>{TXT_FIRSTNAME}<font color=\"#ff0000\">&nbsp;*</font></label>\r\n                        <input type=\"text\" tabindex=\"34\" name=\"firstname2\" value=\"{SHOP_ACCOUNT_FIRSTNAME2}\" />\r\n                    </p>\r\n                    <p><label>{TXT_ADDRESS}<font color=\"#ff0000\">&nbsp;*</font></label>\r\n                        <input type=\"text\" tabindex=\"35\" name=\"address2\" value=\"{SHOP_ACCOUNT_ADDRESS2}\" />\r\n                    </p>\r\n                    <p><label>{TXT_POSTALE_CODE}<font color=\"#ff0000\">&nbsp;*</font></label>\r\n                        <input type=\"text\" tabindex=\"36\" name=\"zip2\" value=\"{SHOP_ACCOUNT_ZIP2}\" size=\"6\" />\r\n                    </p>\r\n                    <p><label>{TXT_CITY}<font color=\"#ff0000\">&nbsp;*</font></label>\r\n                        <input type=\"text\" tabindex=\"37\" name=\"city2\" value=\"{SHOP_ACCOUNT_CITY2}\" />\r\n                    </p>\r\n                    <p><label>{TXT_COUNTRY}</label>\r\n                        <input type=\"hidden\" name=\"countryId2\" id=\"countryId2\" value=\"{SHOP_ACCOUNT_COUNTRY2_ID}\" />{SHOP_ACCOUNT_COUNTRY2}\r\n                    </p>\r\n                    <p><label>{TXT_PHONE_NUMBER}<font color=\"#ff0000\">&nbsp;*</font></label>\r\n                        <input type=\"text\" tabindex=\"38\" name=\"phone2\" value=\"{SHOP_ACCOUNT_PHONE2}\" />\r\n                    </p>\r\n                </div>\r\n            </div>\r\n            <!-- END shipping_address --><!-- BEGIN account_details -->\r\n            <div class=\"account_details\">\r\n                <h2>{TXT_YOUR_ACCOUNT_DETAILS}</h2>\r\n                <!-- BEGIN dont_register -->\r\n                <p>\r\n                    <input type=\"checkbox\" tabindex=\"61\" value=\"1\" id=\"dont_register\" name=\"dont_register\" {SHOP_DONT_REGISTER_CHECKED} onClick=\"document.getElementById(\'account_password\').style.display = (this.checked ? \'none\' : \'block\');\" />\r\n                    <label class=\"description\" for=\"dont_register\">{TXT_SHOP_ACCOUNT_DONT_REGISTER}</label>\r\n                    <br />\r\n                    {TXT_SHOP_ACCOUNT_DONT_REGISTER_NOTE}\r\n                </p>\r\n                <!-- END dont_register -->\r\n                <div class=\"shop_text\">\r\n                    <p>\r\n                        <label>{TXT_EMAIL}<font color=\"#ff0000\">&nbsp;*</font></label>\r\n                        <input type=\"text\" tabindex=\"51\" name=\"email\" value=\"{SHOP_ACCOUNT_EMAIL}\" />\r\n                    </p>\r\n                    <div id=\"account_password\" style=\"{SHOP_ACCOUNT_PASSWORD_DISPLAY};\">\r\n                        <p>\r\n                            <label>{TXT_PASSWORD}<font color=\"#ff0000\">&nbsp;*</font></label>\r\n                            <input type=\"password\" tabindex=\"52\" name=\"password\" value=\"\" />\r\n                        </p>\r\n                        <p>{TXT_SHOP_ACCOUNT_PASSWORD_HINT}</p>\r\n                    </div>\r\n                </div>\r\n            </div>\r\n            <!-- END account_details -->\r\n            <p>\r\n                <input type=\"reset\" value=\"{TXT_RESET}\" name=\"reset\" tabindex=\"71\" />\r\n                <input type=\"submit\" value=\"{TXT_SHOP_CONTINUE_ARROW}\" name=\"bsubmit\" tabindex=\"72\" />\r\n            </p>\r\n        </form>\r\n    </div>\r\n</div>\r\n<script type=\"text/javascript\">//<![CDATA[\r\n    function copy_address() {\r\n      with (document.account) {\r\n        var disp = \"block\";\r\n        if (equal_address && equal_address.checked) {\r\n          gender2.value = gender.value;\r\n          company2.value = company.value;\r\n          lastname2.value = lastname.value;\r\n          firstname2.value = firstname.value;\r\n          address2.value = address.value;\r\n          zip2.value = zip.value;\r\n          city2.value = city.value;\r\n          phone2.value = phone.value;\r\n          countryId2.value = countryId.value;\r\n          disp = \"none\";\r\n    /* Optionally clear the shipment address\r\n        } else {\r\n          gender2.value = \"\";\r\n          company2.value = \"\";\r\n          lastname2.value = \"\";\r\n          firstname2.value = \"\";\r\n          address2.value = \"\";\r\n          zip2.value = \"\";\r\n          city2.value = \"\";\r\n          phone2.value = \"\";\r\n    */\r\n        }\r\n        if (document.getElementById(\'shipping_address\')) {\r\n          document.getElementById(\'shipping_address\').style.display = disp;\r\n        }\r\n      }\r\n    }\r\n    // Redisplay the shipping address after the reset button has been clicked\r\n    function shopReset()\r\n    {\r\n      if (!confirm(\"{TXT_SHOP_FORM_RESET_CONFIRM}\")) {\r\n        return false;\r\n      }\r\n      document.getElementById(\'shipping_address\').style.display = \"block\";\r\n      return true;\r\n    }\r\n    for (var element in document.getElementsByTagName(\"input\")) {\r\n      element.onchange = function() { copy_address(); };\r\n    }\r\n    for (var element in document.getElementsByTagName(\"select\")) {\r\n      element.onchange = function() { copy_address(); };\r\n    }\r\n    copy_address();\r\n    //]]>\r\n</script>','Kontoangaben','account','y',42,'','system',54);
INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`) VALUES (33,16,'{SHOP_JAVASCRIPT_CODE}\r\n<div id=\"shop\">\r\n    <!-- BEGIN core_message -->\r\n    <span class=\"{MESSAGE_CLASS}\">{MESSAGE_TEXT}</span>\r\n    <!-- END core_message -->\r\n    <div class=\"menu\">\r\n        <form name=\"shopSearch\" action=\"index.php?section=shop\" method=\"post\">\r\n            <select name=\"catId\" tabindex=\"2\">\r\n            {SHOP_CATEGORIES_MENUOPTIONS}\r\n            </select>\r\n            <input type=\"text\" name=\"term\" value=\"{SHOP_SEARCH_TERM}\" tabindex=\"1\" style=\"width: 170px;\" />&nbsp;\r\n            <input type=\"submit\" name=\"bsubmit\" value=\"{TXT_SHOP_SEARCH}\" style=\"width: 66px;\" />\r\n        </form>\r\n    </div>\r\n    <!-- <div class=\"cart_info\">{SHOP_CART_INFO}</div> -->\r\n    <div class=\"product detail\" itemscope itemtype=\"http://schema.org/Offer\">\r\n        <!-- BEGIN seller -->\r\n        <span itemprop=\"seller\" itemscope itemtype=\"http://schema.org/Organization\">\r\n            <meta itemprop=\"name\" content=\"{SHOP_SELLER_NAME}\" />\r\n            <meta itemprop=\"url\" content=\"{SHOP_SELLER_URL}\" />\r\n        </span>\r\n        <!-- END seller -->\r\n        <form id=\"{SHOP_PRODUCT_FORM_NAME}\" name=\"{SHOP_PRODUCT_FORM_NAME}\" action=\"index.php?section=shop&amp;cmd=cart\" method=\"post\" {SHOP_FORM_ENCTYPE}>\r\n            <div itemprop=\"itemOffered\" itemscope itemtype=\"http://schema.org/Product\">\r\n                <meta itemprop=\"url\" content=\"{SHOP_PRODUCT_DETAIL_URL}\" />\r\n                <input type=\"hidden\" name=\"productId\" value=\"{SHOP_PRODUCT_ID}\" />\r\n                <div class=\"images\">\r\n                    <meta itemprop=\"image\" content=\"{SHOP_PRODUCT_IMAGE}\" />\r\n                    <!-- BEGIN productImage1 -->\r\n                    <a href=\"{SHOP_PRODUCT_THUMBNAIL_LINK_1}\