INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`) VALUES (50,20,'{FORUM_JAVASCRIPT} \r\n{FORUM_JAVASCRIPT_GOTO}\r\n<div id=\"forum\">\r\n	<!-- <div class=\"links\">{FORUM_COMMUNITY_LINKS}</div> -->\r\n	<div class=\"latest\">\r\n		<h2>{TXT_FORUM_LATEST_ENTRIES}</h2>\r\n		<table cellspacing=\"0\">\r\n			<tr>\r\n				<th width=\"60%\">{TXT_FORUM_THREAD}</th>\r\n				<th width=\"22%\" nowrap=\"nowrap\" align=\"left\">{TXT_FORUM_THREAD_STRATER}</th>		\r\n				<th width=\"8%\" nowrap=\"nowrap\" align=\"center\">{TXT_FORUM_POST_COUNT}</th>		\r\n				<th width=\"10%\" nowrap=\"nowrap\" align=\"center\">{TXT_FORUM_THREAD_CREATE_DATE}</th>\r\n			</tr>\r\n			<!-- BEGIN latestPosts -->\r\n			<tr class=\"row{FORUM_ROWCLASS}\">\r\n				<td><b>{FORUM_THREAD}<b></td>\r\n				<td nowrap=\"nowrap\" align=\"left\" class=\"smallfont\">{FORUM_THREAD_STARTER}</td>\r\n				<td nowrap=\"nowrap\" align=\"center\">{FORUM_POST_COUNT}</td>\r\n				<td nowrap=\"nowrap\" align=\"center\">{FORUM_THREAD_CREATE_DATE}</td>\r\n			</tr>	\r\n			<!-- END latestPosts -->	\r\n		</table>\r\n	</div>\r\n	<!-- BEGIN forumMainCategory -->\r\n	<div class=\"category\">\r\n		<h2>{FORUM_MAINCATEGORY_NAME_TITLE}</h2>{FORUM_MAINCATEGORY_NAME_DESCRIPTION}\r\n		<div style=\"display: block;\" id=\"maincat_{FORUM_MAINCATEGORY_ID}\">\r\n			<table cellspacing=\"0\">\r\n				<tr>\r\n					<th width=\"50%\">{TXT_FORUM}</th>\r\n					<th width=\"49%\" nowrap=\"nowrap\">{TXT_LASTPOST}</th>		\r\n					<th width=\"1%\" nowrap=\"nowrap\" align=\"center\">{TXT_THREADS}</th>		\r\n				</tr>\r\n				<!-- BEGIN forumSubCategory -->\r\n				<tr class=\"row{FORUM_SUBCATEGORY_ROWCLASS}\">\r\n					<td valign=\"top\">\r\n						<div style=\"padding-left: {FORUM_SUBCATEGORY_SPACER}px;\" title=\"{FORUM_SUBCATEGORY_DESC}\">\r\n							<a title=\"{FORUM_SUBCATEGORY_NAME}\" href=\"index.php?section=forum&amp;cmd=board&amp;id={FORUM_SUBCATEGORY_ID}\"><b>{FORUM_SUBCATEGORY_NAME}</b></a><br />{FORUM_SUBCATEGORY_DESC}\r\n						</div>\r\n					</td>\r\n					<td nowrap=\"nowrap\" valign=\"top\">{FORUM_SUBCATEGORY_LASTPOST_TITLE} - {FORUM_SUBCATEGORY_LASTPOST_DATE}</td>\r\n					<td nowrap=\"nowrap\" align=\"center\" valign=\"top\">{FORUM_SUBCATEGORY_THREADS}</td>\r\n				</tr>	\r\n				<!-- END forumSubCategory -->\r\n			</table>\r\n		</div>\r\n	</div>\r\n	<!-- END forumMainCategory --> \r\n</div>','Forum','','y',0,'on','system',194);
INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`) VALUES (51,20,'{FORUM_JAVASCRIPT_NOTIFICATION}\r\n<div id=\"forum\">\r\n	<!-- <div class=\"links\">{FORUM_COMMUNITY_LINKS}</div> -->\r\n	<!-- BEGIN notification -->\r\n	<fieldset> \r\n        <legend>{TXT_FORUM_THREAD_NOTIFICATION}</legend>\r\n        <form id=\"frmForumNotification\" action=\"index.php?section=forum&amp;cmd=notification\" method=\"post\" onsubmit=\"SelectAllList(document.getElementById(\'frmForumNotification\').elements[\'subscribed[]\']);\">\r\n            <div class=\"notification\">\r\n    			<div class=\"notification_left\">\r\n    				<h3>Verf&uuml;gbare Foren/Kategorien</h3>\r\n    				<select name=\"unsubscribed[]\" size=\"32\" multiple=\"multiple\">\r\n    				{FORUM_NOTIFICATION_UNSUBSCRIBED}\r\n    				</select>\r\n    				<br />\r\n    				<a href=\"javascript:SelectAllList(document.getElementById(\'frmForumNotification\').elements[\'unsubscribed[]\']);\">{TXT_SELECT_ALL}<br />\r\n    				</a><a href=\"javascript:DeselectAllList(document.getElementById(\'frmForumNotification\').elements[\'unsubscribed[]\']);\">{TXT_DESELECT_ALL}</a><br />\r\n    			</div>\r\n    			<div class=\"notification_center\">\r\n    				<input type=\"button\" value=\"&gt;&gt;\" name=\"adduser\" onclick=\"AddToTheList(document.getElementById(\'frmForumNotification\').elements[\'unsubscribed[]\'],document.getElementById(\'frmForumNotification\').elements[\'subscribed[]\'],adduser,removeuser);\" /><br />\r\n    				<input type=\"button\" value=\"&lt;&lt;\" name=\"removeuser\" onclick=\"RemoveFromTheList(document.getElementById(\'frmForumNotification\').elements[\'unsubscribed[]\'],document.getElementById(\'frmForumNotification\').elements[\'subscribed[]\'],adduser,removeuser);\" />\r\n    			</div>\r\n    			<div class=\"notification_right\">\r\n    				<h3>Abonnierte Foren/Kategorien</h3>\r\n    				<select name=\"subscribed[]\" size=\"32\" multiple=\"multiple\">\r\n    				{FORUM_NOTIFICATION_SUBSCRIBED}\r\n    				</select>\r\n    				<br />\r\n    				<a href=\"javascript:SelectAllList(document.getElementById(\'frmForumNotification\').elements[\'subscribed[]\']);\">{TXT_SELECT_ALL}<br />\r\n    				</a><a href=\"javascript:DeselectAllList(document.getElementById(\'frmForumNotification\').elements[\'subscribed[]\']);\">{TXT_DESELECT_ALL}</a>\r\n    			</div>\r\n            </div>\r\n            <input type=\"submit\" value=\"{TXT_FORUM_NOTIFICATION_SUBMIT}\" name=\"forumNotificationSubmit\" />\r\n        </form>\r\n    </fieldset> \r\n	<!-- END notification -->\r\n</div>','Abonnierte Foren/Kategorien','notification','y',50,'on','system',195);
INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`) VALUES (52,20,'Eine <strong>Tag Cloud</strong> ist eine Methode zur Informationsvisualisierung, bei der eine Liste aus Schlagworten zweidimensional alphabetisch sortiert angezeigt wird, wobei einzelne unterschiedlich gewichtete Worte gr&ouml;sser oder auf andere Weise hervorgehoben dargestellt werden. Wortwolken werden zunehmend beim gemeinschaftlichen Indexieren und in Blogs eingesetzt. [<a target=\"_blank\" href=\"http://de.wikipedia.org/wiki/Tag_cloud\">Quelle</a>]\r\n<br /><br /><fieldset><legend>Beliebteste Tags</legend>{FORUM_TAG_HITLIST}</fieldset>\r\n<fieldset><legend>Tag Cloud</legend>{FORUM_TAG_CLOUD}</fieldset>','Tag Cloud','cloud','y',50,'on','system',197);
INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`) VALUES (53,20,'<div id=\"forum\">\r\n	<h2>Nach Aufrufe</h2>\r\n	<div class=\"div_board\">\r\n		<ol>\r\n		<!-- BEGIN mostViewed -->\r\n		<li>\r\n			<a href=\"{FORUM_MOST_VIEWED_LINK}\" title=\"{FORUM_MOST_VIEWED_SUBJECT}\">\r\n				{FORUM_MOST_VIEWED_SUBJECT} ({FORUM_MOST_VIEWED_VIEWS} mal aufgerufen)\r\n			</a><br />\r\n			Stichworte: {FORUM_MOST_VIEWED_KEYWORDS}\r\n			Inhalt: {FORUM_MOST_VIEWED_CONTENT}\r\n		</li>\r\n		<!-- END mostViewed -->\r\n		<ol>\r\n	</div>\r\n	<h2>Nach Bewertung</h2>\r\n	<div class=\"div_board\">\r\n		<ol>\r\n		<!-- BEGIN topRated -->\r\n		<li>\r\n		<a href=\"{FORUM_TOP_RATED_LINK}\" title=\"{FORUM_SUBJECT}\">\r\n			{FORUM_TOP_RATED_SUBJECT} (Bewertung: {FORUM_TOP_RATED_RATING})\r\n		</a><br />\r\n		Stichworte: {FORUM_TOP_RATED_KEYWORDS}\r\n		Inhalt: {FORUM_TOP_RATED_CONTENT}\r\n		</li>\r\n		<!-- END topRated -->\r\n		</ol>\r\n	</div>\r\n</div>','Populärste Diskussionen','toplist','y',50,'on','system',199);
INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`) VALUES (54,20,'<div id=\"forum\">\r\n	<div class=\"text\">\r\n		Hier kann in allen Beiträgen nach Stichworten gesucht werden. <br />\r\n		Wenn Sie auch den Inhalt der Diskussionen anstatt nur deren Stichworte durchsuchen wollen,\r\n		aktivieren Sie die das Kästchen neben dem Suchfeld. <br /> <br />\r\n	</div>\r\n	<!-- BEGIN forumError -->\r\n		<div class=\"message_error\">{FORUM_ERROR}</div>\r\n	<!-- END forumError -->\r\n	<div class=\"searchform\">\r\n		<form action=\"index.php\" method=\"get\">\r\n			<input type=\"hidden\" value=\"forum\" name=\"section\">\r\n			<input type=\"hidden\" value=\"searchTags\" name=\"cmd\">\r\n			<input style=\"width: 250px;\" type=\"text\" name=\"term\" value=\"{FORUM_SEARCH_TERM}\">  <input type=\"submit\" name=\"search\" value=\"Suchen\" /><br /><br />\r\n			<input type=\"checkbox\" value=\"1\" id=\"srchContent\" name=\"searchContent\" {FORUM_SEARCH_CONTENT_CHECKED} /><label for=\"srchContent\">Inhalt auch durchsuchen</label>\r\n		</form>\r\n	</div>\r\n	<div class=\"threads\">\r\n		<ul>\r\n			<!-- BEGIN threadList -->\r\n				<li style=\"list-item: none;\"><a title=\"{FORUM_THREAD_SUBJECT}\" href=\"{FORUM_THREAD_LINK}\"> {FORUM_THREAD_SUBJECT} </a><br />\r\n				Stichworte: {FORUM_THREAD_KEYWORDS} <br /> \r\n				Inhalt: {FORUM_THREAD_CONTENT}<br /><br /></li>\r\n			<!-- END threadList -->\r\n		</ul>\r\n	</div>\r\n</div>','Suche nach Tags','searchTags','y',50,'on','system',201);
INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`) VALUES (55,20,'{FORUM_JAVASCRIPT_GOTO}\r\n{FORUM_JAVASCRIPT_DELETE} \r\n{FORUM_JAVASCRIPT_SCROLLTO}\r\n{FORUM_JAVASCRIPT_INSERT_TEXT}\r\n<div id=\"forum\">\r\n    <!-- BEGIN threadDisplay -->\r\n    <!-- <div class=\"links\">{FORUM_COMMUNITY_LINKS}</div> -->\r\n    <!-- <div class=\"dropdown\">{FORUM_DROPDOWN}</div> -->\r\n    <div class=\"navtree\">\r\n        {FORUM_TREE}{FORUM_NAME}\r\n        <span style=\"color: rgb(255, 0, 0);\">{TXT_FORUM_ERROR}&nbsp;</span>\r\n        <span style=\"color: #006900;\">{TXT_FORUM_SUCCESS}&nbsp;</span>\r\n    </div>\r\n    <!-- BEGIN addPostAnchor -->\r\n    <div class=\"anchor\" style=\"float: left; width: 45%; clear: left;\">\r\n        <input onclick=\"location.href=\'#post\';\" value=\"{TXT_FORUM_CREATE_POST}\" type=\"button\" />\r\n    </div>                \r\n    <!-- END addPostAnchor -->\r\n    <!-- BEGIN threadActionsSelect -->\r\n    <div style=\"text-align:right; float: right; width: 45%; margin-bottom: 4px;\">\r\n        <select onchange=\"doAction(this.options[this.options.selectedIndex].value);\" name=\"thread_actions\">\r\n             <option value=\"\">{TXT_FORUM_THREAD_ACTION_DESC}</option>\r\n             <option value=\"sticky\" {FORUM_THREAD_ACTIONS_DISABLED_STICKY}>{TXT_FORUM_THREAD_ACTION_STICKY}</option>\r\n             <option value=\"move\" {FORUM_THREAD_ACTIONS_DISABLED_MOVE}>{TXT_FORUM_THREAD_ACTION_MOVE}</option>\r\n             <option value=\"close\" {FORUM_THREAD_ACTIONS_DISABLED_CLOSE}>{TXT_FORUM_THREAD_ACTION_CLOSE}</option>\r\n             <option value=\"delete\" {FORUM_THREAD_ACTIONS_DISABLED_DELETE}>{TXT_FORUM_THREAD_ACTION_DELETE}</option>\r\n        </select>\r\n    </div>\r\n    <!-- END threadActionsSelect -->\r\n    <div class=\"posts\">\r\n        <table cellspacing=\"0\" cellpadding=\"0\" border=\"0\">\r\n            <tbody>\r\n                <!-- BEGIN forumPosts -->\r\n                <tr class=\"row{FORUM_POST_ROWCLASS}\">\r\n                    <th width=\"99%\"><span style=\"padding-right: 60px;\">{FORUM_USER_NAME}, {FORUM_POST_DATE} </span>\r\n                        <span class=\"rating\"> {TXT_FORUM_RATING}:     {FORUM_POST_RATING}   </span>\r\n                            <!-- BEGIN rating -->\r\n                            <span id=\"forum_rating_{FORUM_RATING_POST_ID}\">\r\n                                <a title=\"+1\" href=\"javascript:void(0);\" onclick=\"ratePost({FORUM_RATING_POST_ID}, 1);\" >\r\n                                [\r\n                                <span class=\"rate\">\r\n                                    +\r\n                                </span>\r\n                                ]\r\n                                </a>\r\n                                <a title=\"-1\" href=\"javascript:void(0);\" onclick=\"ratePost({FORUM_RATING_POST_ID}, -1);\">\r\n                                [\r\n                                <span class=\"rate\">\r\n                                    -\r\n                                </span>\r\n                                ]\r\n                                </a>\r\n                            </span>\r\n                        <!-- END rating -->\r\n                    </th>\r\n                    <th width=\"1%\">{FORUM_POST_NUMBER}</th>\r\n                </tr>\r\n                <tr class=\"row{FORUM_POST_ROWCLASS}\">\r\n                    <td colspan=\"2\"><h3>{FORUM_POST_SUBJECT} {FORUM_POST_ICON}</h3>{FORUM_POST_MESSAGE}  </td>\r\n                </tr>\r\n                <!-- BEGIN attachment -->\r\n                <tr class=\"row_{FORUM_POST_ROWCLASS}\">\r\n                    <td colspan=\"2\">\r\n                        {TXT_FORUM_FILE_ATTACHMENT}:\r\n                        <a href=\"{FORUM_POST_ATTACHMENT_LINK}\">\r\n                            <img alt=\"{FORUM_POST_ATTACHMENT_FILENAME}\" src=\"{FORUM_POST_ATTACHMENT_ICON}\" />\r\n                            {FORUM_POST_ATTACHMENT_FILENAME} ({FORUM_POST_ATTACHMENT_FILESIZE} Bytes)\r\n                        </a>\r\n                    </td>\r\n                </tr>\r\n                    <!-- END attachment -->\r\n                <tr class=\"row{FORUM_POST_ROWCLASS}\">\r\n                    <td colspan=\"2\" align=\"right\">\r\n                         <!-- BEGIN postEdit --><input onclick=\"location.href=\'?section=forum&amp;cmd=thread&amp;id={FORUM_THREAD_ID}&amp;act=edit&amp;postid={FORUM_POST_ID}\';\" value=\"{TXT_FORUM_EDIT}\" type=\"button\" />&nbsp;<!-- END postEdit -->\r\n                          <!-- BEGIN postQuote --><input onclick=\"{FORUM_QUOTE_ONCLICK}\" value=\"{TXT_FORUM_QUOTE}\" type=\"button\" />&nbsp;<!-- END postQuote -->               \r\n                          <!-- BEGIN postDelete --><input onclick=\"deletePost(\'{FORUM_THREAD_ID}\', \'{FORUM_POST_ID}\'); return false;\" value=\"{TXT_FORUM_DELETE}\" type=\"button\" />&nbsp;<!-- END postDelete --> \r\n                    </td>\r\n                </tr>\r\n                <!-- END forumPosts -->\r\n            </tbody>\r\n        </table>\r\n    </div>\r\n    <div class=\"paging\">\r\n        {FORUM_POSTS_PAGING}\r\n    </div>\r\n    <!-- BEGIN addThread --><!-- END addThread -->\r\n        {FORUM_POST_REPLY_REDIRECT}\r\n    <!-- BEGIN addPost -->\r\n    <div class=\"addpost\">\r\n        <a name=\"post\"></a>\r\n        <fieldset> \r\n        <legend>{TXT_FORUM_ADD_POST}</legend>\r\n            <form enctype=\"multipart/form-data\" name=\"frm_thread\" action=\"?section=forum&amp;cmd=thread&amp;id={FORUM_THREAD_ID}\" method=\"post\" onsubmit=\"setScrollPos();\">\r\n                <input type=\"hidden\" name=\"post_edit_user_id\" value=\"{FORUM_POST_EDIT_USERID}\" />     \r\n                <input type=\"hidden\" name=\"post_id\" value=\"{FORUM_EDIT_POST_ID}\" />   \r\n                <input type=\"hidden\" name=\"thread_id\" value=\"{FORUM_THREAD_ID}\" />    \r\n                <input type=\"hidden\" name=\"category_id\" value=\"{FORUM_CATEGORY_ID}\" />    \r\n                <input type=\"hidden\" name=\"pos\" value=\"{FORUM_PAGING_POS}\" />\r\n                <!-- <p>\r\n                    <label>{TXT_FORUM_ICON}</label> -->\r\n                    <!-- BEGIN icons -->\r\n                    <!-- <input type=\"radio\" id=\"icons_{FORUM_ICON_VALUE}\" name=\"icons\" value=\"{FORUM_ICON_VALUE}\" /><label class=\"description\" for=\"icons_{FORUM_ICON_VALUE}\"><img src=\"{FORUM_ICON_SRC}\" alt=\"{FORUM_ICON_ALT}\" title=\"{FORUM_ICON_TITLE}\" /></label><br /> -->\r\n                    <!-- END icons -->\r\n                <!-- </p> -->\r\n                <p><label for=\"thread_subject\">{TXT_FORUM_SUBJECT}</label><input type=\"text\" name=\"subject\" id=\"thread_subject\" value=\"{FORUM_SUBJECT}\" /></p>\r\n                <p><label for=\"keywords\">{TXT_FORUM_KEYWORDS}</label><input onfocus=\"showToolTip(\'{TXT_FORUM_COMMA_SEPARATED_KEYWORDS}\', this, \'forumKeywordsHelp\')\" onblur=\"hideToolTip(\'forumKeywordsHelp\')\" title=\"{TXT_FORUM_COMMA_SEPARATED_KEYWORDS}\"  type=\"text\" name=\"keywords\" id=\"keywords\" value=\"{FORUM_KEYWORDS}\" /></p>\r\n                <p><label for=\"forum_attachment\">{TXT_FORUM_FILE_ATTACHMENT}</label><input class=\"inputtext\" type=\"file\" value=\"{FORUM_ATTACHMENT_VALUE}\" name=\"forum_attachment\" id=\"forum_attachment\" /></p>\r\n                    <!-- BEGIN delAttachment -->\r\n                            <br /><input type=\"checkbox\" id=\"delAttachment\" name=\"forum_delete_attachment\" value=\"1\" />\r\n                            <label for=\"delAttachment\">{TXT_FORUM_DELETE_ATTACHMENT}</label>\r\n                            <input type=\"hidden\" name=\"forum_attachment_oldname\" value=\"{FORUM_ATTACHMENT_OLDNAME}\" />\r\n                    <!-- END delAttachment -->\r\n                <p><a href=\"javascript:void(0);\" onclick=\"showAllowedExtensions();\">erlaubte Dateiendungen</a></p>\r\n                <p><label for=\"message\">{TXT_FORUM_MESSAGE}<br />\r\n                                    <a style=\"font-size:0.9em;\" href=\"http://de.wikipedia.org/wiki/BBCode\" title=\"BBCode\">Über BB-Code</a></label>{FORUM_MESSAGE_INPUT}</p>\r\n                <!-- BEGIN captcha -->\r\n                <p>\r\n                    <label for=\"captcha\">{TXT_FORUM_CAPTCHA}</label>\r\n                    {FORUM_CAPTCHA_CODE}\r\n                </p>\r\n                <!-- END captcha -->\r\n                <!-- BEGIN notificationRow -->\r\n                    <p><input id=\"forum_notify\" type=\"checkbox\" name=\"notification\" value=\"notify\" {FORUM_NOTIFICATION_CHECKBOX_CHECKED} /><label class=\"description\" for=\"forum_notify\">{TXT_FORUM_NOTIFY_NEW_POSTS}</label></p>\r\n                <!-- END notificationRow -->\r\n                <p>\r\n                    <input type=\"reset\" value=\"{TXT_FORUM_RESET}\" />&nbsp;\r\n                    <!-- BEGIN createPost --><input type=\"submit\" name=\"create\" value=\"{TXT_FORUM_CREATE_POST}\" />&nbsp;<!-- END createPost -->                \r\n                    <!-- BEGIN updatePost --><input type=\"submit\" name=\"update\" value=\"{TXT_FORUM_UPDATE_POST}\" />&nbsp;<!-- END updatePost -->                  \r\n                    <!-- BEGIN previewNewPost --><input type=\"submit\" name=\"preview_new\" value=\"{TXT_FORUM_PREVIEW}\" />&nbsp;<!-- END previewNewPost -->                 \r\n                    <!-- BEGIN previewEditPost --><input type=\"submit\" name=\"preview_edit\" value=\"{TXT_FORUM_PREVIEW}\" />&nbsp;<!-- END previewEditPost -->   \r\n                </p>\r\n            </form>\r\n        </fieldset> \r\n    </div>\r\n    <!-- END addPost -->\r\n    <!-- END threadDisplay -->\r\n    <!-- BEGIN threadActions -->\r\n      <br /><span style=\"color: rgb(255, 0, 0);\">{TXT_THREAD_ACTION_ERROR}&nbsp;</span>\r\n      <br /><span style=\"color: #006900;\">{TXT_THREAD_ACTION_SUCCESS}&nbsp;</span>\r\n      <!-- BEGIN moveForm -->\r\n      <form action=\"index.php?section=forum&amp;cmd=thread&amp;action=move&amp;id={FORUM_THREAD_ID}\" method=\"POST\" name=\"frmThreadMove\">\r\n      <select name=\"moveToThread\" size=\"32\" style=\"width:225px;\">\r\n        {FORUM_THREADS}\r\n      </select>\r\n      <br />\r\n      <input type=\"submit\" value=\"{TXT_FORUM_THREAD_ACTION_MOVE}\" />\r\n      </form>\r\n      <!-- END moveForm -->\r\n    <!-- END threadActions -->\r\n</div>\r\n','Thread anzeigen','thread','y',50,'','system',203);
