tr> \r\n            <!-- END mediadirFormList -->   \r\n        </table>\r\n        <!-- END mediadirForms -->\r\n    \r\n        <!-- BEGIN mediadirInputfields -->\r\n            <!-- BEGIN mediadirEntryAddStepNavigation -->\r\n            <!-- BEGIN mediadirEntryAddStepNavigationElement -->\r\n            <input type=\"button\" value=\"{MEDIADIR_ENTRY_ADDSTEP_NAME}\" id=\"{MEDIADIR_ENTRY_ADDSTEP_ID}\" onclick=\"{MEDIADIR_ENTRY_ADDSTEP_HREF}\" class=\"{MEDIADIR_ENTRY_ADDSTEP_CLASS}\" />\r\n            <!-- END mediadirEntryAddStepNavigationElement -->\r\n            <!-- END mediadirEntryAddStepNavigation -->\r\n\r\n            <input type=\"hidden\" value=\"{MEDIADIR_ENTRY_ID}\" name=\"entryId\" />\r\n            <input type=\"hidden\" value=\"{MEDIADIR_FORM_ID}\" name=\"formId\" />\r\n        \r\n            <!-- BEGIN mediadirInputfieldElement -->   \r\n    \r\n                <!-- BEGIN mediadirInputfieldAddStep -->  \r\n                {MEDIADIR_INPUTFIELD_ADDSTEP}\r\n                <!-- END mediadirInputfieldAddStep -->\r\n    \r\n                <!-- BEGIN mediadirInputfieldList -->   \r\n                <p><label>{TXT_MEDIADIR_INPUTFIELD_NAME}</label>{MEDIADIR_INPUTFIELD_FIELD}</p>\r\n                <!-- END mediadirInputfieldList -->\r\n        \r\n                <!-- BEGIN mediadirTranslationStatus -->\r\n                <p>\r\n                <label>{TXT_MEDIADIR_TRANSLATION_STATUS}</label>\r\n                <span id=\"mediadirInputfield_TranslationStatus_list\" style=\"display: block;\">\r\n                      <!-- BEGIN mediadirTranslationLangList -->\r\n                      <input class=\"marketplaceInputfieldCheckbox\" name=\"translationStatus[]\" id=\"mediadirInputfield_TranslationStatus\" value=\"{MEDIADIR_TRANSLATION_LANG_ID}\" type=\"checkbox\" {MEDIADIR_TRANSLATION_LANG_STATUS} />&nbsp;{TXT_MEDIADIR_TRANSLATION_LANG_NAME}<br>\r\n                      <!-- END mediadirTranslationLangList -->\r\n                </span>\r\n                </p>\r\n                <!-- END mediadirTranslationStatus -->\r\n        \r\n            <!-- END mediadirInputfieldElement -->\r\n            \r\n            {MEDIADIR_READY_TO_CONFIRM}\r\n            <p><input type=\"submit\" value=\"{TXT_MEDIADIR_SUBMIT}\" name=\"submitEntryModfyForm\" /></p>\r\n            <div style=\"display: none; color: rgb(255, 0, 0);\" id=\"mediadirErrorMessage\"><p>{TXT_MEDIADIR_PLEASE_CHECK_INPUT}</p></div>\r\n\r\n            {MEDIADIR_INPUTFIELD_ADDSTEP_TERMINATOR}\r\n        <!-- END mediadirInputfields -->\r\n    </form>\r\n    <!-- END mediadirEntryModifyForm -->\r\n</div>','Bearbeiten','edit','y',0,'','system',233);
INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`) VALUES (147,62,'<div id=\"checkout\">\r\n	{CHECKOUT_MSG_OK} {CHECKOUT_MSG_ERROR} <!-- BEGIN redirect -->{CHECKOUT_YELLOWPAY_FORM} <!-- END redirect --><!-- BEGIN form -->\r\n	<table>\r\n		<tbody>\r\n			<tr>\r\n				<td class=\"left\">\r\n					<form action=\"index.php?section=checkout\" method=\"post\">\r\n						<fieldset>\r\n							<legend>{TXT_CHECKOUT_BILL_DATA}</legend>\r\n							<p>\r\n								<input class=\"{CHECKOUT_INVOICE_NUMBER_CLASS}\" id=\"invoice_number\" name=\"invoice_number\" type=\"text\" value=\"{CHECKOUT_INVOICE_NUMBER}\" /></p>\r\n							<p>\r\n								<input class=\"{CHECKOUT_INVOICE_AMOUNT_CLASS}\" id=\"invoice_amount\" name=\"invoice_amount\" type=\"text\" value=\"{CHECKOUT_INVOICE_AMOUNT}\" /> <select class=\"{CHECKOUT_INVOICE_CURRENCY_CLASS}\" id=\"invoice_currency\" name=\"invoice_currency\">{CHECKOUT_INVOICE_CURRENCY_OPTIONS}</select></p>\r\n						</fieldset>\r\n						<fieldset>\r\n							<legend>{TXT_CHECKOUT_CONTACT_DATA}</legend>\r\n							<p>\r\n								<select class=\"{CHECKOUT_CONTACT_TITLE_CLASS}\" id=\"contact_title\" name=\"contact_title\">{CHECKOUT_CONTACT_TITLE_OPTIONS}</select></p>\r\n							<p>\r\n								<input class=\"{CHECKOUT_CONTACT_FORENAME_CLASS}\" id=\"contact_forename\" name=\"contact_forename\" type=\"text\" value=\"{CHECKOUT_CONTACT_FORENAME}\" /></p>\r\n							<p>\r\n								<input class=\"{CHECKOUT_CONTACT_SURNAME_CLASS}\" id=\"contact_surname\" name=\"contact_surname\" type=\"text\" value=\"{CHECKOUT_CONTACT_SURNAME}\" /></p>\r\n							<p>\r\n								<input class=\"{CHECKOUT_CONTACT_COMPANY_CLASS}\" id=\"contact_company\" name=\"contact_company\" type=\"text\" value=\"{CHECKOUT_CONTACT_COMPANY}\" /></p>\r\n							<p>\r\n								<input class=\"{CHECKOUT_CONTACT_STREET_CLASS}\" id=\"contact_street\" name=\"contact_street\" type=\"text\" value=\"{CHECKOUT_CONTACT_STREET}\" /></p>\r\n							<p>\r\n								<input class=\"{CHECKOUT_CONTACT_POSTCODE_CLASS}\" id=\"contact_postcode\" name=\"contact_postcode\" type=\"text\" value=\"{CHECKOUT_CONTACT_POSTCODE}\" /> <input class=\"{CHECKOUT_CONTACT_PLACE_CLASS}\" id=\"contact_place\" name=\"contact_place\" type=\"text\" value=\"{CHECKOUT_CONTACT_PLACE}\" /></p>\r\n							<p>\r\n								<select class=\"{CHECKOUT_CONTACT_COUNTRY_CLASS}\" id=\"contact_country\" name=\"contact_country\">{CHECKOUT_CONTACT_COUNTRY_OPTIONS}</select></p>\r\n							<p>\r\n								<input class=\"{CHECKOUT_CONTACT_PHONE_CLASS}\" id=\"contact_phone\" name=\"contact_phone\" type=\"text\" value=\"{CHECKOUT_CONTACT_PHONE}\" /></p>\r\n							<p>\r\n								<input class=\"{CHECKOUT_CONTACT_EMAIL_CLASS}\" id=\"contact_email\" name=\"contact_email\" type=\"text\" value=\"{CHECKOUT_CONTACT_EMAIL}\" /></p>\r\n						</fieldset>\r\n						<div id=\"buttons\">\r\n							<input name=\"submit\" type=\"submit\" value=\"{TXT_CORE_SUBMIT}\" /> <input name=\"reset\" type=\"reset\" value=\"{TXT_CORE_RESET}\" /></div>\r\n					</form>\r\n				</td>\r\n				<td class=\"right\">\r\n					<div id=\"payment_methods\">\r\n						<img alt=\"Postfinance Card\" src=\"modules/checkout/images/payment/postfinance_card.png\" /> <img alt=\"Postfinance E-Finance\" src=\"modules/checkout/images/payment/postfinance_efinance.png\" /> <img alt=\"Visa\" src=\"modules/checkout/images/payment/visa.png\" /><br />\r\n						<img alt=\"Mastercard\" src=\"modules/checkout/images/payment/mastercard.png\" /> <img alt=\"American Express\" src=\"modules/checkout/images/payment/american_express.png\" /> <img alt=\"Diners Club\" src=\"modules/checkout/images/payment/diners_club.png\" /><br />\r\n						<img alt=\"JCB\" src=\"modules/checkout/images/payment/jcb.png\" /> <img alt=\"PayPal\" src=\"modules/checkout/images/payment/pay_pal.png\" /></div>\r\n					<div id=\"description\">\r\n						{TXT_CHECKOUT_DESCRIPTION}</div>\r\n				</td>\r\n			</tr>\r\n		</tbody>\r\n	</table