INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`) VALUES (148,68,'{UPLOAD_WIDGET_CODE}\r\n{UPLOADER_CODE}\r\n{EXTENDED_FILE_INPUT_CODE}\r\n\r\n<script type=\"text/javascript\">\r\n    $J(document).ready(function () {\r\n        $J(\'input[name=accept_terms]\').click(function () {\r\n            if ($J(\'input[name=accept_terms]\').is(\':checked\')) {\r\n                $J(\'input[type=submit]\').removeAttr(\'disabled\');\r\n            } else {\r\n                $J(\'input[type=submit]\').attr(\'disabled\', true);\r\n            }\r\n        });\r\n    });\r\n</script>\r\n\r\n<div id=\"fileshare\">\r\n    <!-- BEGIN error_file_not_found -->\r\n    <span class=\"error\">{FILESHARING_ERROR_FILE_NOT_FOUND}</span>\r\n    <!-- END error_file_not_found -->\r\n    <!-- BEGIN error_no_files_uploaded -->\r\n    <span class=\"error\">{FILESHARING_ERROR_NO_FILES_UPLOADED}</span>\r\n    <!-- END error_no_files_uploaded -->\r\n    {TXT_FILESHARING_EXPLANATION}<br />\r\n    <!-- BEGIN uploaded -->\r\n    <table border=\"0\" cellpadding=\"0\" cellspacing=\"0\" width=\"100%\">\r\n        <tbody>\r\n            <!-- BEGIN filesharing_file -->\r\n            <tr>\r\n                <td rowspan=\"3\" width=\"120\">\r\n                    <!-- BEGIN image -->\r\n                    <img src=\"{FILE_IMAGE_SRC}\" width=\"100\" />\r\n                    <!-- END image -->\r\n                </td>\r\n                <td width=\"100\">\r\n                    <b>{FILESHARING_FILE_NAME}</b>\r\n                </td>\r\n                <td>\r\n                    {FILE_NAME}\r\n                </td>\r\n            </tr>\r\n            <tr>\r\n                <td>\r\n                    <b>{FILESHARING_DOWNLOAD_LINK}</b>\r\n                </td>\r\n                <td>\r\n                    <a href=\"{FILE_DOWNLOAD_LINK_HREF}\" target=\"_blank\">{FILE_DOWNLOAD_LINK_HREF}</a>\r\n                </td>\r\n            </tr>\r\n            <tr>\r\n                <td>\r\n                    <b>{FILESHARING_DELETE_LINK}</b>\r\n                </td>\r\n                <td>\r\n                    <a href=\"{FILE_DELETE_LINK_HREF}\" target=\"_blank\">{FILE_DELETE_LINK_HREF}</a>\r\n                </td>\r\n            </tr>\r\n            <!-- END filesharing_file -->\r\n        </tbody>\r\n    </table>\r\n    <br />\r\n    <br />\r\n    <a href=\"{NEW_UPLOAD_HREF}\">{NEW_UPLOAD}</a>\r\n    <!-- END uploaded -->\r\n    <!-- BEGIN upload_form -->\r\n    <form action=\"{FORM_ACTION}\" class=\"fileshareForm\" id=\"contactForm\" method=\"{FORM_METHOD}\" style=\"float: left;\">\r\n        <p>\r\n            <label>{TXT_FILESHARING_FILES}</label>\r\n            <input disabled=\"disabled\" id=\"file_upload\" type=\"file\" />\r\n        </p>\r\n        <div id=\"uploadFormField_uploadWidget\" class=\"formUploadedFiles\"></div>\r\n        <p>\r\n            <label for=\"FILESHARING_FORM_EMAIL\">{FILESHARING_EMAIL}</label> <input id=\"FILESHARING_FORM_EMAIL\" name=\"email\" placeholder=\"{FILESHARING_EMAIL_INFO}\" type=\"text\" />\r\n        </p>\r\n        <p>\r\n            <label for=\"FILESHARING_FORM_SUBJECT\">{FILESHARING_SUBJECT}</label><input id=\"FILESHARING_FORM_SUBJECT\" name=\"subject\" placeholder=\"{FILESHARING_SUBJECT_INFO}\" type=\"text\" />\r\n        </p>\r\n        <p>\r\n            <a class=\"toggle\" href=\"#\">{FILESHARING_MORE}</a>\r\n        </p>\r\n        <div class=\"toggle\" style=\"display: none;\">\r\n            <p>\r\n                <label for=\"FILESHARING_FORM_MESSAGE\">{FILESHARING_MESSAGE}</label><textarea id=\"FILESHARING_FORM_MESSAGE\" name=\"message\" placeholder=\"{FILESHARING_MESSAGE_INFO}\"></textarea>\r\n            </p>\r\n            <p>\r\n                <label for=\"FILESHARING_FORM_EXPIRATION\">{FILESHARING_EXPIRATION}</label> \r\n                <select id=\"FILESHARING_FORM_EXPIRATION\" name=\"expiration\">\r\n                    <option value=\"86400\">{1D}</option>\r\n                    <option selected=\"selected\" value=\"864000\">{10D}</option>\r\n                    <option value=\"1728000\">{20D}</option>\r\n                    <option value=\"2592000\">{30D}</option>\r\n                </select>\r\n            </p>\r\n        </div>\r\n        <p>\r\n            <input id=\"FILESHARING_ACCEPT_TERMS\" name=\"accept_terms\" type=\"checkbox\" value=\"1\" />{TXT_FILESHARING_I_AGREE} <a href=\"index.php?section=agb\">{TXT_FILESHARING_TERMS_OF_SERVICE}</a> {TXT_FILESHARING_I_ACCEPT}\r\n        </p>\r\n        <p>\r\n            <input disabled=\"disabled\" type=\"submit\" value=\"{FILESHARING_SEND}\" />\r\n        </p>\r\n    </form>\r\n    <!-- END upload_form -->\r\n    <!-- BEGIN confirm_delete -->\r\n    <form action=\"{FORM_ACTION}\" class=\"fileshareForm\" id=\"contactForm\" method=\"{FORM_METHOD}\" style=\"float: left;\">\r\n        <p>\r\n            <label>{TXT_FILESHARING_FILE_NAME}</label>{FILESHARING_FILE_NAME}\r\n        </p>\r\n        <p>\r\n            <input name=\"delete\" type=\"submit\" value=\"{TXT_FILESHARING_CONFIRM_DELETE}\" />\r\n        </p>\r\n    </form>\r\n    <!-- END confirm_delete -->\r\n    <!-- BEGIN no_access -->\r\n    {FILESHARING_NO_ACCESS}\r\n    <!-- END no_access -->\r\n</div>','Filesharing','','y',0,'on','system',43);
