INSERT INTO `contrexx_content` (`id`, `content`, `title`, `metatitle`, `metadesc`, `metakeys`, `metarobots`, `css_name`, `redirect`, `expertmode`) VALUES (1,'Besten Dank f&uuml;r Ihren Entscheid, <a target=\"_blank\" href=\"http://www.contrexx.com\">Contrexx&reg;</a> als Grundlage f&uuml;r Ihr Webprojekt einzusetzen.\r\n<h2>Erfolgreiche Installation von Contrexx&reg;</h2>\r\n<p><em>Contrexx&reg; Web Content Management System Version 2.0 ist </em>erfolgreich installiert worden. Nun ist <a target=\"_blank\" href=\"http://www.contrexx.com\">Contrexx&reg;</a> einsatzbereit. Sie  k&ouml;nnen jetzt die Inhalte erstellen und Ihr WebDesign integrieren. Mit der Installation von Contrexx&reg; akzeptieren Sie unsere <a href=\"http://www.contrexx.com/de/index.php?page=1132\" target=\"_blank\">Lizenzbedingungen</a>.</p>\r\n<h2>Lassen Sie Ihre Seite finden</h2>\r\nBereits w&auml;hrend der Erstellung des Internetauftrittes k&ouml;nnen Sie auf einige Punkte f&uuml;r die <a target=\"_blank\" href=\"http://www.contrexx.com/wiki/de/index.php?title=Suchmaschinenoptimierung_%28SEO%29\">Suchmaschinenoptimierung achten</a>, dannach empfehlen wir Ihnen die Webseiten bei den entsprechenden Suchmaschinen anzumelden. Die Indexierung der Suchmaschinen k&ouml;nnen Sie in der Statistik von Contrexx mitverfolgen. F&uuml;r die ersten Tipps folgen Sie bitte diesem <a target=\"_blank\" href=\"http://www.contrexx.com/wiki/de/index.php?title=Suchmaschinenoptimierung_%28SEO%29\">Link</a>.<br />\r\n<h2>Laufend &uuml;ber Contrexx&reg; informiert</h2>\r\n<p>Wir sind &uuml;berzeugt, dass Contrexx&reg; die richtige Wahl ist: Die Bedienung ist einfach, der <a href=\"index.php?page=2\">professionelle Support</a> unterst&uuml;tzt Sie auf Anfrage und Contrexx&reg; wird laufend weiter entwickelt. Informieren Sie sich regelm&auml;ssig unter <a target=\"_blank\" href=\"http://www.contrexx.com\">www.contrexx.com</a> oder abonnieren Sie <a href=\"http://www.contrexx.com/feed/news_headlines_de.xml\" target=\"_blank\">unseren RSS-Feed</a>.</p>\r\n<h2>Referenz werden</h2>\r\n<a href=\"http://www.contrexx.com/de/index.php?section=directory&amp;cmd=add&amp;langId=1\" target=\"_blank\">Bewerben Sie sich nach Abschluss Ihres Projektes als Referenz bei Contrexx&reg;</a>, nach einer kurzen Pr&uuml;fung werden wir die Referenz freischalten. Damit erhalten Sie eine weitere Verlinkung auf Ihre Webseite und damit gewinnen Sie eine h&ouml;here Relevanz bei Suchmaschinen.<br />','Willkommen und Glückwunsch','Willkommen bei der erfolgreichen Installation von Contrexx® Web Content Management System Version 2.0','Willkommen bei der erfolgreichen Installation von Contrexx® Web Content Management System Version 2.0','Willkommen bei der erfolgreichen Installation von Contrexx® Web Content Management System Version 2.0','index','','','n');
INSERT INTO `contrexx_content` (`id`, `content`, `title`, `metatitle`, `metadesc`, `metakeys`, `metarobots`, `css_name`, `redirect`, `expertmode`) VALUES (2,'Die Erfahrung zeigt, dass Contrexx zuverl&auml;ssig funktioniert. Sollten Sie trotzdem Unterst&uuml;tzung ben&ouml;tigen, stehen wir Ihnen mit den folgenden Instrumenten sehr gerne zur Verf&uuml;gung.<br />\r\n<ul>\r\n    <li><a target=\"_blank\" href=\"http://www.contrexx.com/wiki/de/\">Dokumentation</a>: Das Dokumentationsportal f&uuml;r Contrexx Anwender und Entwickler.</li>\r\n    <li><a target=\"_blank\" href=\"http://www.contrexx.com/forum/forumdisplay.php?f=68\">Contrexx - Forum</a>: Weiterf&uuml;hrende Unterst&uuml;tzung erhalten Sie von der Contrexx Community im Open Source Forum.</li>\r\n    <li><a target=\"_blank\" href=\"http://www.contrexx.com/bugtracker/\">Bugtracker</a>: Hier wird eine aktuelle Liste der bekannten Fehler, sowie der geplanten/gew&uuml;nschten Verbesserungen und Erweiterungen der Contrexx Software gef&uuml;hrt.</li>\r\n    <li><a href=\"http://support.comvation.com/index.php\" target=\"_blank\">Support - Ticket f&uuml;r Contrexx - Kunden</a>: Contrexx - Kunden k&ouml;nnen unter diesem Link ein Support-Ticket er&ouml;ffnen. Die Anfrage wird in das Ticketing-System geleitet und damit wird gew&auml;hrleistet, dass Ihre Anfrage rasch und zuverl&auml;ssig beantwortet wird. Nutzer der kostenlosen Opensource Version stehen die obigen M&ouml;glichkeiten zur Verf&uuml;gung.</li>\r\n</ul>','Contrexx Support','Contrexx Support','Contrexx Support','Contrexx Support','index','','','n');
INSERT INTO `contrexx_content` (`id`, `content`, `title`, `metatitle`, `metadesc`, `metakeys`, `metarobots`, `css_name`, `redirect`, `expertmode`) VALUES (3,'<h2>Kommerzielle Software</h2>\r\nContrexx WCMS ist modular aufgebaut und in drei Grundpaketen zusammengefasst: Contrexx <a target=\"_blank\" href=\"http://www.contrexx.com/de/index.php?page=884\">Basic</a>, <a target=\"_blank\" href=\"http://www.contrexx.com/de/index.php?page=885\">Standard </a>und <a target=\"_blank\" href=\"http://www.contrexx.com/de/index.php?page=886\">Premium</a>.<br />\r\n<h2>Software f&uuml;r Schulen</h2>\r\nSpeziell f&uuml;r Schulen, Universit&auml;ten und Fachhochschulen steht das <a href=\"http://www.contrexx.com/de/index.php?section=shop&amp;cmd=details&amp;productId=88\" target=\"_blank\">Contrexx Education</a> bereit, welche zu einem tiefen Preis bezogen werden kann.<br />\r\n<h2>Contrexx Open Source</h2>\r\nVereine und private Personen <a href=\"http://www.contrexx.com/wiki/de/index.php?title=FAQ:Lizenz\" target=\"_blank\">ohne kommerzielle Ziele </a>k&ouml;nnen <a href=\"http://www.contrexx.com/de/index.php?page=1107\" target=\"_blank\">Contrexx Open Source</a> kostenlos <a href=\"http://www.contrexx.com/de/index.php?page=99\" target=\"_blank\">herunterladen und verwenden</a>.<br />\r\n<h2>Zus&auml;tzliche Module und Funktionalit&auml;ten</h2>\r\nVerschaffen Sie sich einen &Uuml;berblick von weiteren Funktionalit&auml;ten und Modulen, dazu m&uuml;ssen Sie nur diesem <a target=\"_blank\" href=\"http://www.contrexx.com/de/index.php?page=883\">Link folgen</a>. Sollte Ihnen ein Modul oder eine weitere Funktionalit&auml;t zus&auml;tzlichen Nutzen verschaffen, k&ouml;nnen Sie das <a target=\"_blank\" href=\"http://www.contrexx.com/de/index.php?page=892\">entsprechende Modul dazu bestellen</a> oder ein <a target=\"_blank\" href=\"http://www.contrexx.com/de/index.php?section=shop&amp;catId=122\">entsprechendes Upgrade</a> kaufen.<br />\r\n<br type=\"_moz\" />','Contrexx Module','Contrexx Module','Contrexx Module','Contrexx Module','index','','','n');
INSERT INTO `contrexx_content` (`id`, `content`, `title`, `metatitle`, `metadesc`, `metakeys`, `metarobots`, `css_name`, `redirect`, `expertmode`) VALUES (4,'<form name=\"formNews\" method=\"post\" action=\"index.php?section=news\">\r\n    {NEWS_CAT_DROPDOWNMENU}\r\n</form>\r\n<br />\r\n<table cellspacing=\"0\" id=\"news\">\r\n    <tbody>\r\n        <tr>\r\n            <th width=\"15%\" nowrap=\"nowrap\" class=\"newsback\"><strong>{TXT_DATE}</strong></th>\r\n            <th width=\"85%\" nowrap=\"nowrap\" class=\"newsback\"><strong>{TXT_NEWS_MESSAGE}</strong></th>\r\n        </tr>\r\n        <!-- BEGIN newsrow -->\r\n        <tr class=\"{NEWS_CSS}\">\r\n            <td nowrap=\"nowrap\">{NEWS_DATE}&nbsp;&nbsp;</td>\r\n            <td>{NEWS_LINK}</td>\r\n        </tr>\r\n        <!-- END newsrow -->\r\n    </tbody>\r\n</table>\r\n<br />\r\n{NEWS_PAGING}','News','News','News','News','index','','','y');
INSERT INTO `contrexx_content` (`id`, `content`, `title`, `metatitle`, `metadesc`, `metakeys`, `metarobots`, `css_name`, `redirect`, `expertmode`) VALUES (5,'{NEWS_TEXT}<br />\r\n{NEWS_SOURCE}<br />\r\n{NEWS_URL}<br />\r\nVer&ouml;ffentlicht am<br />\r\n{NEWS_DATE}<br />\r\n<br />\r\n<br />\r\n<div id=\"newsbox\">\r\n<div>\r\n<table cellspacing=\"0\">\r\n    <tbody>\r\n        <tr>\r\n            <th colspan=\"2\">Toolbox</th>\r\n        </tr>\r\n        <tr class=\"row1\">\r\n            <td width=\"1%\"><img width=\"16\" height=\"16\" alt=\"Print\" src=\"images/content/icons/printer-1.gif\" /></td>\r\n            <td width=\"99%\"><a href=\"{PRINT_URL}\">Drucken</a></td>\r\n        </tr>\r\n        <tr class=\"row2\">\r\n            <td><img width=\"16\" height=\"16\" alt=\"Acrobat\" src=\"images/content/icons/page_white_acrobat.gif\" /></td>\r\n            <td><a href=\"{PDF_URL}\">PDF</a></td>\r\n        </tr>\r\n        <tr class=\"row1\">\r\n            <td><img width=\"16\" height=\"16\" alt=\"Arrow Right\" src=\"images/content/icons/arrow_right.gif\" /></td>\r\n            <td><a href=\"index.php?section=recommend\">Weiterempfehlen</a></td>\r\n        </tr>\r\n        <tr class=\"row2\">\r\n            <td><img width=\"16\" height=\"16\" alt=\"RSS\" src=\"images/content/icons/rss.gif\" /></td>\r\n            <td><a href=\"feed/news_headlines_de.xml\">RSS Abonnieren</a></td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n</div>\r\n</div>','Newsmeldung','Newsmeldung','Newsmeldung','Newsmeldung','index','','','y');
INSERT INTO `contrexx_content` (`id`, `content`, `title`, `metatitle`, `metadesc`, `metakeys`, `metarobots`, `css_name`, `redirect`, `expertmode`) VALUES (6,'<font color=\"#ff0000\">{NEWS_STATUS_MESSAGE}&nbsp;</font> <!-- BEGIN news_submit_form -->\r\n<form method=\"post\" action=\"index.php?section=news&amp;cmd=submit\">\r\n    <h2>     {TXT_NEWS_MESSAGE}&nbsp;</h2>\r\n    <table width=\"100%\" cellspacing=\"0\" cellpadding=\"2\" border=\"0\">\r\n        <tbody>\r\n            <tr>\r\n                <td width=\"20%\">{TXT_TITLE}</td>\r\n                <td width=\"80%\"><input type=\"text\" maxlength=\"250\" value=\"{NEWS_TITLE}\" name=\"newsTitle\" style=\"width: 250px;\" /></td>\r\n            </tr>\r\n            <tr>\r\n                <td width=\"20%\">{TXT_CATEGORY}</td>\r\n                <td width=\"80%\"><select name=\"newsCat\" style=\"width: 250px;\"><!-- BEGIN news_category_menu -->\r\n                <option value=\"{NEWS_CATEGORY_ID}\">{NEWS_CATEGORY_TITLE}</option>\r\n                <!-- END news_category_menu --></select></td>\r\n            </tr>\r\n            <tr>\r\n                <td width=\"20%\">{TXT_EXTERNAL_SOURCE}</td>\r\n                <td width=\"80%\"><input type=\"text\" maxlength=\"250\" value=\"{NEWS_SOURCE}\" name=\"newsSource\" style=\"width: 250px;\" /></td>\r\n            </tr>\r\n            <tr>\r\n                <td width=\"20%\">{TXT_LINK} #1</td>\r\n                <td width=\"80%\"><input type=\"text\" maxlength=\"250\" value=\"{NEWS_URL1}\" name=\"newsUrl1\" style=\"width: 250px;\" /></td>\r\n            </tr>\r\n            <tr>\r\n                <td width=\"20%\">{TXT_LINK} #2</td>\r\n                <td width=\"80%\"><input type=\"text\" maxlength=\"250\" value=\"{NEWS_URL2}\" name=\"newsUrl2\" style=\"width: 250px;\" /></td>\r\n            </tr>\r\n        </tbody>\r\n    </table>\r\n    <br />\r\n    <h2>{TXT_NEWS_TEASER_TEXT}&nbsp;</h2>\r\n    <table width=\"100%\" cellspacing=\"0\" cellpadding=\"3\" border=\"0\">\r\n        <tbody>\r\n            <tr>\r\n                <td colspan=\"2\"><textarea name=\"newsTeaserText\" style=\"width: 100%;\" cols=\"30\" rows=\"5\">{NEWS_TEASER_TEXT}</textarea></td>\r\n            </tr>\r\n        </tbody>\r\n    </table>\r\n    <br />\r\n    <h2> {TXT_NEWS_NEWS_CONTENT}&nbsp;</h2>\r\n    <table width=\"100%\" cellspacing=\"0\" cellpadding=\"3\" border=\"0\">\r\n        <tbody>\r\n            <tr>\r\n                <td colspan=\"2\">{TXT_NEWS_REDIRECT}</td>\r\n            </tr>\r\n            <tr>\r\n                <td>{TXT_NEWS_NEWS_URL}</td>\r\n                <td><input type=\"text\" style=\"width: 250px;\" value=\"{NEWS_REDIRECT}\" name=\"newsRedirect\" /></td>\r\n            </tr>\r\n            <tr>\r\n                <td colspan=\"2\">{NEWS_TEXT}</td>\r\n            </tr>\r\n            <tr>\r\n                <td colspan=\"2\"><input type=\"reset\" onclick=\"FCKeditorAPI.GetInstance(\'newsText\').SetHTML(\'\')\" /> <input type=\"submit\" name=\"submitNews\" value=\"Anmelden\" /></td>\r\n            </tr>\r\n        </tbody>\r\n    </table>\r\n</form>\r\n<!-- END news_submit_form --> <!-- BEGIN news_submitted --> <a title=\"weitere Newsmeldung hinzuf&uuml;gen\" href=\"index.php?section=news&amp;cmd=submit\">Klicken Sie hier um eine weitere Newsmeldung zu erfassen.</a> <!-- END news_submitted -->','Newsanmelden','Newsanmelden','News anmelden','News anmelden','index','','','n');
INSERT INTO `contrexx_content` (`id`, `content`, `title`, `metatitle`, `metadesc`, `metakeys`, `metarobots`, `css_name`, `redirect`, `expertmode`) VALUES (7,'<h3>Online Einbindung mittels RSS-Newsfeed</h3>\n<p><a href=\"{NEWS_RSS_FEED_URL}\" title=\"RSS Newsfeed von {NEWS_HOSTNAME}\" class=\"rssfeed\"><img width=\"48\" height=\"74\" border=\"0\" style=\"margin: 5px 20px 20px; float: right;\" src=\"images/content/icons/rss_bg.gif\" alt=\"Der Feed dieser Website kann &uuml;ber diesen Link aufgerufen werden\" /></a>RSS ist ein elektronisches Nachrichtenformat, das dem Nutzer erm&ouml;glicht, die Inhalte einer Website &ndash; oder Teile davon &ndash; als sogenannte RSS-Feeds zu abonnieren oder in andere Websites zu integrieren.</p>\n<h3>Online Einbindung mittels Javascript</h3>\nDer Feed dieser Website kann auch ganz einfach auf Ihrer Website dargestellt werden. Dazu m&uuml;ssen Sie lediglich den folgenden Code in Ihre eigene Webseite einf&uuml;gen:<br />\n<br />\n<form action=\"\">\n    <textarea style=\"width: 98%; font-size: 95%;\" rows=\"18\" cols=\"30\" name=\"code\">{NEWS_RSS2JS_CODE}</textarea>     <br />\n    <br />\n    <input type=\"button\" value=\"Alles markieren\" onclick=\"javascript:this.form.code.focus();this.form.code.select();\" name=\"button\" />\n</form>\n','News Feed','News Feed','News Feed','News Feed','index','','','n');
INSERT INTO `contrexx_content` (`id`, `content`, `title`, `metatitle`, `metadesc`, `metakeys`, `metarobots`, `css_name`, `redirect`, `expertmode`) VALUES (1115,'<form action=\"index.php\" method=\"get\">\r\n	<input name=\"term\" value=\"{SEARCH_TERM}\" size=\"30\" maxlength=\"100\" class=\"searchinput\" />\r\n	<input value=\"search\" name=\"section\" type=\"hidden\" />\r\n	<input value=\"{TXT_SEARCH}\" name=\"Submit\" type=\"submit\" class=\"searchbutton\" />\r\n</form>\r\n<br />\r\n{SEARCH_TITLE}<br />\r\n<!-- BEGIN searchrow -->\r\n	{LINK} {COUNT_MATCH}<br />\r\n	{SHORT_CONTENT}<br />\r\n<!-- END searchrow -->\r\n<br />\r\n{SEARCH_PAGING}\r\n<br />\r\n<br />','Suchen','Suchen','Suchen','Suchen','index','','','y');
INSERT INTO `contrexx_content` (`id`, `content`, `title`, `metatitle`, `metadesc`, `metakeys`, `metarobots`, `css_name`, `redirect`, `expertmode`) VALUES (1116,'<div id=\"voting\">\r\n	<div class=\"form\">\r\n		<form name=\"VotingForm\" action=\"index.php?section=voting\" method=\"post\">\r\n			<h2>{VOTING_TITLE}</h2><em>gestartet am {VOTING_DATE}</em><br />\r\n			<div class=\"message\">{VOTING_MSG}</div>\r\n			<div class=\"votes\">\r\n                                {VOTING_RESULTS_TEXT}<br />\r\n			        {VOTING_RESULTS_TOTAL_VOTES}\r\n			</div>\r\n			<!-- BEGIN voting_email_input --> \r\n				Tragen Sie bitte Ihre E-Mail Adresse ein, damit Sie an der Umfrage teilnehmen k&ouml;nnen:<br /><br />\r\n				<p><label>E-Mail Adresse</label><input type=\"text\" name=\"votingemail\" value=\"{VOTING_EMAIL}\"/></p><br />\r\n		   	<!-- END voting_email_input -->\r\n			<br />\r\n                        <!-- BEGIN additional_fields -->\r\n                            Für diese Umfrage werden weitere Informationen von Ihnen benötigt:<br /><br />\r\n                            <!-- BEGIN additional_elements -->\r\n                            <p><label>{VOTING_ADDITIONAL_INPUT_LABEL}</label>{VOTING_ADDITIONAL_INPUT}</p>\r\n                            <!-- END additional_elements -->\r\n                        <!-- END additional_fields -->\r\n			<br />\r\n			{TXT_SUBMIT}\r\n		</form>\r\n	</div>\r\n	<div class=\"archive\">\r\n		<table width=\"100%\" border=\"0\">\r\n			<tbody>\r\n				<tr>\r\n					<th valign=\"top\" colspan=\"2\">{VOTING_OLDER_TITLE}</th>\r\n				</tr>\r\n				<!-- BEGIN votingRow -->\r\n				<tr class=\"{VOTING_LIST_CLASS}\">\r\n					<td nowrap=\"nowrap\">{VOTING_OLDER_DATE}&nbsp;</td>\r\n					<td nowrap=\"nowrap\">{VOTING_OLDER_TEXT}&nbsp;</td>\r\n				</tr>\r\n				<!-- END votingRow -->\r\n			</tbody>\r\n		</table>\r\n		<br />\r\n		{VOTING_PAGING}\r\n	</div>\r\n</div>','Umfragen','Umfragen','Umfragen','Umfragen','index','','','y');
INSERT INTO `contrexx_content` (`id`, `content`, `title`, `metatitle`, `metadesc`, `metakeys`, `metarobots`, `css_name`, `redirect`, `expertmode`) VALUES (1114,'<div id=\"recommend\">\r\n	<div class=\"status\">{RECOM_STATUS}</div>\r\n	<!-- BEGIN recommend_form -->\r\n	{RECOM_SCRIPT}\r\n	<div class=\"text\">{RECOM_TEXT}</div>\r\n	<div class=\"form\">\r\n		<form id=\"recommendForm\" name=\"recommend\" method=\"post\" action=\"index.php?section=recommend&amp;act=sendRecomm\">\r\n			<input type=\"hidden\" value=\"{RECOM_REFERER}\" name=\"uri\" /> \r\n			<input type=\"hidden\" value=\"{RECOM_FEMALE_SALUTATION_TEXT}\" name=\"female_salutation_text\" /> \r\n			<input type=\"hidden\" value=\"{RECOM_MALE_SALUTATION_TEXT}\" name=\"male_salutation_text\" /> \r\n			<input type=\"hidden\" value=\"{RECOM_PREVIEW}\" name=\"preview_text\" />\r\n			<p><label for=\"receivername\">{RECOM_TXT_RECEIVER_NAME}</label><input type=\"text\" onchange=\"update();\" value=\"{RECOM_RECEIVER_NAME}\" maxlength=\"100\" name=\"receivername\" /></p>\r\n			<p><label for=\"receivermail\">{RECOM_TXT_RECEIVER_MAIL}</label><input type=\"text\" onchange=\"update();\" value=\"{RECOM_RECEIVER_MAIL}\" maxlength=\"100\" name=\"receivermail\" /></p>\r\n			<p><label>{RECOM_TXT_GENDER}</label><input type=\"radio\" onclick=\"update();\" value=\"female\" name=\"gender\" id=\"female\" /><label class=\"description\" for=\"female\">{RECOM_TXT_FEMALE}</label></p>\r\n			<p><input type=\"radio\" onclick=\"update();\" value=\"male\" margin-left:=\"\" name=\"gender\" id=\"male\" /><label class=\"description\" for=\"male\">{RECOM_TXT_MALE}</label></p>\r\n			<p><label for=\"sendername\">{RECOM_TXT_SENDER_NAME}</label><input type=\"text\" onchange=\"update();\" value=\"{RECOM_SENDER_NAME}\" maxlength=\"100\" name=\"sendername\" /></p>\r\n			<p><label for=\"sendermail\">{RECOM_TXT_SENDER_MAIL}</label><input type=\"text\" onchange=\"update();\" value=\"{RECOM_SENDER_MAIL}\" maxlength=\"100\" name=\"sendermail\" /></p>\r\n			<p>