INSERT INTO `contrexx_content` (`id`, `content`, `title`, `metatitle`, `metadesc`, `metakeys`, `metarobots`, `css_name`, `redirect`, `expertmode`) VALUES (89,'Mit dem Layout &amp; Design Manager k&ouml;nnen Sie Ihre Webdesign Vorlagen verwalten. <br />\r\n<br />\r\nW&auml;hlen Sie in der Navigation die gew&uuml;nschte Webdesign Vorlage. Mit Contrexx haben Sie die M&ouml;glichkeit, f&uuml;r jede eigene Seite ein anderes Design zu bestimmen.<br />\r\n<h2>Vorschau</h2>\r\nDie gesamte Website kann mit dem URI Paramter&nbsp; preview=ThemeID [ Beispiel: index.php?preview=3 ]&nbsp; mit dem ausgew&auml;hlten Theme als Vorschau angesehen werden.<br />\r\n<h2>Theme Portal</h2>\r\nOffizielles <a href=\"http://webdesign.contrexx.com\">Contrexx Themeportal</a>&nbsp; (in Aufbau)<br />','Design Vorlagen','Design Vorlagen','Design Vorlagen','Design Vorlagen','index','','','n');
