L}\';\r\n              if($(\'copyPlace\') && $(\'copyPlace\').checked == true) {\r\n                  $(\'zip\').value = $(\'organizerzip\').value;\r\n                  $(\'street\').value = $(\'organizerstreet\').value;\r\n                  $(\'place\').value = $(\'organizerplace\').value;\r\n              }\r\n              \r\n          }\r\n        }\r\n        \r\n        function disableOrgaId() {\r\n        if($(\'organizerid\')) {\r\n            $(\'organizerid\').checked = false;\r\n        }\r\n        }\r\n        \r\n        function copyText(text, target) {\r\n          if($(\'copyPlace\') && $(\'copyPlace\').checked == true) {\r\n            $(target).value = text;\r\n          }\r\n        }\r\n        \r\n        function copyPlaceValues() {\r\n          if($(\'copyPlace\') && $(\'copyPlace\').checked == true) {\r\n              $(\'street\').value = $(\'organizerstreet\').value;\r\n              $(\'zip\').value = $(\'organizerzip\').value;\r\n              $(\'place\').value = $(\'organizerplace\').value;\r\n          }\r\n        }\r\n      \r\n\r\n        function showOrHide(strElement)\r\n        {\r\n        	seriesMastSelect	= document.getElementsByName(\'inputSeriesType\')[0].value;\r\n        \r\n        	if (strElement == \'seriesType\') {\r\n        		if(seriesMastSelect == 1){\r\n        			document.getElementById(\'seriesMaskDaily\').style.display = \'block\';\r\n        			document.getElementById(\'seriesMaskWeekly\').style.display = \'none\';\r\n        			document.getElementById(\'seriesMaskMonthly\').style.display = \'none\';\r\n        		} else if(seriesMastSelect == 2) {\r\n        			document.getElementById(\'seriesMaskDaily\').style.display = \'none\';\r\n        			document.getElementById(\'seriesMaskWeekly\').style.display = \'block\';\r\n        			document.getElementById(\'seriesMaskMonthly\').style.display = \'none\';\r\n        		} else if(seriesMastSelect == 3) {\r\n        			document.getElementById(\'seriesMaskDaily\').style.display = \'none\';\r\n        			document.getElementById(\'seriesMaskWeekly\').style.display = \'none\';\r\n        			document.getElementById(\'seriesMaskMonthly\').style.display = \'block\';\r\n        		} else {\r\n        			document.getElementById(\'seriesMaskDaily\').style.display = \'block\';\r\n        			document.getElementById(\'seriesMaskDaily\').style.display = \'block\';\r\n        			document.getElementById(\'seriesMaskWeekly\').style.display = \'none\';\r\n        			document.getElementById(\'seriesMaskMonthly\').style.display = \'none\';\r\n        		}\r\n        	}\r\n        	\r\n        	if(strElement == \'seriesBlock\') {\r\n        	    seriesBlockChecked    = $(\'inputSeriesStatus\').checked;\r\n        	    if(seriesBlockChecked == true) {\r\n        	        $(\'seriesBlock\').style.display = \'block\';\r\n        	    } else {\r\n        	        $(\'seriesBlock\').style.display = \'none\';\r\n        	    }\r\n        	}\r\n        }\r\n      \r\n      // -->\r\n      </script>\r\n      \r\n    <!-- BEGIN calError -->\r\n    <div class=\"errorbox\" id=\"calError\">\r\n        {ERROR}\r\n    </div>\r\n    <!-- END calError -->\r\n    <!-- BEGIN calOK -->\r\n    <div class=\"okbox\" id=\"calOK\">\r\n        {SUCCESS}\r\n    </div>\r\n    <!-- END calOK -->\r\n    <!-- BEGIN addeventForm -->      \r\n    <form action=\"index.php?section=calendar{MODULE_INDEX}&amp;cmd=addevent\" method=\"post\"  enctype=\"multipart/form-data\" id=\"newCalendarNote\" name=\"newCalendarNote\" >\r\n\r\n    <table class=\"addevent\" cellpadding=\"0\" cellspacing=\"0\" width=\"100%\" border=\"0\">\r\n        <tr>\r\n          <th colspan=\"2\" nowrap=\"nowrap\">\r\n            <div align=\"left\">{TXT_CALENDAR_NEW}</div>\r\n          </th>\r\n        </tr>\r\n        <tr class=\"row1\">\r\n            <td class=\"first\">{TXT_CALENDAR_CAT}:</td>\r\n            <td>\r\n            <select name=\"catid\" style=\"width: 25%; \">\r\n                <option value=