0px;\" valign=\"middle\">\r\n            		        <input type=\"radio\" name=\'inputSeriesDouranceType\' value=\"2\" {CALENDAR_SERIES_PATTERN_DOURANCE_2}/>&nbsp;{TXT_CALENDAR_SERIES_PATTERN_ENDS_AFTER}\r\n            		    </td>\r\n            		    <td style=\"border: 0px;\">\r\n            		    	<input type=\"text\" name=\'inputSeriesDouranceNotes\' maxlength=\"3\" value=\"{CALENDAR_SERIES_PATTERN_ENDS_AFTER}\" style=\"width: 20px;\" />&nbsp;{TXT_CALENDAR_SERIES_PATTERN_APPONTMENTS}\r\n            		    </td>\r\n              		</tr>\r\n              		<tr>\r\n              			<td style=\"border: 0px;\" valign=\"middle\">\r\n            		        <input type=\"radio\" name=\'inputSeriesDouranceType\' value=\"3\" {CALENDAR_SERIES_PATTERN_DOURANCE_3}/>&nbsp;{TXT_CALENDAR_SERIES_PATTERN_ENDS}\r\n            		    </td>\r\n            		    <td style=\"border: 0px;\">\r\n            		    	<input type=\"text\" name=\"inputRepeatDouranceEnd\" value=\"{CALENDAR_SERIES_PATTERN_ENDS}\" style=\"width: 140px;\" />\r\n            		    </td>\r\n              		</tr>\r\n              	</table>\r\n              </td>\r\n            </tr>\r\n        </table>\r\n    </div>\r\n        <table class=\"addevent\" cellpadding=\"0\" cellspacing=\"0\" width=\"100%\" border=\"0\">\r\n        <tr>\r\n            <td colspan=\"2\"><hr></td>\r\n        </tr>        \r\n        <tr>\r\n            <th colspan=\"2\">{TXT_EVENTPLACE}</th>\r\n        </tr>\r\n        <tr class=\"row1\">\r\n            <td class=\"first\">{TXT_CALENDAR_START}</td>\r\n            <td>\r\n    		<input type=\"text\" name=\"startdate\" id=\"DPC_edit1_YYYY-MM-DD\" value=\"{CALENDAR_START}\" style=\"width: 8em;\" />\r\n    		<select name=\"hour\" style=\"margin-left: 10px\" class=\"date\">\r\n    			<!-- BEGIN hour -->\r\n    			<option value=\"{CALENDAR_HOUR}\" {CALENDAR_HOUR_SELECT}>{CALENDAR_HOUR}</option>\r\n    			<!-- END hour -->\r\n    		</select>\r\n    		:\r\n    		<select name=\"minutes\" class=\"date\">