INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`, `lang`) VALUES (211,21,'{CALENDAR_JAVASCRIPT}\r\n<div id=\"calendar\">\r\n	<div class=\"search\">\r\n		<form id=\"searchform\" method=\"post\" action=\"index.php?section=calendar&amp;act=search\">\r\n		<table cellspacing=\"0\">\r\n			<tbody>\r\n				<tr>\r\n					<th colspan=\"4\">Kalender Suche</th>\r\n				</tr>\r\n				<tr class=\"row1\">\r\n					<td width=\"12%\">{TXT_CALENDAR_FROM}:&nbsp;</td>\r\n					<td width=\"18%\"><input type=\"text\" style=\"width: 80px;\" value=\"{CALENDAR_DATEPICKER_START}\" id=\"DPC_edit1_YYYY-MM-DD\" name=\"startDate\" /></td>\r\n					<td width=\"15%\">{TXT_CALENDAR_KEYWORD}:&nbsp;</td>\r\n					<td width=\"55%\"><input type=\"text\" value=\"{CALENDAR_SEARCHED_KEYWORD}\" name=\"keyword\" /></td>\r\n				</tr>\r\n				<tr class=\"row2\">\r\n					<td>{TXT_CALENDAR_TILL}:&nbsp;</td>\r\n					<td><input type=\"text\" value=\"{CALENDAR_DATEPICKER_END}\" style=\"width: 80px;\" id=\"DPC_edit2_YYYY-MM-DD\" name=\"endDate\" /></td>\r\n					<td>&nbsp;</td>\r\n					<td ><input type=\"submit\" value=\"{TXT_CALENDAR_SEARCH}\" /></td>\r\n				</tr>\r\n			</tbody>\r\n		</table>\r\n	</form>\r\n	</div>\r\n	<div class=\"notes_list\">\r\n		<table cellspacing=\"0\">\r\n			<tbody> \r\n				<tr>\r\n					<th>Datum</td>\r\n					<th>&nbsp;</td>\r\n					<th>Veranstaltung</td>\r\n				</tr>\r\n				<!-- BEGIN event -->\r\n				<tr class=\"{CALENDAR_ROW}\">\r\n					<td width=\"12%\" align=\"left\" valign=\"top\">{CALENDAR_START_SHOW}&nbsp;{CALENDAR_STARTTIME}</td>\r\n                    <td width=\"3%\" align=\"center\" valign=\"top\">{CALENDAR_PRIORITY_IMG}&nbsp;</td>\r\n					<td width=\"85%\" valign=\"top\"><a href=\"index.php?section=calendar&amp;cmd=event&amp;id={CALENDAR_ID}\">{CALENDAR_TITLE}</a><br /><font size=\"-2\">Ort: {CALENDAR_PLACE}</font></td>\r\n				</tr>\r\n				<!-- END event -->\r\n			</tbody>\r\n		</tabl