INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`, `lang`) VALUES (257,47,'<div id=\"blog\">\r\n	<div class=\"categories\">\r\n		<h2>Themen Kategorien</h2>\r\n		{BLOG_CATEGORIES_LIST}\r\n	</div>\r\n	<!-- BEGIN showBlogEntries -->\r\n		<div class=\"entries\">\r\n			<h2><a title=\"{BLOG_ENTRIES_TITLE}\" href=\"index.php?section=blog&amp;cmd=details&amp;id={BLOG_ENTRIES_ID}\">{BLOG_ENTRIES_TITLE}</a></h2>\r\n			<div class=\"posted_comment\">\r\n				{BLOG_ENTRIES_POSTED}\r\n				<!-- BEGIN showCommentPart -->\r\n					&nbsp;&nbsp;|&nbsp;&nbsp;<a title=\"{TXT_BLOG_COMMENTS}\" href=\"index.php?section=blog&amp;cmd=details&amp;id={BLOG_ENTRIES_ID}#comments\">{BLOG_ENTRIES_COMMENTS}</a>\r\n				<!-- END showCommentPart -->\r\n			</div>\r\n			<div class=\"content\">\r\n				<div class=\"image\">{BLOG_ENTRIES_IMAGE}</div>\r\n				<div class=\"text\">{BLOG_ENTRIES_INTRODUCTION}</div>\r\n				<div class=\"link\"><a href=\"index.php?section=blog&amp;cmd=details&amp;id={BLOG_ENTRIES_ID}\">Weiter...</a></div>\r\n			</div>\r\n			<div class=\"statistics\">\r\n				<strong>{TXT_BLOG_CATEGORIES}:</strong>&nbsp;{BLOG_ENTRIES_CATEGORIES}<br />\r\n				<strong>{TXT_BLOG_TAGS}:</strong>&nbsp;{BLOG_ENTRIES_TAGS}<br />\r\n				<!-- BEGIN showVotingPart -->\r\n					<strong>{TXT_BLOG_VOTING}:</strong>&nbsp; <a title=\"{TXT_BLOG_VOTING_DO}\" href=\"index.php?section=blog&amp;cmd=details&amp;id={BLOG_ENTRIES_ID}#voting\">{BLOG_ENTRIES_VOTING_STARS}</a>&nbsp;(<a title=\"{TXT_BLOG_VOTING_DO}\" href=\"index.php?section=blog&amp;cmd=details&amp;id={BLOG_ENTRIES_ID}#voting\">{BLOG_ENTRIES_VOTING}</a>) \r\n				<!-- END showVotingPart -->\r\n			</div>\r\n		</div>\r\n	<!-- END showBlogEntries -->\r\n	{BLOG_ENTRIES_PAGING}\r\n</div>','Blog','','y',0,'on','system',8,'1');
INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`, `lang`) VALUES (258,47,'<div id=\"blog\">\r\n	{BLOG_SEARCH_JAVASCRIPT}\r\n	<div class=\"searchform\">\r\n		<form action=\"index.php?section=blog&amp;cmd=search\" name=\"frmDoSearch\" method=\"post\">\r\n			<p><label>{TXT_SEARCH_MODUS_KEYWORD}</label><input type=\"text\" value=\"{BLOG_SEARCH_KEYWORD_STRING}\" name=\"frmDoSearch_Keyword_String\" /></p>\r\n			<p><label>Kategorie</label>{BLOG_SEARCH_KEYWORD_CATEGORIES}</p>\r\n			<p><label>{TXT_SEARCH_MODUS_DATE}</label><div style=\"width: 200px; \">{BLOG_SEARCH_DATE_CALENDAR}</div></p>\r\n			<p><input type=\"submit\" value=\"{TXT_SEARCH_SUBMIT}\" name=\"frmDoSearch_Submit\" /></p>\r\n		</form>\r\n	</div>\r\n	<!-- BEGIN ResultPart -->\r\n		<h2>{TXT_SEARCH_RESULTS}</h2>\r\n		<!-- BEGIN showResults -->\r\n			<div class=\"entries\">\r\n				<h2><a title=\"{BLOG_SEARCH_RESULTS_SUBJECT}\" href=\"index.php?section=blog&amp;cmd=details&amp;id={BLOG_SEARCH_RESULTS_MID}\">{BLOG_SEARCH_RESULTS_SUBJECT}</a></h2>\r\n				<div class=\"content\">\r\n					<div class=\"text\">{BLOG_SEARCH_RESULTS_INTRODUCTION}</div>\r\n					<div class=\"link\"><a href=\"index.php?section=blog&amp;cmd=details&amp;id={BLOG_SEARCH_RESULTS_MID}\">Weiter...</a></div>\r\n				</div>\r\n				<div class=\"statistics\">\r\n					<strong>{TXT_SEARCH_RESULTS_DATE}:</strong>&nbsp;{BLOG_SEARCH_RESULTS_POSTED}<br />\r\n					<strong>{TXT_SEARCH_RESULTS_CATEGORY}:</strong>&nbsp;{BLOG_SEARCH_RESULTS_CATEGORIES}<br />\r\n					<strong>{TXT_SEARCH_RESULTS_KEYWORDS}:</strong>&nbsp; {BLOG_SEARCH_RESULTS_TAGS} \r\n				</div>\r\n			</div>\r\n		<!-- END showResults -->\r\n		<!-- BEGIN showNoResults -->\r\n			<div class=\"text\">{TXT_SEARCH_RESULTS_NONE}</div>\r\n		<!-- END showNoResults -->\r\n	<!-- END ResultPart -->\r\n</div>','Suchen','search','y',257,'on','system',0,'1');
INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`, `lang`) VALUES (259,47,'Eine <strong>Tag Cloud</strong> ist eine Methode zur Informationsvisualisierung, bei der eine Liste aus Schlagworten zweidimensional alphabetisch sortiert angezeigt wird, wobei einzelne unterschiedlich gewichtete Worte gr&ouml;sser oder auf andere Weise hervorgehoben dargestellt werden. Wortwolken werden zunehmend beim gemeinschaftlichen Indexieren und in Blogs eingesetzt. [<a target=\"_blank\" href=\"http://de.wikipedia.org/wiki/Tag_cloud\">Quelle</a>]\r\n<h2 class=\"blogTitle\">Beliebteste Tags</h2>\r\n{BLOG_TAG_HITLIST}\r\n<h2 class=\"blogTitle\">Tag Cloud</h2>\r\n<div style=\"text-align: justify; margin-top: 20px;\">{BLOG_TAG_CLOUD}</div>','Tag Cloud','cloud','y',257,'on','system',1,'1');
INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`, `lang`) VALUES (260,0,'<div id=\"blog\">\r\n	<div class=\"text\">Das Contrexx Blog-Modul erzeugt, falls in den Moduleinstellungen aktiviert, automatisch RSS-Feeds von Ihren Meldungen und den dazu verfassten Kommentaren. Dabei wird pro aktivierte Systemsprache im Ordner <strong>feed/</strong> eine eigene XML-Datei nach folgendem Schema erstellt:</div>\r\n	<div class=\"code\">\r\n<pre>blog_messages_XX.xml\r\nblog_comments_XX.xml\r\nblog_category_ID_XX.xml</pre>\r\n	</div>\r\n	<div class=\"text\">Dabei steht der Platzhalter XX f&uuml;r die Kurzform der jeweiligen Sprache und ID f&uuml;r die eindeutige Bezeichnungs-ID der jeweiligen Kategorie. F&uuml;r die Sprache Deutsch (Kurzform: de) lauten der komplette Link beispielsweise wie folgt:</div>\r\n	<div class=\"code\">\r\n<pre>http://www.Ihre-Website.ch/feed/blog_messages_de.xml\r\nhttp://www.Ihre-Website.ch/feed/blog_comments_de.xml</pre>\r\n	</div>\r\n	<div class=\"text\">F&uuml;r die Kategorie 1 (ID = 1) in der Sprache deutsch w&uuml;rde der Pfad demnach wie folgt lauten:</div>\r\n	<div class=\"code\">\r\n<pre>http://www.Ihre-Website.ch/feed/blog_category_1_de.xml</pre>\r\n	</div>\r\n	<div class=\"text\">Eine komplette &Uuml;bersicht aller Kurzformen finden Sie in Ihrem Administrationsbereich unter <strong>Administration &gt; Spracheinstellungen</strong>.</div>\r\n</div>','RSS-Feed','','y',257,'on','system',3,'1');
INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`, `lang`) VALUES (261,47,'<div id=\"blog\">\r\n	<!-- BEGIN showOkay -->\r\n		<div class=\"message_ok\">{BLOG_DETAILS_COMMENT_OKAY}</div>\r\n	<!-- END showOkay -->\r\n	<!-- BEGIN showError -->\r\n		<div class=\"message_error\">{BLOG_DETAILS_COMMENT_ERROR}</div>\r\n	<!-- END showError -->\r\n	<div class=\"entry\">\r\n		<h2>{BLOG_DETAILS_TITLE}</h2>\r\n		<div class=\"posted_comment\">{BLOG_DETAILS_POSTED}</div>\r\n		<div class=\"content\">\r\n			<div class=\"image\">{BLOG_DETAILS_IMAGE}</div>\r\n			<div class=\"text\">{BLOG_DETAILS_CONTENT}</div>\r\n			<div class=\"networks\">{BLOG_DETAILS_NETWORKS}</div>\r\n		</div>\r\n	</div>\r\n	<!-- BEGIN votingPart -->\r\n		<div class=\"voting\">\r\n			<table cellspacing=\"0\">\r\n				<tr>\r\n					<th colspan=\"2\">{TXT_VOTING}</th>\r\n				</tr>\r\n				<tr class=\"row1\">\r\n					<td width=\"30%\" nowrap=\"nowrap\">{TXT_VOTING_ACTUAL}</td>\r\n					<td width=\"70%\">{BLOG_DETAILS_VOTING_BAR}</td>\r\n				</tr>\r\n				<tr class=\"row2\">\r\n					<td nowrap=\"nowrap\">{TXT_VOTING_AVG}</td>\r\n					<td>{BLOG_DETAILS_VOTING_AVG}</td>\r\n				</tr>\r\n				<tr class=\"row1\">\r\n					<td nowrap=\"nowrap\">{TXT_VOTING_COUNT}</td>\r\n					<td>{BLOG_DETAILS_VOTING_COUNT}</td>\r\n				</tr>\r\n				<tr class=\"row2\">\r\n					<td nowrap=\"nowrap\">{TXT_VOTING_USER}</td>\r\n					<td>{BLOG_DETAILS_VOTING_USER}</td>\r\n				</tr>\r\n			</table>\r\n		</div>\r\n	<!-- END votingPart -->\r\n	<!-- BEGIN commentPart -->\r\n	{BLOG_DETAILS_COMMENTS_JAVASCRIPT}\r\n	<h3>{TXT_COMMENTS}</h3>\r\n	<!-- BEGIN showCommentRows -->\r\n		<div class=\"comments\">\r\n			<div class=\"title\"><strong>{BLOG_DETAILS_COMMENT_TITLE}</strong></div>\r\n			<div class=\"posted_comment\">{BLOG_DETAILS_COMMENT_POSTED}</div>\r\n			<div class=\"text\">{BLOG_DETAILS_COMMENT_CONTENT}</div>\r\n		</div>\r\n	<!-- END showCommentRows -->\r\n	<!-- BEGIN showNoCommentRows -->\r\n		<div class=\"comments\">{TXT_COMMENTS_NONE_EXISTING}</div>\r\n	<!-- END showNoCommentRows -->\r\n	<!-- BEGIN commentAddPart -->\r\n		<div class=\"addcomment\">\r\n			<h3>{TXT_COMMENT_ADD}</h3>\r\n			<form name=\"frmAddComment\" method=\"post\" action=\"index.php?section=blog&amp;cmd=details&amp;id={BLOG_DETAILS_COMMENT_ADD_MESSAGE_ID}\">\r\n				<input type=\"hidden\" name=\"frmAddComment_MessageId\" value=\"{BLOG_DETAILS_COMMENT_ADD_MESSAGE_ID}\" />\r\n				<p><label>{TXT_COMMENT_ADD_NAME}</label>{BLOG_DETAILS_COMMENT_ADD_NAME}</p>\r\n				<p><label>{TXT_COMMENT_ADD_EMAIL}</label>{BLOG_DETAILS_COMMENT_ADD_EMAIL}</p>\r\n				<p><label>{TXT_COMMENT_ADD_WWW}</label>{BLOG_DETAILS_COMMENT_ADD_WWW}</p>\r\n				<p><label>{TXT_COMMENT_ADD_SUBJECT}</label><input type=\"text\" name=\"frmAddComment_Subject\" value=\"{BLOG_DETAILS_COMMENT_ADD_SUBJECT}\" class=\"blogCommentInput\" /></p>\r\n				<p><label>{TXT_COMMENT_ADD_COMMENT}</label>{BLOG_DETAILS_COMMENT_ADD_COMMENT}</p>\r\n				<p><label>{TXT_COMMENT_ADD_SPAM}</label><img src=\"{BLOG_DETAILS_COMMENT_ADD_SPAM_URL}\" alt=\"{BLOG_DETAILS_COMMENT_ADD_SPAM_ALT}\" title=\"{BLOG_DETAILS_COMMENT_ADD_SPAM_ALT}\" /></p>\r\n				<p><input type=\"hidden\" name=\"frmAddComment_Offset\" value=\"{BLOG_DETAILS_COMMENT_ADD_SPAM_OFFSET}\" /><input type=\"text\" name=\"frmAddComment_Captcha\" /></p>\r\n				<p><input type=\"submit\" name=\"frmAddComment_Submit\" value=\"{TXT_COMMENT_ADD_SUBMIT}\" /></p>\r\n			</form>\r\n		</div>\r\n	<!-- END commentAddPart -->\r\n	<!-- END commentPart -->\r\n	\r\n</div>','Blogmeldung anzeigen','details','y',257,'off','system',99,'1');
INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`, `lang`) VALUES (262,17,'<div id=\"voting\">\r\n	<div class=\"form\">\r\n		<form name=\"VotingForm\" action=\"index.php?section=voting\" method=\"post\">\r\n			<h2>{VOTING_TITLE}</h2><em>gestartet am {VOTING_DATE}</em><br />\r\n			<div class=\"message\">{VOTING_MSG}</div>\r\n			<div class=\"votes\">\r\n                                {VOTING_RESULTS_TEXT}<br />\r\n			        {VOTING_RESULTS_TOTAL_VOTES}\r\n			</div>\r\n			<!-- BEGIN voting_email_input --> \r\n				Tragen Sie bitte Ihre E-Mail Adresse ein, damit Sie an der Umfrage teilnehmen k&ouml;nnen:<br /><br />\r\n				<p><label>E-Mail Adresse</label><input type=\"text\" name=\"votingemail\" value=\"{VOTING_EMAIL}\"/></p><br />\r\n		   	<!-- END voting_email_input -->\r\n			<br />\r\n                        <!-- BEGIN additional_fields -->\r\n                            Für diese Umfrage werden weitere Informationen von Ihnen benötigt:<br /><br />\r\n                            <!-- BEGIN additional_elements -->\r\n                            <p><label>{VOTING_ADDITIONAL_INPUT_LABEL}</label>{VOTING_ADDITIONAL_INPUT}</p>\r\n                            <!-- END additional_elements -->\r\n                        <!-- END additional_fields -->\r\n			<br />\r\n			{TXT_SUBMIT}\r\n		</form>\r\n	</div>\r\n	<div class=\"archive\">\r\n		<table width=\"100%\" border=\"0\">\r\n			<tbody>\r\n				<tr>\r\n					<th valign=\"top\" colspan=\"2\">{VOTING_OLDER_TITLE}</th>\r\n				</tr>\r\n				<!-- BEGIN votingRow -->\r\n				<tr class=\"{VOTING_LIST_CLASS}\">\r\n					<td nowrap=\"nowrap\">{VOTING_OLDER_DATE}&nbsp;</td>\r\n					<td nowrap=\"nowrap\">{VOTING_OLDER_TEXT}&nbsp;</td>\r\n				</tr>\r\n				<!-- END votingRow -->\r\n			</tbody>\r\n		</table>\r\n		<br />\r\n		{VOTING_PAGING}\r\n	</div>\r\n</div>','Umfragen','','y',0,'on','system',7,'1');
