INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`, `lang`) VALUES (283,20,'Eine <strong>Tag Cloud</strong> ist eine Methode zur Informationsvisualisierung, bei der eine Liste aus Schlagworten zweidimensional alphabetisch sortiert angezeigt wird, wobei einzelne unterschiedlich gewichtete Worte gr&ouml;sser oder auf andere Weise hervorgehoben dargestellt werden. Wortwolken werden zunehmend beim gemeinschaftlichen Indexieren und in Blogs eingesetzt. [<a target=\"_blank\" href=\"http://de.wikipedia.org/wiki/Tag_cloud\">Quelle</a>]\r\n<h2 class=\"blogTitle\">Beliebteste Tags</h2>\r\n{FORUM_TAG_HITLIST}\r\n<h2 class=\"blogTitle\">Tag Cloud</h2>\r\n<div style=\"text-align: justify; margin-top: 20px;\">{FORUM_TAG_CLOUD}</div>','Tag Cloud','cloud','y',281,'on','system',1,'1');
