INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`, `lang`) VALUES (353,54,'<script language=\"javascript\">\r\nfunction showBcc() {\r\n document.getElementById(\"bcc\").style.display=\"block\";\r\ndocument.getElementById(\"show\").style.display=\"none\";\r\n}\r\nfunction validate()  {\r\n\r\nvar textarea = document.getElementsByTagName(\"textarea\")[0];\r\n       \r\n	if(textarea.value==\"\") {\r\n              alert(\"please enter the recepients name\");\r\n              return false;\r\n \r\n        }\r\n\r\n}\r\nfunction drop_down_block() {\r\n//alert(\"drop\");\r\n\r\n      document.getElementById(\"radio_send\").style.display=\"none\";\r\n      document.getElementById(\"multi_send\").style.display=\"block\";\r\n      document.send_message.rcpt_name.value=\"\"; \r\n}\r\nfunction multi_send_block() {\r\n//alert(\"multi\");\r\n//      alert(document.getElementById(\"rcpt\").value);\r\n      document.getElementById(\"radio_send\").style.display=\"block\";\r\n      document.getElementById(\"multi_send\").style.display=\"none\";\r\n}\r\n\r\n</script>\r\n<form action=\"index.php?section=u2u&amp;act=message\" method=\"post\" name=\"send_message\">\r\n    <table cellspacing=\"1\" cellpadding=\"1\" border=\"0\" align=\"center\" width=\"70%\">\r\n        <tbody>\r\n            <!-- BEGIN u2u_private_notification_message_link -->\r\n            <tr>\r\n                <td bgcolor=\"#efefef\" style=\"padding-left: 10px;\"><a href=\"?section=u2u&amp;cmd=notification\">{TXT_PRIVATE_NOTIFICATION_LINK}</a><b>{TXT_NOTIFICATION_TOTAL_COUNT}</b></td>\r\n            </tr>\r\n            <!-- END u2u_private_notification_message_link -->\r\n            <tr>\r\n                <td height=\"20\">&nbsp;</td>\r\n            </tr>\r\n            <tr>\r\n                <td bgcolor=\"#efefef\">{TXT_SEND_PRIVATE_MESSAGE}</td>\r\n            </tr>\r\n            <br />\r\n            <br 