INSERT INTO `contrexx_module_knowledge_article_content` (`id`, `article`, `lang`, `question`, `answer`) VALUES ('184', '1', '1', 'Was ist Contrexx?', 'Contrexx&reg; ist ein modernes, einzigartiges und modulares Web Content Management System (WCMS) f&uuml;r die komplette Verwaltung einer Webseite. Zudem kann Contrexx&reg; auch f&uuml;r andere&nbsp;Informationsangebote wie Intranet, Extranet, eShop, Portal und weiteres eingesetzt werden. Das Contrexx&reg; basiert auf neuster PHP und MySQL Technologie und besticht in der einfachen Bedienung.');
INSERT INTO `contrexx_module_knowledge_article_content` (`id`, `article`, `lang`, `question`, `answer`) VALUES ('185', '1', '2', 'What is Contrexx?', 'Contrexx&reg; is a powerful Open Source Web Content Management System (WCMS) which will assist you in the creation, administration and maintenance of contents for the internet or intranet. It\'s a completely web-based system with an easy-to-use WYSIWYG editor and intuitive user interface. Contrexx&reg; is very flexible, by adding modules you can customize the CMS according to your individual demands and it requires no technical knowledge or previous training. Visit us online at <a rel=\"nofollow\" title=\"http://www.contrexx.com/\" class=\"external\" href=\"http://www.contrexx.com/\">http://www.contrexx.com/</a> to learn more.');
INSERT INTO `contrexx_module_knowledge_article_content` (`id`, `article`, `lang`, `question`, `answer`) VALUES ('186', '1', '3', 'What is Contrexx?', 'Contrexx&reg; is a powerful Open Source Web Content Management System (WCMS)  which will assist you in the creation, administration and maintenance  of contents for the internet or intranet. It\'s a completely web-based  system with an easy-to-use WYSIWYG editor and intuitive user interface.  Contrexx&reg; is very flexible, by adding modules you can customize the CMS  according to your individual demands and it requires no technical  knowledge or previous training. Visit us online at <a href=\"http://www.contrexx.com/\" class=\"external\" title=\"http://www.contrexx.com/\" rel=\"nofollow\">http://www.contrexx.com/</a> to learn more.');
INSERT INTO `contrexx_module_knowledge_article_content` (`id`, `article`, `lang`, `question`, `answer`) VALUES ('187', '7', '1', 'Wie erstelle ich einen neuen Eintrag?', '<h3>1. Im Backend einloggen</h3> <p>Melden Sie sich zuerst im Backend Ihrer Website an. Die URL&nbsp;zum Backend lautet jeweils <a href=\"http://www.DOMAINNAME.TLD/cadmin\">http://www.DOMAINNAME.TLD/cadmin</a></p> <h3>2. Neuen Artikel erstellen</h3> <p>Navigieren Sie nach &quot;Module/Wissensdatenbank/Artikel&quot; und klicken Sie dort auf die Schaltfl&auml;che &quot;Hinzuf&uuml;gen&quot;.</p> <h3>3. Kategorie festlegen</h3> <p>W&auml;hlen Sie die Kategorie, unter welcher der Artikel erscheinen soll.</p> <img height=\"23\" width=\"412\" src=\"images/content/knowledgebase/wie_erstelle_ich_einen_neuen_eintrag_schritt_3_de.jpg\" alt=\"\" /><br /> <br /> <h3>4. Frage festlegen</h3> <p>Definieren Sie die Frage, welche beantwortet werden soll.</p> <img height=\"47\" border=\"0\" width=\"475\" alt=\"\" src=\"images/content/knowledgebase/wie_erstelle_ich_einen_neuen_eintrag_schritt_4_de.jpg\" /><br /> <br /> <h3>5. Stichworte festlegen</h3> <p>W&auml;hlen Sie vorhandene Stichworte aus oder f&uuml;gen Sie neue hinzu.</p> <img height=\"21\" width=\"378\" alt=\"\" src=\"images/content/knowledgebase/wie_erstelle_ich_einen_neuen_eintrag_schritt_5_de.jpg\" /><br /> <br /> <h3>6. Antwort schreiben</h3> <p>Schreiben Sie die Antwort im Editor.</p> <img height=\"102\" width=\"425\" alt=\"\" src=\"images/content/knowledgebase/wie_erstelle_ich_einen_neuen_eintrag_schritt_6_de.jpg\" /><br /> <br /> <h3>7. Eintrag speichern</h3> <p>Klicken Sie nun auf die Schaltfl&auml;che &quot;Speichern&quot;.</p>');
INSERT INTO `contrexx_module_knowledge_article_content` (`id`, `article`, `lang`, `question`, `answer`) VALUES ('188', '7', '2', 'How do I create a new entry?', '<h3>1. Backend login</h3> <p>First login to your backend. The url is always named on <a href=\"http://www.DOMAINNAME.TLD/cadmin\">http://www.DOMAINNAME.TLD/cadmin</a></p> <h3>2. Create a new entry</h3> <p>Navigate to &quot;Module/Knowledge base/Articles&quot; and click on the button &quot;Add&quot;.</p> <h3>3. Set the category</h3> <p>Choose the category in which the entry should appear.</p> <img height=\"23\" width=\"408\" alt=\"\" src=\"images/content/knowledgebase/wie_erstelle_ich_einen_neuen_eintrag_schritt_3_en.jpg\" /><br /> <br /> <h3>4. Set the question</h3> <p>Define the question that has to be answered.</p> <img height=\"49\" border=\"0\" width=\"412\" src=\"images/content/knowledgebase/wie_erstelle_ich_einen_neuen_eintrag_schritt_4_en.jpg\" alt=\"\" /><br /> <br /> <h3>5. Set the keywords</h3> <p>Choose some existing keywords or add new ones.</p> <img height=\"22\" width=\"348\" src=\"images/content/knowledgebase/wie_erstelle_ich_einen_neuen_eintrag_schritt_5_en.jpg\" alt=\"\" /><br /> <br /> <h3>6. Write the answer</h3> <p>Type in the answer into the editor.</p> <img height=\"104\" width=\"424\" src=\"images/content/knowledgebase/wie_erstelle_ich_einen_neuen_eintrag_schritt_6_en.jpg\" alt=\"\" /><br /> <br /> <h3>7. Save the entry</h3> <p>Now click on the button &quot;Save&quot;.</p>');
INSERT INTO `contrexx_module_knowledge_article_content` (`id`, `article`, `lang`, `question`, `answer`) VALUES ('189', '7', '3', 'How do I create a new entry?', '<h3>1. Backend login</h3> <p>First login to your backend. The url is always named on <a href=\"http://www.DOMAINNAME.TLD/cadmin\">http://www.DOMAINNAME.TLD/cadmin</a></p> <h3>2. Create a new entry</h3> <p>Navigate to &quot;Module/Knowledge base/Articles&quot; and click on the button &quot;Add&quot;.</p> <h3>3. Set the category</h3> <p>Choose the category in which the entry should appear.</p> <img height=\"23\" width=\"408\" alt=\"\" src=\"images/content/knowledgebase/wie_erstelle_ich_einen_neuen_eintrag_schritt_3_en.jpg\" /><br /> <br /> <h3>4. Set the question</h3> <p>Define the question that has to be answered.</p> <img height=\"49\" border=\"0\" width=\"412\" src=\"images/content/knowledgebase/wie_erstelle_ich_einen_neuen_eintrag_schritt_4_en.jpg\" alt=\"\" /><br /> <br /> <h3>5. Set the keywords</h3> <p>Choose some existing keywords or add new ones.</p> <img height=\"22\" width=\"348\" src=\"images/content/knowledgebase/wie_erstelle_ich_einen_neuen_eintrag_schritt_5_en.jpg\" alt=\"\" /><br /> <br /> <h3>6. Write the answer</h3> <p>Type in the answer into the editor.</p> <img height=\"104\" width=\"424\" src=\"images/content/knowledgebase/wie_erstelle_ich_einen_neuen_eintrag_schritt_6_en.jpg\" alt=\"\" /><br /> <br /> <h3>7. Save the entry</h3> <p>Now click on the button &quot;Save&quot;.</p>');
