INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `displayorder`, `username`) VALUES ('92', '23', 'Sofern Inhaltscontainer blau umrahmt wurden, k&ouml;nnen Sie mit der Maus in einen dieser Bereiche klicken. Alle anderen Bereiche werden dann f&uuml;r die Bearbeitung gesperrt, solange die &Auml;nderungen nicht gespeichert oder verworfen wurden.<br />\r\n<br />\r\n<img alt=\"\" src=\"/images/content/screenshots/700px-Frontend_editing_block_border.png\" style=\"width: 700px; height: 234px;\" /><br />\r\n<br />\r\nWeitere Informationen zum Frontend-Editing finden Sie im Contrexx-Wiki:&nbsp;<a href=\"http://wiki.contrexx.com/de/index.php?title=Frontend_Editing\">http://wiki.contrexx.com/de/index.php?title=Frontend_Editing</a>', 'Inhaltscontainer bearbeiten', '', '0', '71', '1', '15', 'system');
