INSERT INTO `contrexx_module_podcast_medium` (`id`, `title`, `youtube_id`, `author`, `description`, `source`, `thumbnail`, `template_id`, `width`, `height`, `playlenght`, `size`, `views`, `status`, `date_added`) VALUES (3,'Photoshop Tutorial: How to Create a Web 2.0-Style Logo','-pOkaC4eHsE','','Tutorial how to create a Web 2.0-Style logo using Adobe Photoshop.','http://youtube.com/v/-pOkaC4eHsE','images/podcast/youtube_thumbnails/youtube_-pOkaC4eHsE.jpg',101,425,350,369,0,1,1,1209970275),(4,'Photoshop Tutorial: Web 2.0 Logo','LVAkb1V4NKk','','Ein kleines Photoshop Tutorial welches zeigt wie man ein Web 2.0 Logo mit Spiegel Effekt realisiert.','http://youtube.com/v/LVAkb1V4NKk','images/podcast/youtube_thumbnails/youtube_LVAkb1V4NKk.jpg',101,425,350,217,0,1,1,1209970618),(5,'Schlagwort Web 2.0 Part 2','deqIIoSyuik','','Vortrag anlässlich der Cisco Expo 2008 Vienna','http://youtube.com/v/deqIIoSyuik','images/podcast/youtube_thumbnails/youtube_deqIIoSyuik.jpg',101,425,350,549,0,1,1,1209970754),(7,'Im Gespräch: Heiko Hebig zu den neuesten Trends im Internet','kvzX0UTa5zI','mediaTREFF','Kongress Web 2.0 in Wiesbaden','http://youtube.com/v/kvzX0UTa5zI','images/podcast/youtube_thumbnails/youtube_kvzX0UTa5zI.jpg',101,425,350,585,0,1,1,1209972776),(9,'Social Networking - Der große Trend im Internet','GSuJwQxU6a8','','Die Idee ist einfach: Menschen mit gleichen Interessen treffen sich auf Internetportalen, um z. B. Persönliches auszutauschen oder für den Beruf eine Gemeinschaft aufzubauen. Die Nutzerzahlen steigen rasant. Doch bei allen \"Social Networking\"-Portalen gibt es noch keine zündende Idee, wie man damit auch richtig Geld verdienen kann.','http://youtube.com/v/GSuJwQxU6a8','images/podcast/youtube_thumbnails/youtube_GSuJwQxU6a8.jpg',101,425,350,291,0,1,1,1209973309),(10,'Are You Taking Advantage of Web 2.0 Marketing','HR0Ip0JwJKk','Jack Humphrey','\"Are You Taking Advantage of Web 2.0 Marketing?\" by Jack Humphrey','http://youtube.com/v/HR0Ip0JwJKk','images/podcast/youtube_thumbnails/youtube_HR0Ip0JwJKk.jpg',101,425,350,266,0,1,1,1209973477),(11,'Marketingberater 2.0@Web 2.0-Expo (3): iStockphoto','Lp8oqHseoOw','','Berater, Coach, Blogger und Podcaster Sebastian Voss alias Marketingberater 2.0, hat sich zur Web2.0Expo in Berlin aufgemacht, um für Sie die neuesten Trends in Sachen Marketing und Mitmach-Web zu erhaschen. Ausgestattet mit seiner nagelneuen Handycam und einer Handvoll Neugier spricht er mit CEOs und Mitarbeitern von Adobe Systems, Wikio, GfK GeoMarketing uvm.','http://youtube.com/v/Lp8oqHseoOw','images/podcast/youtube_thumbnails/youtube_Lp8oqHseoOw.jpg',101,425,350,545,0,1,1,1209973680),(12,'What is Web 2.0?','0LzQIUANnHc','Andy Gutmans, Co-founder and VP, Zend','It\'s one of the biggest buzzwords out there, but what exactly does it mean? Andy Gutmans of Zend defines Web 2.0 and explains how it\'s changing the face of the Internet.','http://youtube.com/v/0LzQIUANnHc','images/podcast/youtube_thumbnails/youtube_0LzQIUANnHc.jpg',101,425,350,182,0,1,1,1213617873);
