INSERT INTO `contrexx_content` (`id`, `content`, `title`, `metatitle`, `metadesc`, `metakeys`, `metarobots`, `css_name`, `redirect`, `expertmode`) VALUES (2,'Informationen rund um das Contrexx&reg; Web Content Management System erhalten Sie in den folgenden Bereichen:<br />\r\n<br />\r\n<ul>\r\n    <li><a href=\"http://www.contrexx.com/docs/\" target=\"_blank\">Dokumentation</a><br />\r\n    Das Dokumentationsportal f&uuml;r Contrexx Anwender und Entwickler.</li>\r\n<br /> \r\n    <li><a href=\"http://www.contrexx.com/forum/\" target=\"_blank\">Forum</a><br />\r\n    Weiterf&uuml;hrende Hilfe erhalten Sie von der Contrexx Community im Open Source Forum.</li>\r\n<br />\r\n    <li><a href=\"http://www.contrexx.com/bugtracker/\" target=\"_blank\">Bugtracker</a><br />\r\n    Hier wird eine aktuelle Liste der bekannten Fehler, sowie der geplanten/gew&uuml;nschten Verbesserungen und Erweiterungen der Contrexx Software gef&uuml;hrt.</li>\r\n<br />\r\n    <li><a href=\"http://www.contrexxthemes.com/\" target=\"_blank\">Contrexx Themes</a><br />\r\n    Offizielles Contrexx Themes Portal mit Themes Paketen zum herunterladen.</li>\r\n</ul>','Contrexx Support','Contrexx Support','Contrexx Support','Contrexx Support','index','','','n');
