ote -->\r\n					<a style=\"border: 1px solid black; padding: 2px;\" href=\"?section=forum&amp;cmd=thread&amp;id={FORUM_THREAD_ID}&amp;act=quote&amp;postid={FORUM_POST_ID}\">\r\n						{TXT_FORUM_QUOTE}\r\n					</a>&nbsp;\r\n					<!-- END postQuote -->\r\n					<!-- BEGIN postDelete -->\r\n						<a style=\"border: 1px solid black; padding: 2px;\" onclick=\"deletePost(\'{FORUM_THREAD_ID}\', \'{FORUM_POST_ID}\'); return false;\" href=\"?section=forum&amp;cmd=thread&amp;id={FORUM_THREAD_ID}&amp;act=delete&amp;postid={FORUM_POST_ID}\">\r\n						{TXT_FORUM_DELETE}\r\n						</a>&nbsp;\r\n					<!-- END postDelete --><br />\r\n				</div>\r\n			</td>\r\n		</tr>\r\n		<!-- END forumPosts -->\r\n	</tbody>\r\n</table>\r\n</div>\r\n<br />\r\n{FORUM_POSTS_PAGING}\r\n<br /><br />\r\n<!-- BEGIN addPost -->\r\n<div class=\"div_title\" style=\"text-align: left;\"> 	{TXT_FORUM_ADD_POST} </div>\r\n<form enctype=\"multipart/form-data\" name=\"frm_thread\" action=\"?section=forum&amp;cmd=thread&amp;id={FORUM_THREAD_ID}\" method=\"post\" onsubmit=\"setScrollPos();\">\r\n	<div class=\"div_board\">\r\n	<table cellspacing=\"0\" cellpadding=\"0\" class=\"bottomborder\">\r\n		<!--<tr>\r\n				<th colspan=\"2\" style=\"text-align: left;\">\r\n					{TXT_FORUM_ADD_POST}\r\n				</th>\r\n			</tr>-->\r\n		<tbody>\r\n			<tr>\r\n				<td><a name=\"post\" /><!--{TXT_FORUM_ICON}--></td>\r\n				<td nowrap=\"nowrap\" colspan=\"2\">\r\n					<ul>\r\n					<!-- BEGIN icons -->\r\n					<li style=\"display: none;\">\r\n						<input type=\"radio\" style=\"border: 0px none ;\" id=\"icons_{FORUM_ICON_VALUE}\" name=\"icons\" value=\"{FORUM_ICON_VALUE}\" />\r\n						<label for=\"icons_{FORUM_ICON_VALUE}\">\r\n							<img src=\"{FORUM_ICON_SRC}\" alt=\"{FORUM_ICON_ALT}\" title=\"{FORUM_ICON_TITLE}\" />\r\n						</label>\r\n					</li>\r\n					<!-- END icons -->\r\n					</ul>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					{TXT_FORUM_SUBJECT}\r\n				</td>\r\n				<td colspan=\"2\">\r\n					<input class=\"inputtext\" type=\"text\" name=\"subject\" value=\"{FORUM_SUBJECT}\" />\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					{TXT_FORUM_KEYWORDS}\r\n				</td>\r\n				<td colspan=\"2\">\r\n					<input onfocus=\"showToolTip(\'{TXT_FORUM_COMMA_SEPARATED_KEYWORDS}\', this, \'forumKeywordsHelp\')\" onblur=\"hideToolTip(\'forumKeywordsHelp\')\" title=\"{TXT_FORUM_COMMA_SEPARATED_KEYWORDS}\" class=\"inputtext\" type=\"text\" name=\"keywords\" value=\"{FORUM_KEYWORDS}\" />\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					{TXT_FORUM_MESSAGE}\r\n				</td>\r\n				<td colspan=\"2\">\r\n					{FORUM_MESSAGE_INPUT}\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					{TXT_FORUM_FILE_ATTACHMENT}\r\n				</td>\r\n				<td colspan=\"2\">\r\n					<input class=\"inputtext\" type=\"file\" value=\"{FORUM_ATTACHMENT_VALUE}\" name=\"forum_attachment\" />\r\n					<!-- BEGIN delAttachment -->\r\n					    <br /><input type=\"checkbox\" id=\"delAttachment\" name=\"forum_delete_attachment\" value=\"1\" />\r\n						<label for=\"delAttachment\">{TXT_FORUM_DELETE_ATTACHMENT}</label>\r\n						<input type=\"hidden\" name=\"forum_attachment_oldname\" value=\"{FORUM_ATTACHMENT_OLDNAME}\" />\r\n					<!-- END delAttachment -->\r\n				</td>\r\n				</tr>\r\n				<!-- BEGIN captcha -->\r\n				<tr>\r\n					<td style=\"vertical-align: middle;\"> 	Captcha-Code:  </td>\r\n					<td style=\"vertical-align: middle; width: 10%;\"> 					<img src=\"{FORUM_CAPTCHA_IMAGE_URL}\" title=\"{FORUM_CAPTCHA_IMAGE_ALT}\" alt=\"{FORUM_CAPTCHA_IMAGE_ALT}\" />	 				</td>\r\n					<td style=\"vertical-align: middle; width: 90%;\"> 					<input type=\"text\" name=\"captcha\" size=\"6\" /> 					<br /></td>\r\n				</tr>\r\n				<!-- END captcha -->\r\n\r\n				<!-- BEGIN notificationRow -->\r\n				<tr>\r\n				  <td>\r\n					&nbsp;\r\n				  </td>\r\n				  <td colspan=\"2\">\r\n					<input id=\"forum_notify\" type=\"checkbox\" name=\"notification\" value=\"notify\" {FORUM_NOTIFICATION_CHECKBOX_CHECKED} /> <label for=\"forum_notify\">{TXT_FORUM_NOTIFY_NEW_POSTS}</label><br /><br />\r\n					 <input type=\"submit\" name=\"notification_update\" value=\"{TXT_FORUM_UPDATE_NOTIFICATION}\" /><br />\r\n				  </td>\r\n				</tr>\r\n			  <!-- END notificationRow -->\r\n			</tbody>\r\n		</table>\r\n\r\n		<br />\r\n		<input type=\"reset\" value=\"{TXT_FORUM_RESET}\" />\r\n		<!-- BEGIN createPost -->\r\n			<input type=\"submit\" name=\"create\" value=\"{TXT_FORUM_CREATE_POST}\" />\r\n		<!-- END createPost -->\r\n		<!-- BEGIN updatePost -->\r\n			<input type=\"submit\" name=\"update\" value=\"{TXT_FORUM_UPDATE_POST}\" />\r\n		<!-- END updatePost -->\r\n		<!-- BEGIN previewNewPost -->\r\n			<input type=\"submit\" name=\"preview_new\" value=\"{TXT_FORUM_PREVIEW}\" />\r\n		<!-- END previewNewPost -->\r\n		<!-- BEGIN previewEditPost -->\r\n			<input type=\"submit\" name=\"preview_edit\" value=\"{TXT_FORUM_PREVIEW}\" />\r\n		<!-- END previewEditPost -->\r\n	</div>\r\n		<input type=\"hidden\" name=\"post_edit_user_id\" value=\"{FORUM_POST_EDIT_USERID}\" />\r\n		<input type=\"hidden\" name=\"post_id\" value=\"{FORUM_EDIT_POST_ID}\" />\r\n		<input type=\"hidden\" name=\"thread_id\" value=\"{FORUM_THREAD_ID}\" />\r\n		<input type=\"hidden\" name=\"category_id\" value=\"{FORUM_CATEGORY_ID}\" />\r\n		<input type=\"hidden\" name=\"pos\" value=\"{FORUM_PAGING_POS}\" />\r\n		<input type=\"hidden\" name=\"scrollpos\" id=\"scrollpos\" />\r\n		<input type=\"hidden\" name=\"offset\" value=\"{FORUM_CAPTCHA_OFFSET}\" />\r\n	</form>\r\n	<!-- END addPost -->\r\n</div><!-- END div#forum -->\r\n\r\n<script type=\"text/javascript\" language=\"JavaScript\">\r\n//<![CDATA[\r\n  window.scrollTo(0, {FORUM_SCROLLPOS});\r\n//]]>\r\n</script>\r\n\r\n','Thread anzeigen','Thread anzeigen','Thread anzeigen','Thread anzeigen','index','','','y');
