n				<select name=\"subscribed[]\" size=\"32\" style=\"width:225px;\" multiple=\"multiple\">\r\n				{FORUM_NOTIFICATION_SUBSCRIBED}\r\n				</select>\r\n				<br />\r\n				<a href=\"javascript:SelectAllList(document.getElementById(\'frmForumNotification\').elements[\'subscribed[]\']);\">{TXT_SELECT_ALL}<br />\r\n				</a><a href=\"javascript:DeselectAllList(document.getElementById(\'frmForumNotification\').elements[\'subscribed[]\']);\">{TXT_DESELECT_ALL}</a>\r\n			</div>\r\n		</div>\r\n	</form>\r\n	<!-- END notification -->\r\n</div>','Abonnierte Themen','Abonnierte Themen','Abonnierte Themen','Abonnierte Themen','index','','','y');
