dden\" name=\"offset\" value=\"{FORUM_CAPTCHA_OFFSET}\" /></td>\r\n    				</tr>\r\n   					<!-- END captcha -->\r\n\r\n    				<!-- BEGIN notificationRow -->\r\n    				<tr>\r\n      					<td>&nbsp;</td>\r\n      					<td colspan=\"2\">\r\n        					<input id=\"forum_notify\" type=\"checkbox\" name=\"notification\" value=\"notify\" {FORUM_NOTIFICATION_CHECKBOX_CHECKED} /> <label for=\"forum_notify\">{TXT_FORUM_NOTIFY_NEW_POSTS}</label><br /><br />\r\n      					</td>\r\n    				</tr>\r\n  					<!-- END notificationRow -->\r\n				</tbody>\r\n			</table>\r\n			<br />\r\n			<input type=\"reset\" value=\"{TXT_FORUM_RESET}\" />\r\n			<input type=\"submit\" name=\"create\" value=\"{TXT_FORUM_CREATE_THREAD}\" />\r\n			<!--<input type=\"submit\" name=\"preview\" value=\"{TXT_FORUM_PREVIEW}\" />-->\r\n		</div>\r\n		<input type=\"hidden\" name=\"category_id\" value=\"{FORUM_FORUM_ID}\" />\r\n		<input type=\"hidden\" value=\"{FORUM_PAGING_POS}\" name=\"pos\" />\r\n	</form>\r\n    <!-- END addThread -->\r\n</div>','Forum anzeigen','board','y',97,'off','kon',10,'1');
