INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`, `lang`) VALUES (495,33,'<script type=\"text/javascript\">\r\n<!--\r\nfunction toggle(target){\r\n    obj = document.getElementById(target);\r\n    obj.style.display = (obj.style.display==\'none\') ? \'inline\' : \'none\';  \r\n    if (obj.style.display==\'none\' && target == \'hiddenSearch\'){\r\n         document.getElementById(\'searchCheck\').value = \'norm\';\r\n    }else if(obj.style.display==\'inline\' && target == \'hiddenSearch\'){\r\n         document.getElementById(\'searchCheck\').value = \'exp\';\r\n    }\r\n}\r\n\r\nfunction CheckForm() {\r\n  var errorMsg = \"\";\r\n  with( document.formEntry ) {\r\n  	if (type.value == \"\") {\r\n    	errorMsg = errorMsg + \"- {TXT_MARKET_TYPE}\\n\";\r\n    }\r\n    if (title.value == \"\") {\r\n        errorMsg = errorMsg + \"- {TXT_MARKET_TITLE_ENTRY}\\n\";\r\n    } \r\n    if (description.value == \"\") {\r\n        errorMsg = errorMsg + \"- {TXT_MARKET_DESCRIPTION}\\n\";\r\n    }   \r\n    if (cat.value == \"\") {\r\n        errorMsg = errorMsg + \"- {TXT_MARKET_CATEGORIE}\\n\";\r\n    } \r\n    \r\n    {END_DATE_JS}\r\n\r\n    if (userdetails.value == \"\") {\r\n        errorMsg = errorMsg + \"- {TXT_MARKET_USER_DETAIL}\\n\";\r\n    }\r\n    if (name.value == \"\") {\r\n        errorMsg = errorMsg + \"- {TXT_MARKET_NAME}\\n\";\r\n    }\r\n    if (email.value == \"\") {\r\n        errorMsg = errorMsg + \"- {TXT_MARKET_EMAIL}\\n\";\r\n    }else{\r\n    	if (!matchType(\'^[_a-zA-Z0-9-]+(.[_a-zA-Z0-9-]+)*@[a-zA-Z0-9-]+(.[a-zA-Z0-9-]+)*.(([0-9]{1,3})|([a-zA-Z]{2,3})|(aero|coop|info|museum|name))$\', email.value)){\r\n    		errorMsg = errorMsg + \"- {TXT_MARKET_EMAIL}\\n\";\r\n    	}\r\n    }\r\n    \r\n    if (errorMsg != \"\") \r\n    {\r\n        alert (\"{TXT_MARKET_FIELDS_REQUIRED}n{TXT_MARKET_THOSE_FIELDS_ARE_EMPTY}\\n\" + errorMsg);       \r\n        return false;\r\n    }else{\r\n    	return true;\r\n    }\r\n  }\r\n}\r\n\r\nfunction matchType(pattern, value) {\r\n	var reg = new RegExp(pattern);\r\n	if (value.match(reg)) {\r\n		return true;\r\n	}\r\n	return false;\r\n} \r\n\r\n-->\r\n</script>\r\n<div id=\"marketSearch\">\r\n	<form action=\"index.php?\" method=\"get\" name=\"marketSearch\" id=\"marketSearch\">\r\n		<input name=\"term\" value=\"{TXT_MARKET_SEARCHTERM}\" size=\"25\" maxlength=\"100\" /><input type=\"hidden\" id=\"searchCheck\" name=\"check\" value=\"norm\" size=\"10\" /><input type=\"hidden\" name=\"section\" value=\"market\" size=\"10\" /><input type=\"hidden\" name=\"cmd\" value=\"search\" size=\"10\" />&nbsp;<input type=\"submit\" value=\"{TXT_MARKET_SEARCH}\" name=\"search\" />                  &nbsp;&raquo;&nbsp;<a onclick=\"javascript:toggle(\'hiddenSearch\')\" href=\"javascript:{}\">{TXT_MARKET_SEARCH_EXP}</a><br />\r\n		<div style=\"display: none;\" id=\"hiddenSearch\"><br />\r\n		<table width=\"100%\" cellspacing=\"0\" cellpadding=\"0\" border=\"0\">\r\n			{MARKET_EXP_SEARCH_FIELD}\r\n		</table>\r\n		</div>\r\n	</form>\r\n</div>\r\n<form name=\"formEntry\" enctype=\"multipart/form-data\" method=\"post\" action=\"?section=market&cmd=confirm\" onsubmit=\"return CheckForm()\">\r\n<input type=\"hidden\" name=\"id\" value=\"{MARKET_ENTRY_ID}\" >\r\n	<table width=\"100%\" cellspacing=\"0\" cellpadding=\"2\" border=\"0\" align=\"top\" class=\"adminlist\">\r\n		<tr>\r\n			<td nowrap=\"nowrap\">{TXT_MARKET_TYPE}<font color=\"red\"> *</font></td>\r\n			<td>\r\n				<input type=\"radio\" name=\"type\" value=\"offer\" {MARKET_ENTRY_TYPE_OFFER} />&nbsp;{TXT_MARKET_OFFER}\r\n				<input type=\"radio\" name=\"type\" value=\"search\" {MARKET_ENTRY_TYPE_SEARCH} />&nbsp;{TXT_MARKET_SEARCH}\r\n			</td>\r\n		</tr>\r\n		<tr valign=\"top\">\r\n			<td nowrap=\"nowrap\" width=\"120\">{TXT_MARKET_TITLE_ENTRY}<font color=\"red\"> *</font></td>		\r\n			<td><input type=\"text\" name=\"title\" style=\"width: 300px;\" maxlength=\"100\" value=\"\" ></td>\r\n		</td>		\r\n		</tr>\r\n		<tr valign=\"top\">\r\n			<td nowrap=\"nowrap\" width=\"120\">{TXT_MARKET_DESCRIPTION}<font color=\"red\"> *</font></td>\r\n			<td><textarea name=\"description\" rows=\"5\"  style=\"width: 300px; overflow: auto;\"></textarea></td>\r\n		</tr>\r\n		<tr valign=\"top\">\r\n			<td nowrap=\"nowrap\" width=\"120\">{TXT_MARKET_PREMIUM}</td>		\r\n			<td><input type=\"checkbox\" name=\"premium\" value=\"1\" />&nbsp;<em>{TXT_MARKET_PREMIUM_CONDITIONS_NO}</em></td>\r\n		</td>\r\n		<tr valign=\"top\">\r\n			<td nowrap=\"nowrap\" width=\"120\">{TXT_MARKET_PICTURE}</td>		\r\n			<td>{ENTRY_PICTURE}<input type=\"file\" name=\"pic\" size=\"38\" style=\"width: 304px;\" maxlength=\"100\"></td>\r\n		</td>	\r\n		<tr valign=\"top\">\r\n			<td nowrap=\"nowrap\" width=\"120\">{TXT_MARKET_CATEGORIE}<font color=\"red\"> *</font></td>		\r\n			<td><select name=\"cat\"  style=\"width: 305px;\">{MARKET_CATEGORIES}</select></td>\r\n		</td>	\r\n		<tr valign=\"top\">\r\n			<td nowrap=\"nowrap\" width=\"120\">{TXT_MARKET_PRICE}</td>		\r\n			<td><input type=\"text\" name=\"price\" style=\"width: 98px;\" maxlength=\"100\" value=\"\">&nbsp;&nbsp;<input type=\"checkbox\" name=\"forfree\" value=\"1\" />&nbsp;{TXT_MARKET_FOR_FREE}&nbsp;<input type=\"checkbox\" name=\"agreement\" value=\"1\"/>&nbsp;{TXT_MARKET_AGREEMENT}</td>\r\n		</td>\r\n                <!-- BEGIN end_date_dropdown -->\r\n		<tr valign=\"top\">\r\n			<td nowrap=\"nowrap\" width=\"120\">{TXT_MARKET_END_DATE}<font color=\"red\"> *</font></td>		\r\n			<td><select name=\"days\" style=\"width: 100px;\">{MARKET_DAYS_ONLINE}</select>&nbsp;&nbsp;{TXT_MARKET_DAYS}</td>\r\n		</td>\r\n		<!-- END end_date_dropdown -->\r\n		<tr valign=\"top\">\r\n			<td nowrap=\"nowrap\" width=\"120\">{TXT_MARKET_NAME}<font color=\"red\"> *</font></td>		\r\n			<td><input type=\"text\" name=\"name\" style=\"width: 300px;\" maxlength=\"100\" value=\"\"></td>\r\n		</td>\r\n		<tr valign=\"top\">\r\n			<td nowrap=\"now