INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`, `lang`) VALUES (508,17,'<br />\r\n<form method=\"post\" action=\"?section=voting\" name=\"VotingForm\">\r\n    <table width=\"100%\" border=\"0\">\r\n        <tbody>\r\n            <tr>\r\n                <td><strong>{VOTING_TITLE}</strong><em>{VOTING_DATE}</em></td>\r\n            </tr>\r\n            <tr>\r\n                <td class=\"desc\">\r\n                <div style=\"color: rgb(255, 0, 0);\">{VOTING_MSG}</div>\r\n                {VOTING_RESULTS_TEXT}<br />\r\n                {VOTING_RESULTS_TOTAL_VOTES}<!-- BEGIN voting_email_input --> Tragen Sie bitte Ihre E-Mail Adresse ein, damit Sie an der Umfrage teilnehmen k&ouml;nnen:<br />\r\n                <input type=\"text\" style=\"width: 250px;\" value=\"{VOTING_EMAIL}\" name=\"votingemail\" /><br />\r\n                <br />\r\n                <!-- END voting_email_input -->{TXT_SUBMIT}</td>\r\n            </tr>\r\n        </tbody>\r\n    </table>\r\n</form>\r\n<table width=\"100%\" border=\"0\">\r\n    <tbody>\r\n        <tr>\r\n            <td valign=\"top\" class=\"title\" colspan=\"2\"><strong>{VOTING_OLDER_TITLE}</strong></td>\r\n        </tr>\r\n        <tr>\r\n            <td valign=\"top\" nowrap=\"nowrap\"><strong>{TXT_DATE}&nbsp;</strong></td>\r\n            <td valign=\"top\" nowrap=\"nowrap\"><strong>{TXT_TITLE}&nbsp;</strong></td>\r\n        </tr>\r\n        <!-- BEGIN votingRow -->\r\n        <tr class=\"{VOTING_LIST_CLASS}\">\r\n            <td nowrap=\"nowrap\">{VOTING_OLDER_DATE}&nbsp;</td>\r\n            <td nowrap=\"nowrap\">{VOTING_OLDER_TEXT}&nbsp;</td>\r\n        </tr>\r\n        <!-- END votingRow -->\r\n    </tbody>\r\n</table>\r\n<br />\r\n{VOTING_PAGING}','Umfragen','','n',0,'on','system',5,'1');
INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`, `lang`) VALUES (513,0,'Das Contrexx Blog-Modul erzeugt, falls in den Moduleinstellungen aktiviert, automatisch RSS-Feeds von Ihren Meldungen und den dazu verfassten Kommentaren. Dabei wird pro aktivierte Systemsprache im Ordner <strong>feed/</strong> eine eigene XML-Datei nach folgendem Schema erstellt:<br />\r\n<pre>blog_messages_XX.xml<br />blog_comments_XX.xml<br />blog_category_ID_XX.xml</pre>\r\nDabei steht der Platzhalter XX f&uuml;r die Kurzform der jeweiligen Sprache und ID f&uuml;r die eindeutige Bezeichnungs-ID der jeweiligen Kategorie. F&uuml;r die Sprache Deutsch (Kurzform: de) lauten der komplette Link beispielsweise wie folgt:\r\n<pre>http://www.Ihre-Website.ch/feed/blog_messages_de.xml<br />http://www.Ihre-Website.ch/feed/blog_comments_de.xml</pre>\r\nF&uuml;r die Kategorie 1 (ID = 1) in der Sprache deutsch w&uuml;rde der Pfad demnach wie folgt lauten:<br />\r\n<pre>http://www.Ihre-Website.ch/feed/blog_category_1_de.xml</pre>\r\nEine komplette &Uuml;bersicht aller Kurzformen finden Sie in Ihrem Administrationsbereich unter <strong>Administration &gt; Spracheinstellungen</strong>.','RSS-Feed','','n',509,'on','system',4,'1');
INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`, `lang`) VALUES (514,3,'<!-- BEGIN galleryCategories -->\r\n<table width=\"100%\" cellspacing=\"0\" cellpadding=\"2\" border=\"0\">\r\n    <tbody>\r\n        <tr>\r\n            <td colspan=\"2\">Sie sind hier: {GALLERY_CATEGORY_TREE}</td>\r\n        </tr>\r\n        <!-- BEGIN galleryCategoryList -->\r\n        <tr>\r\n            <td colspan=\"2\"><hr size=\"1\" />\r\n            </td>\r\n        </tr>\r\n        <tr class=\"row{GALLERY_STYLE}\">\r\n            <td width=\"1%\" valign=\"top\" align=\"left\" class=\"imagestart\">{GALLERY_CATEGORY_IMAGE}</td>\r\n            <td valign=\"top\"><strong>{GALLERY_CATEGORY_NAME}</strong><br />\r\n            {GALLERY_CATEGORY_INFO}<br />\r\n            {GALLERY_CATEGORY_DESCRIPTION}</td>\r\n        </tr>\r\n        <!-- END galleryCategoryList -->\r\n        <tr>\r\n            <td colspan=\"2\"><hr size=\"1\" />\r\n            </td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<!-- CATEGORY END AND IMAGES START -->   <!-- BEGIN galleryImageBlock --> {GALLERY_JAVASCRIPT}\r\n<table width=\"100%\" cellspacing=\"1\" cellpadding=\"0\" border=\"0\" id=\"gallery\">\r\n    <tbody>\r\n        <tr>\r\n            <td colspan=\"3\">{GALLERY_CATEGORY_COMMENT}</td>\r\n        </tr>\r\n        <tr>\r\n            <td colspan=\"3\"><hr size=\"1\" />\r\n            </td>\r\n        </tr>\r\n        <!-- BEGIN galleryShowImages -->\r\n        <tr>\r\n            <td width=\"33%\" valign=\"top\" align=\"center\">{GALLERY_IMAGE1}<br />\r\n            {GALLERY_IMAGE_LINK1}</td>\r\n            <td width=\"33%\" valign=\"top\" align=\"center\">{GALLERY_IMAGE2}<br />\r\n            {GALLERY_IMAGE_LINK2}</td>\r\n            <td width=\"33%\" valign=\"top\" align=\"center\">{GALLERY_IMAGE3}<br />\r\n            {GALLERY_IMAGE_LINK3}</td>\r\n        </tr>\r\n        <!-- END galleryShowImage