INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`, `lang`) VALUES (556,47,'<!-- BEGIN showOkay -->\r\n<div class=\"blogOkayMessage\">{BLOG_DETAILS_COMMENT_OKAY}</div>\r\n<!-- END showOkay -->\r\n<!-- BEGIN showError -->\r\n<div class=\"blogErrorMessage\">{BLOG_DETAILS_COMMENT_ERROR}</div>\r\n<!-- END showError -->\r\n<h2 class=\"blogTitle\">{BLOG_DETAILS_TITLE}</h2>\r\n<h4 class=\"blogPostedBy\">{BLOG_DETAILS_POSTED}</h4>\r\n<div class=\"blogContent\">\r\n<div style=\"float:left; padding-right: 10px;\">{BLOG_DETAILS_IMAGE}</div>\r\n{BLOG_DETAILS_CONTENT}\r\n<br /><br />\r\n<div style=\"text-align: left; width: 100%;\">{BLOG_DETAILS_NETWORKS}</div>\r\n</div>\r\n<!-- BEGIN votingPart -->\r\n<h3 class=\"blogSubtitle\"><a name=\"voting\">{TXT_VOTING}</a></h3>\r\n<table width=\"580px\" cellspacing=\"0\" cellpadding=\"2\" border=\"0\" bgcolor=\"#FAFAFA\">\r\n<tr><td width=\"180px\" nowrap=\"nowrap\">{TXT_VOTING_ACTUAL}:</td><td width=\"470px\">{BLOG_DETAILS_VOTING_BAR}</td></tr>\r\n<tr><td nowrap=\"nowrap\">{TXT_VOTING_AVG}:</td><td>{BLOG_DETAILS_VOTING_AVG}</td></tr>\r\n<tr><td nowrap=\"nowrap\">{TXT_VOTING_COUNT}:</td><td>{BLOG_DETAILS_VOTING_COUNT}</td></tr>\r\n<tr><td nowrap=\"nowrap\">{TXT_VOTING_USER}:</td><td>{BLOG_DETAILS_VOTING_USER}</td></tr>\r\n</table>\r\n<!-- END votingPart -->\r\n<!-- BEGIN commentPart -->\r\n<h3 class=\"blogSubtitle\"><a name=\"comments\">{TXT_COMMENTS}</a></h3>\r\n{BLOG_DETAILS_COMMENTS_JAVASCRIPT}\r\n<!-- BEGIN showCommentRows -->\r\n<div class=\"blogCommentTitle\">\r\n<div style=\"float: left;\">{BLOG_DETAILS_COMMENT_TITLE}</div>\r\n<div style=\"float: right;\"><a href=\"javascript:toggleComment(\'{BLOG_DETAILS_COMMENT_ID}\');\" title=\"\"><img id=\"comment_{BLOG_DETAILS_COMMENT_ID}_img\" src=\"images/modules/blog/arrow_up.gif\" border=\"0\" alt=\"\" /></a></div>\r\n</div>\r\n<div class=\"blogPostedBy\">{BLOG_DETAILS_COMMENT_POSTED}</d