INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`, `lang`) VALUES (558,47,'Eine <strong>Tag Cloud</strong> ist eine Methode zur Informationsvisualisierung, bei der eine Liste aus Schlagworten zweidimensional alphabetisch sortiert angezeigt wird, wobei einzelne unterschiedlich gewichtete Worte gr&ouml;sser oder auf andere Weise hervorgehoben dargestellt werden. Wortwolken werden zunehmend beim gemeinschaftlichen Indexieren und in Blogs eingesetzt. [<a target=\"_blank\" href=\"http://de.wikipedia.org/wiki/Tag_cloud\">Quelle</a>]\r\n<h2>Beliebteste Tags</h2>\r\n{BLOG_TAG_HITLIST}\r\n<h2>Tag Cloud</h2>\r\n<div style=\"text-align: justify; margin-top: 20px;\">{BLOG_TAG_CLOUD}</div>','Tag Cloud','cloud','y',555,'on','system',3,'1');
INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`, `lang`) VALUES (559,0,'Das Contrexx Blog-Modul erzeugt, falls in den Moduleinstellungen aktiviert, automatisch RSS-Feeds von Ihren Meldungen und den dazu verfassten Kommentaren. Dabei wird pro aktivierte Systemsprache im Ordner <strong>feed/</strong> eine eigene XML-Datei nach folgendem Schema erstellt:<br />\r\n<pre>blog_messages_XX.xml<br />blog_comments_XX.xml<br />blog_category_ID_XX.xml</pre>\r\nDabei steht der Platzhalter XX f&uuml;r die Kurzform der jeweiligen Sprache und ID f&uuml;r die eindeutige Bezeichnungs-ID der jeweiligen Kategorie. F&uuml;r die Sprache Deutsch (Kurzform: de) lauten der komplette Link beispielsweise wie folgt:\r\n<pre>http://www.Ihre-Website.ch/feed/blog_messages_de.xml<br />http://www.Ihre-Website.ch/feed/blog_comments_de.xml</pre>\r\nF&uuml;r die Kategorie 1 (ID = 1) in der Sprache deutsch w&uuml;rde der Pfad demnach wie folgt lauten:<br />\r\n<pre>http://www.Ihre-Website.ch/feed/blog_category_1_de.xml</pre>\r\nEine komplette &Uuml;bersicht aller Kurzformen finden Sie in Ihrem Administrationsbereich unter <strong>Administration &gt; Spracheinstellungen</strong>.','RSS-Feed','','n',555,'on','system',4,'1');
INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`, `lang`) VALUES (560,35,'<!-- BEGIN podcast_medium -->\r\n<div style=\"overflow: hidden; float: right; width: 180px; height: auto ! important;\"><img id=\"podcast_preview\" src=\"{PODCAST_MEDIUM_THUMBNAIL}\" alt=\"{PODCAST_MEDIUM_TITLE}\" title=\"{PODCAST_MEDIUM_TITLE}\" /></div>\r\n<h2>Spiele: {PODCAST_MEDIUM_TITLE}</h2>\r\nDatum: {PODCAST_MEDIUM_SHORT_DATE}<br />\r\nSpieldauer: {PODCAST_MEDIUM_PLAYLENGHT}<br />\r\nAutor: {PODCAST_MEDIUM_AUTHOR}<br />\r\nBeschreibung: {PODCAST_MEDIUM_DESCRIPTION}<br />\r\nDateigr&ouml;sse: {PODCAST_MEDIUM_FILESIZE}<br />\r\nAufrufe: {PODCAST_MEDIUM_VIEWS}\r\n<div id=\"podcast_container\">&nbsp;</div>\r\n{PODCAST_MEDIUM_CODE}  <br />\r\n<br />\r\n<a href=\"{PODCAST_MEDIUM_URL}\" title=\"Video in externem Player starten\">Video in externem Player starten</a>  <br />\r\n<!-- END podcast_medium -->  <!-- BEGIN podcast_no_medium --> W&auml;hlen Sie ein Medium aus, dass abgespielt werden soll.<br />\r\n<!-- END podcast_no_medium -->  <br />\r\n{PODCAST_CATEGORY_MENU}<br />\r\n<br />\r\n<!-- BEGIN podcast_media -->\r\n<div id=\"podcastStart\"><a href=\"index.php?section=podcast&amp;id={PODCAST_MEDIA_ID}&amp;cid={PODCAST_MEDIA_CATEGORY_ID}\" title=\"{PODCAST_MEDIA_TITLE}\">{PODCAST_MEDIA_TITLE}</a> ({PODCAST_MEDIA_PLAYLENGHT})<br />\r\n<div id=\"podcastAnzeige\"><a href=\"index.php?section=podcast&amp;id={PODCAST_MEDIA_ID}&amp;cid={PODCAST_MEDIA_CATEGORY_ID}\" title=\"{TXT_PODCAST_PLAY}\"> <img border=\"0\" name=\"podcast_thumbnails\" src=\"{PODCAST_MEDIA_THUMBNAIL}\" title=\"{PODCAST_MEDIA_TITLE}\" alt=\"{PODCAST_MEDIA_TITLE}\" /> </a></div>\r\n<div id=\"podcastDescription\"><small>{PODCAST_MEDIA_DATE} ({TXT_PODCAST_MEDIA_VIEWS}: {PODCAST_MEDIA_VIEWS})</small> <br />\r\n{PODCAST_MEDIA_DESCRIPTION} <br />\r\n<br />\r\n<a href=\"index.php?section=podcast&amp;id={PODCAST_MEDIA_ID}&amp;cid={