INSERT INTO `contrexx_content` (`id`, `content`, `title`, `metatitle`, `metadesc`, `metakeys`, `metarobots`, `css_name`, `redirect`, `expertmode`) VALUES (1,'Besten Dank f&uuml;r Ihren Entscheid, <a href=\"http://www.contrexx.com\" target=\"_blank\">Contrexx&reg;</a> als Grundlage f&uuml;r Ihr Webprojekt einzusetzen.\r\n<h2>Erfolgreiche Installation von Contrexx&reg;</h2>\r\n<p><em>Contrexx&reg; Web Content Management System Version 2.0 ist </em>erfolgreich installiert worden. Nun ist <a href=\"http://www.contrexx.com\" target=\"_blank\">Contrexx&reg;</a> einsatzbereit. Sie  k&ouml;nnen jetzt die Inhalte erstellen und Ihr WebDesign integrieren. Mit der Installation von Contrexx&reg; akzeptieren Sie unsere <a target=\"_blank\" href=\"http://www.contrexx.com/de/index.php?page=1132\">Lizenzbedingungen</a>.</p>','Willkommen und Glückwunsch','Willkommen bei der erfolgreichen Installation von Contrexx® Web Content Management System Version 2.0','Willkommen bei der erfolgreichen Installation von Contrexx® Web Content Management System Version 2.0','Willkommen bei der erfolgreichen Installation von Contrexx® Web Content Management System Version 2.0','index','','','n');
INSERT INTO `contrexx_content` (`id`, `content`, `title`, `metatitle`, `metadesc`, `metakeys`, `metarobots`, `css_name`, `redirect`, `expertmode`) VALUES (2,'Die Erfahrung zeigt, dass Contrexx zuverl&auml;ssig funktioniert. Sollten Sie trotzdem Unterst&uuml;tzung ben&ouml;tigen, stehen wir Ihnen mit den folgenden Instrumenten sehr gerne zur Verf&uuml;gung.<br />\r\n<ul>\r\n    <li><a href=\"http://www.contrexx.com/wiki/de/\" class=\"out\">Dokumentation</a>: Das Dokumentationsportal f&uuml;r Contrexx Anwender und Entwickler.</li>\r\n    <li><a href=\"http://www.contrexx.com/forum/forumdisplay.php?f=68\" class=\"out\">Contrexx - Forum</a>: Weiterf&uuml;hrende Unterst&uuml;tzung erhalten Sie von der Contrexx Community im Open Source Forum.</li>\r\n    <li><a href=\"http://www.contrexx.com/forum/forumdisplay.php?f=36\" class=\"out\">Bugtracker im Forum</a>: Hier wird eine aktuelle Liste der bekannten Fehler, sowie der geplanten/gew&uuml;nschten Verbesserungen und Erweiterungen der Contrexx Software gef&uuml;hrt.</li>\r\n    <li><a href=\"http://support.comvation.com/index.php\" class=\"out\">Support - Ticket f&uuml;r Contrexx - Kunden</a>: Contrexx - Kunden k&ouml;nnen unter diesem Link ein Support-Ticket er&ouml;ffnen. Die Anfrage wird in das Ticketing-System geleitet und damit wird gew&auml;hrleistet, dass Ihre Anfrage rasch und zuverl&auml;ssig beantwortet wird. Nutzer der kostenlosen Opensource Version stehen die obigen M&ouml;glichkeiten zur Verf&uuml;gung.</li>\r\n</ul>','Contrexx Support','Contrexx Support','Contrexx Support','Contrexx Support','index','','','n');
