		<div class=\"desc\">{AUCTION_CATEGORY_DESCRIPTION}</div>\r\n			<div style=\"clear: both;\"><br /></div>\r\n		<!-- END showCategoriesTitle --> \r\n		\r\n			<table cellspacing=\"0\">\r\n				<tbody>\r\n					<tr>\r\n						<th valign=\"top\" nowrap=\"nowrap\" width=\"80\">&nbsp;</th>\r\n						<th valign=\"top\" nowrap=\"nowrap\"><a href=\"{AUCTION_TITLE_SORT}\">{TXT_AUCTION_TITLE}</a></th>\r\n						<th valign=\"top\" nowrap=\"nowrap\" align=\"center\"><a href=\"{AUCTION_ENDDATE_SORT}\">{TXT_AUCTION_OFFER_END}</a></th>\r\n						<th valign=\"top\" nowrap=\"nowrap\" align=\"center\">{TXT_AUCTION_BIDS}</th>\r\n						<th valign=\"top\" nowrap=\"nowrap\" align=\"right\">{TXT_AUCTION_PRICE}</th>\r\n					</tr>\r\n					<!-- BEGIN showEntries -->\r\n					<tr class=\"{AUCTION_ROW}\" onClick=\"document.location.href = \'{AUCTION_DETAIL}\';\" style=\"cursor:pointer;\" onMouseover=\"this.bgColor=\'#f6f6f6\'\" onMouseOut=\"this.bgColor=\'#ffffff\'\">\r\n						<td valign=\"top\" class=\"auction_tmb\"><a href=\"{AUCTION_DETAIL}\"><img src=\"{AUCTION_TMB}\" alt=\"{AUCTION_TITLE}\" title=\"{AUCTION_TITLE}\" border=\"0\" /></a></td>\r\n						<td valign=\"top\"><strong><a href=\"{AUCTION_DETAIL}\">{AUCTION_TITLE}</a></strong></td>\r\n						<td valign=\"top\" align=\"center\" nowrap>{AUCTION_ENDDATE}</td>\r\n						<td valign=\"top\" align=\"center\">{AUCTION_BIDS}</td>\r\n						<td valign=\"top\" nowrap align=\"right\">{AUCTION_PRICE}</td>\r\n					</tr>\r\n					<!-- END showEntries -->\r\n				</tbody>\r\n			</table>\r\n			\r\n			<!-- BEGIN noEntries -->\r\n				<div class=\"no_entries\">{AUCTION_NO_ENTRIES_FOUND}</div>\r\n			<!-- END noEntries --> \r\n			\r\n		</div>\r\n	<!-- END showEntriesHeader --> \r\n	\r\n	<!-- BEGIN showInsertEntries -->\r\n		<div class=\"insert_entries\">{DIRECTORY_INSERT_ENTRIES}</div>\r\n	<!-- END showInsertEntries -->\r\n	<div id=\"paging\">{SEARCH_PAGING}</div>\r\n</div>','Auktionen','Auktionen','Auktionen','Auktionen','index','','','y');
INSERT INTO `contrexx_content` (`id`, `content`, `title`, `metatitle`, `metadesc`, `metakeys`, `metarobots`, `css_name`, `redirect`, `expertmode`) VALUES (1226,'<script type=\"text/javascript\">\r\n	<!--\r\n	function toggle(target){\r\n		obj = document.getElementById(target);\r\n		obj.style.display = (obj.style.display==\'none\') ? \'inline\' : \'none\';  \r\n		if (obj.style.display==\'none\' && target == \'hiddenSearch\'){\r\n			 document.getElementById(\'searchCheck\').value = \'norm\';\r\n		}else if(obj.style.display==\'inline\' && target == \'hiddenSearch\'){\r\n			 document.getElementById(\'searchCheck\').value = \'exp\';\r\n		}\r\n	}\r\n	\r\n	function CheckForm() {\r\n	  var errorMsg = \"\";\r\n	  with( document.formEntry ) {\r\n		if (type.value == \"\") {\r\n			errorMsg = errorMsg + \"- {TXT_AUCTION_TYPE}\\n\";\r\n		}\r\n		if (title.value == \"\") {\r\n			errorMsg = errorMsg + \"- {TXT_AUCTION_TITLE_ENTRY}\\n\";\r\n		} \r\n		if (description.value == \"\") {\r\n			errorMsg = errorMsg + \"- {TXT_AUCTION_DESCRIPTION}\\n\";\r\n		}   \r\n		if (cat.value == \"\") {\r\n			errorMsg = errorMsg + \"- {TXT_AUCTION_CATEGORIE}\\n\";\r\n		} \r\n		\r\n		{END_DATE_JS}\r\n	\r\n		if (userdetails.value == \"\") {\r\n			errorMsg = errorMsg + \"- {TXT_AUCTION_USER_DETAIL}\\n\";\r\n		}\r\n		if (name.value == \"\") {\r\n			errorMsg = errorMsg + \"- {TXT_AUCTION_NAME}\\n\";\r\n		}\r\n		if (email.value == \"\") {\r\n			errorMsg = errorMsg + \"- {TXT_AUCTION_EMAIL}\\n\";\r\n		}else{\r\n			if (!matchType(\'^[_a-zA-Z0-9-]+(.[_a-zA-Z0-9-]+)*@[a-zA-Z0-9-]+(.[a-zA-Z0-9-]+)*.(([0-9]{1,3})|([a-zA-Z]{2,3})|(aero|coop|info|museum|name))$\', email.value)){\r\n				errorMsg = errorMsg + \"- {TXT_AUCTION_EMAIL}\\n\";\r\n			}\r\n		}\r\n		\r\n		if (errorMsg != \"\") \r\n		{\r\n			alert (\"{TXT_AUCTION_FIELDS_REQUIRED}n{TXT_AUCTION_THOSE_FIELDS_ARE_EMPTY}\\n\" + errorMsg);       \r\n			return false;\r\n		}else{\r\n			return true;\r\n		}\r\n	  }\r\n	}\r\n	\r\n	function matchType(pattern, value) {\r\n		var reg = new RegExp(pattern);\r\n		if (value.match(reg)) {\r\n			return true;\r\n		}\r\n		return false;\r\n	} \r\n	\r\n	-->\r\n	</script>\r\n<div id=\"auction\">\r\n	<form name=\"formEntry\" enctype=\"multipart/form-data\" method=\"post\" action=\"?section=auction&cmd=confirm\" onsubmit=\"return CheckForm()\">\r\n	<input type=\"hidden\" name=\"id\" value=\"{AUCTION_ENTRY_ID}\" >\r\n		<table width=\"100%\" cellspacing=\"0\" cellpadding=\"2\" border=\"0\" align=\"top\" class=\"adminlist\">\r\n			<tr valign=\"top\">\r\n				<td nowrap=\"nowrap\" width=\"120\">{TXT_AUCTION_TITLE_ENTRY}</td>		\r\n				<td><input type=\"text\" name=\"title\" style=\"width: 300px;\" maxlength=\"100\" value=\"\" ></td>		\r\n			</tr>\r\n			<tr valign=\"top\">\r\n				<td nowrap=\"nowrap\" width=\"120\">{TXT_AUCTION_CATEGORIE}</td>		\r\n				<td><select name=\"cat\"  style=\"width: 305px;\">{AUCTION_CATEGORIES}</select></td>\r\n			</td>\r\n			<tr valign=\"top\">\r\n				<td nowrap=\"nowrap\" width=\"120\">{TXT_AUCTION_DESCRIPTION}</td>\r\n				<td><textarea name=\"description\" rows=\"5\"  style=\"width: 300px; overflow: auto;\"></textarea></td>\r\n			</tr>\r\n			<tr valign=\"top\">\r\n				<td nowrap=\"nowrap\" width=\"120\">{TXT_AUCTION_PICTURE} 1</td>		\r\n				<td>{ENTRY_PICTURE}<input type=\"file\" name=\"pic_1\" size=\"38\" style=\"width: 304px;\" maxlength=\"100\"></td>\r\n			</td>	\r\n			<tr valign=\"top\">\r\n				<td nowrap=\"nowrap\" width=\"120\">{TXT_AUCTION_PICTURE} 2</td>		\r\n				<td>{ENTRY_PICTURE}<input type=\"file\" name=\"pic_2\" size=\"38\" style=\"width: 304px;\" maxlength=\"100\"></td>\r\n			</td>	\r\n			<tr valign=\"top\">\r\n				<td nowrap=\"nowrap\" width=\"120\">{TXT_AUCTION_PICTURE} 3</td>		\r\n				<td>{ENTRY_PICTURE}<input type=\"file\" name=\"pic_3\" size=\"38\" style=\"width: 304px;\" maxlength=\"100\"></td>\r\n			</td>	\r\n			<tr valign=\"top\">\r\n				<td nowrap=\"nowrap\" width=\"120\">{TXT_AUCTION_PICTURE} 4</td>		\r\n				<td>{ENTRY_PICTURE}<input type=\"file\" nam