INSERT INTO `contrexx_module_jobs` (`id`, `date`, `title`, `author`, `text`, `workloc`, `workload`, `work_start`, `catid`, `lang`, `userid`, `startdate`, `enddate`, `status`, `changelog`) VALUES (1,1215767617,'Comvation AG Mitarbeiter','admin','<p>&nbsp;Dies ist ein Stellenbeschrieb f&uuml;r Demonstrationszwecke.</p>\r\n<h2>Unternehmensdaten</h2>\r\n<p>COMVATION AG Internet Solutions &ndash; ist ein international t&auml;tiger Softwareentwickler und L&ouml;sungsintegrator f&uuml;r Web-Applikationen, datenbankbasierten und gesch&auml;ftskritischen Anwendungen, Internet- Extranet Portale, E-Business, Corporate Websites und E-Marketing. Zentrales Software-Produkt von COMVATION ist das Web Content Management System <a class=\"out\" href=\"http://www.contrexx.com/\">Contrexx&reg;</a>. In den vergangenen zwei Jahren sind mehr als 24.000 Lizenzen dieses Web CMS durch 150 Vertriebspartner abgesetzt worden. (Stand 01-2008)</p>\r\n<p>&nbsp;</p>\r\n<h2>Jobbeschreibung</h2>\r\n<p>Sie suchen ein kreatives, inspirierendes und innovatives Umfeld? Sie haben jede Menge z&uuml;ndende Ideen und m&ouml;chten diese in die t&auml;gliche Arbeit einbringen? Sind sind flexibel, l&ouml;sungsorientiert und verlieren auch in hektischen Zeiten weder den &Uuml;berblick noch die Geduld f&uuml;r die Detailpflege? Dann sind Sie bei uns genau richtig.</p>\r\n<p style=\"line-height: 13.2pt;\">F&uuml;r unser Team ben&ouml;tigen wir zurzeit noch:</p>\r\n<ul>\r\n    <li>Informatik Entwickler</li>\r\n</ul>\r\n<p style=\"line-height: 13.2pt;\"><span lang=\"DE\" style=\"font-size: 10pt;\">Sie besch&auml;ftigen sich intensiv mit der Weiterentwicklung <o:p></o:p>des&nbsp;</span> <a class=\"out\" href=\"http://www.contrexx.com/\">Contrexx&reg;</a> CMS. Sie schreiben neue Module oder passen die bestehende CMS Umgebung den Kundenw&uuml;nschen an. Sie arbeiten im Team und unterst&uuml;tzen sich gegenseitig.</p>\r\n<p class=\"MsoNormal\" style=\"margin: 0cm 0cm 0pt; background: white none repeat scroll 0% 50%; -moz-background-clip: -moz-initial; -moz-background-origin: -moz-initial; -moz-background-inline-policy: -moz-initial; line-height: 13.2pt;\"><span lang=\"DE\" style=\"font-size: 10pt; font-family: Arial;\"><o:p><br />\r\n</o:p></span>Sie haben Ihre&nbsp; Lehre oder Ihr Studium als Informatiker erfolgreich abgeschlossen und besitzen gute Kenntnisse in den Bereichen PHP und mySQL. Ausserdem haben Sie Freude am Entwickeln neuer Produkte und arbeiten gerne im Team.</p>\r\n<p>&nbsp;</p>','Thun','Vollzeit',1215727200,1,1,1,'0000-00-00','0000-00-00',1,1215781042);
