INSERT INTO `contrexx_module_news` (`id`, `date`, `title`, `text`, `redirect`, `source`, `url1`, `url2`, `catid`, `lang`, `userid`, `startdate`, `enddate`, `status`, `validated`, `frontend_access_id`, `backend_access_id`, `teaser_only`, `teaser_frames`, `teaser_text`, `teaser_show_link`, `teaser_image_path`, `teaser_image_thumbnail_path`, `changelog`) VALUES (1,1210002562,'Neue Webseite mit modernster Technologie online! ','Heute ist es elementar sich seinen Kunden im Internet nicht nur zu pr&auml;sentieren, sondern auch relevante Zusatznutzen zu bieten. Ein Schlagwort ist beispielsweise das &quot;Mitmach-Web&quot; Web 2.0 mit welchem der Kunde interaktiv in einen Dialog eingebunden wird.<br />\r\n<br />\r\nDieses Webprojekt ist nun mit modernster Technologie ausger&uuml;stet und trotzdem sehr einfach zu pflegen. Damit verf&uuml;gt dieser Auftritt &uuml;ber alle wesentlichen Funktionen, um den wachsenden Anforderungen auch k&uuml;nftig zu entsprechen. Nun k&ouml;nnen die Inhalte und Funktionalit&auml;ten mit einem kleinen Aufwand stets aktuell gehalten werden. Abonnieren Sie noch heute unseren Newsletter, RSS-Feeder oder besuchen Sie wieder unsere Webseite.<br />\r\n<br />','','http://www.contrexx.com','http://www.comvation.com','',1,1,1,'0000-00-00','0000-00-00',1,'1',0,0,'0','','Unsere neue Webseite ist online! Erstellt wurde das neue Portal mit <a href=\"http://www.contrexx.com/\" title=\"Contrexx WCMS\">Contrexx WCMS</a>.',1,'','',1236004502);
