INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`, `lang`) VALUES (423,15,'Besten Dank f&uuml;r Ihren Entscheid, <a href=\"http://www.contrexx.com\" target=\"_blank\">Contrexx&reg;</a> als Grundlage f&uuml;r Ihr Webprojekt einzusetzen.\r\n<h2>Erfolgreiche Installation von Contrexx&reg;</h2>\r\n<p><em>Contrexx&reg; Web Content Management System Version 2.0 ist </em>erfolgreich installiert worden. Nun ist <a href=\"http://www.contrexx.com\" target=\"_blank\">Contrexx&reg;</a> einsatzbereit. Sie  k&ouml;nnen jetzt die Inhalte erstellen und Ihr WebDesign integrieren. Mit der Installation von Contrexx&reg; akzeptieren Sie unsere <a target=\"_blank\" href=\"http://www.contrexx.com/de/index.php?page=1132\">Lizenzbedingungen</a>.</p>','Willkommen und Glückwunsch','','n',0,'on','system',0,'1');
INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`, `lang`) VALUES (528,16,'{SHOP_JAVASCRIPT_CODE}\r\n<div id=\"shop\">\r\n  <div class=\"menu\">{SHOP_MENU}</div>\r\n  <div class=\"cart_info\">{SHOP_CART_INFO}</div>\r\n<!-- BEGIN subCategoriesRow -->\r\n  <div class=\"categories\">\r\n    <h2>Kategorien</h2>\r\n<!-- BEGIN subCategories -->\r\n    <div class=\"category\">\r\n      <h3>{SHOP_PRODUCT_TITLE}</h3>\r\n      <a href=\"{SHOP_PRODUCT_DETAILLINK_IMAGE}\">\r\n        <img border=\"0\" alt=\"{TXT_SEE_LARGE_PICTURE}\" src=\"{SHOP_PRODUCT_THUMBNAIL}\" />\r\n      </a>\r\n    </div>\r\n<!-- END subCategories -->\r\n  </div>\r\n<!-- END subCategoriesRow -->\r\n  <div class=\"informations\"><h2>{TXT_PRODUCTS_IN_CATEGORY}&nbsp;{SHOP_CATEGORY_NAME}&nbsp;</h2></div>\r\n<!-- BEGIN shopProductRow -->\r\n  <div class=\"product {SHOP_ROWCLASS}\">\r\n    <form id=\"{SHOP_PRODUCT_FORM_NAME}\" name=\"{SHOP_PRODUCT_FORM_NAME}\"\r\n        action=\"index.php?section=shop&amp;cmd=cart\" method=\"post\">\r\n      <input type=\"hidden\" name=\"productId\" value=\"{SHOP_PRODUCT_ID}\" />\r\n      <div class=\"images\">\r\n<!-- BEGIN productImage1 -->\r\n        <a href=\"{SHOP_PRODUCT_THUMBNAIL_LINK_1}\">\r\n          <img border=\"0\" src=\"{SHOP_PRODUCT_THUMBNAIL_1}\" alt=\"{TXT_SEE_LARGE_PICTURE}\" />\r\n        </a><br />\r\n<!-- END productImage1 -->\r\n<!-- BEGIN productImage2 -->\r\n        <a href=\"{SHOP_PRODUCT_THUMBNAIL_LINK_2}\">\r\n          <img border=\"0\" src=\"{SHOP_PRODUCT_THUMBNAIL_2}\" alt=\"{TXT_SEE_LARGE_PICTURE}\" />\r\n        </a><br />\r\n<!-- END productImage2 -->\r\n<!-- BEGIN productImage3 -->\r\n        <a href=\"{SHOP_PRODUCT_THUMBNAIL_LINK_3}\">\r\n          <img border=\"0\" src=\"{SHOP_PRODUCT_THUMBNAIL_3}\" alt=\"{TXT_SEE_LARGE_PICTURE}\" />\r\n        </a><br />\r\n<!-- END productImage3 -->\r\n      </div>\r\n      <div class=\"description\">\r\n        <h2>{SHOP_PRODUCT_TITLE}</h2>\r\n        {SHOP_PRODUCT_DESCRIPTION}<br /><br />\r\n        <div class=\"detail_link\">{SHOP_PRODUCT_DETAILLINK}</div>\r\n<!-- BEGIN shopProductOptionsRow -->\r\n        <div class=\"shop_options\">{SHOP_PRODUCT_OPTIONS_TITLE}<br />\r\n          <div id=\"product_options_layer{SHOP_PRODUCT_ID}\" style=\"display: none;\">\r\n            <div class=\"shop_options_click\">\r\n              <br />\r\n<!-- BEGIN shopProductOptionsValuesRow -->\r\n              <strong>{SHOP_PRODUCT_OPTIONS_NAME}:</strong><br />{SHOP_PRODUCT_OPTION}<br />\r\n<!-- END shopProductOptionsValuesRow -->\r\n            </div>\r\n          </div>\r\n        </div>\r\n<!-- END shopProductOptionsRow -->\r\n        <br />\r\n<!-- BEGIN shopProductWeight -->\r\n        <div>{TXT_SHOP_PRODUCT_WEIGHT} {SHOP_PRODUCT_WEIGHT}</div>\r\n<!-- END shopProductWeight -->\r\n<!-- BEGIN shopProductExternal -->\r\n        <div>{SHOP_EXTERNAL_LINK}</div>\r\n<!-- END shopProductExternal -->\r\n      </div>\r\n      <br />\r\n      <div class=\"price\">\r\n        <strong>Preis: {SHOP_PRODUCT_DISCOUNTPRICE} {SHOP_PRODUCT_DISCOUNTPRICE_UNIT}\r\n          {SHOP_PRODUCT_PRICE} {SHOP_PRODUCT_PRICE_UNIT}\r\n        </strong><br />\r\n        {SHOP_PRODUCT_TAX_PREFIX}&nbsp;{SHOP_PRODUCT_TAX}\r\n      </div>\r\n      <div class=\"functions\">\r\n        <input type=\"submit\" onclick=\"{SHOP_PRODUCT_SUBMIT_FUNCTION}\"\r\n            name=\"{SHOP_PRODUCT_SUBMIT_NAME}\" value=\"{TXT_ADD_TO_CARD}\" />\r\n      </div>\r\n    </form>\r\n  </div>\r\n<!-- END shopProductRow -->\r\n  <div class=\"paging\">{SHOP_PRODUCT_PAGING}</div>\r\n</div>\r\n','Online Shop','','y',0,'on','system',50,'1');
INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`, `lang`) VALUES (202,0,'<p>Mit <a target=\"_blank\" href=\"http://www.contrexx.com\">Contrexx&reg; Web Content Management System (WCMS)</a> werden Webseiten f&uuml;r Internet, Extranet und Internet schnell, benutzerfreundlich und sicher erstellt. Die <a target=\"_blank\" href=\"http://www.contrexx.com/de/index.php?page=892\">standardisierten Module</a> von Contrexx&reg; erlauben ein rasches Umsetzen von individuellen Bed&uuml;rfnissen, wenn n&ouml;tig mit professioneller Unterst&uuml;tzung.<br />\r\nDas webbasierte System ist f&uuml;r die Verwaltung von flexiblen Internet- und Intranet L&ouml;sungen optimiert. Der modulare Aufbau und das Workflow System erm&ouml;glicht eine 100% Individualisierung der Logik und des Designs und f&ouml;rdert ein konsistentes und stetig wachsendes Wissensnetzwerk.<br />\r\n<br />\r\nDie Downloadversion beinhaltet einen Webinstaller, sowie verschiedene Module wie ein Linkverzeichnis, Podcast, Onlineschalter, Adressverzeichnis, Sitemap, Bildergalerie, Newsletter, G&auml;stebuch, RSS Feed Verzeichnis, News System, Abstimmung, Online Shop, Bannerverwaltung, Blockmodul und die M&ouml;glichkeiten f&uuml;r passwortgesch&uuml;tzte Bereiche.</p>\r\n<h2>Professioneller und innovativer Vertrieb</h2>\r\nContrexx&reg; wird weltweit &uuml;ber ein Partnernetzwerk vertrieben. Die <a target=\"_blank\" href=\"http://www.contrexx.com/de/index.php?page=106\">Contrexx&reg; Solutions Partner</a> sind autorisierte Partner welche ausgewiesene Kompetenzen im den Bereichen WebDesign, Grafik oder Internetmarketing besitzen. Damit erhalten Sie als Kunde professionelle Unterst&uuml;tzung mit hohen Kompetenzen aus Ihrer N&auml;he.','Contrexx® Web Content Management System (WCMS)','','n',201,'on','system',0,'1');
INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`, `lang`) VALUES (203,0,'In diesem Beitrag lernen Sie die ersten Schritte mit Contrexx&reg;. Wir w&uuml;nschen Ihnen viel Spass bei der Umsetzung und viel Erfolg bei der Umsetzung. F&uuml;r allf&auml;llige Fragen stehen Ihnen die folgenden <a href=\"index.php?page=2\">Mittel zur Verf&uuml;gung</a>.<br />\r\n<br />\r\n<h2>Einf&uuml;hrung</h2>\r\nDas Backend-System ist die Administrationskonsole. In diesem Bereich k&ouml;nnen Sie Contrexx administrieren, neue Seiten erstellen, Inhalte anpassen und vieles mehr. Das Frontend-System ist der Internetauftritt, welche Ihre Besucher erleben. Ein Themes ist das hinterlegte WebDesign.<br />\r\n<h2>Im Backend einloggen</h2>\r\n<p>Bitte folgen Sie dazu den folgenden Schritten:</p>\r\n<ul>\r\n    <li>Melden Sie sich im Contrexx an. Geben Sie dazu in der Adressenzeile im Browser Ihre Domain ein und f&uuml;gen Sie im Anschluss &quot;cadmin&quot; hinzu. Beispielsweise &quot;http://www.musterseite.eu/cadmin&quot;</li>\r\n    <li>Anschliessend erscheint die Anmeldemaske.</li>\r\n    <li>Geben Sie bitte die folgenden Daten ein:<br />\r\n    Sicherheitscode: Bitte wiederholen Sie den sichtbaren Code (dieser Code wird als Bild generiert und kann von Hackerwerkzeugen nicht erfasst werden)<br />\r\n    Benutzernamen: gem&auml;ss Angaben im Installer<br />\r\n    Pa&szlig;wort: gem&auml;ss Angaben im Installer</li>\r\n    <li>Empfehlung: vervollst&auml;ndigen Sie in einem ersten Schritt die Angaben in den Grundeinstellungen unter dem Navigationspunkt &quot;Administration&quot; auf der linken Seite.</li>\r\n</ul>\r\n<h2>Mit dem Content Manager arbeiten</h2>\r\n<p>Nach dem erfolgreichen Anmelden im Backend-Bereich k&ouml;nnen Sie Seiten erstellen. Um einen neuen Artikel anzulegen, w&auml;hlen Sie bitte die Funktion &quot;Content Management - Neue Seite&quot; auf der linken Seite. Danach definieren Sie die Artikeleigenschaften, insbesondere den Linknamen, Seitentitel und die Meta Angaben des Artikels (diese Angaben sind f&uuml;r Suchamschinen relevant). Mit Hilfe des WYSIWYG-Editors k&ouml;nnen Sie die Inhalte eingeben und speichern. Im Editor stehen Ihnen die aus der Textverarbeitung bekannten Funktionalit&auml;ten zur Verf&uuml;gung. Auch die Integration von Medien und Verlinkungen erfolgt &uuml;ber den Editor. Bitte verwenden Sie in der Formatierung des Textes die Formate aus dem Editor (Beispielsweise &Uuml;berschrift 1 oder Text). Wie die &Uuml;berschrift oder ein Text im Frontend aussieht, wird im Design und Layout definiert. Ein weiterer Zusatznutzen ist, dass Suchmaschinen eine definierte &Uuml;berschrift 1 (im HTML Code entspricht dies einem H1) besser indexieren weil &Uuml;berschirften aus der Sicht einer Suchmaschine relevanter sind.</p>\r\n<h2>Module und Themes - flexibel und erweiterbar</h2>\r\n<p>Der Aufbau von Contrexx basiert auf den folgenden Elementen:</p>\r\n<ul>\r\n    <li>Core System</li>\r\n    <li>Themes</li>\r\n    <li>Module</li>\r\n    <li>Inhalte</li>\r\n</ul>\r\n<p>Sogenannte Themes sind in XHTML programmiert und definieren das Erscheinungsbild der Site. Dabei werden im XHTML-Quelltext Platzhalter hinterlegt. Diese Platzhalter beinhalten weitere Dateien und Funktionalit&auml;ten, die sp&auml;ter auf der Website integriert werden.<br />\r\n<br />\r\nHinweis: Zur Zeit ist ein Portal f&uuml;r Contrexx&reg; - Themes in Vorbereitung und wird in K&uuml;rze erscheinen. Dies kann als Grundlage dienen, um WebDesign einfacher zu erstellen. <a href=\"http://www.contrexx.com/feed/news_headlines_de.xml\" target=\"_blank\">Abonnieren Sie sich gleich den Contrexx RSS - Feed</a>, damit Sie den Start dieses Portals nicht verpassen.</p>\r\n<p>Module sind in PHP programmiert und stellen die Funktionalit&auml;ten einer Website dar. Standardmodule sind die Darstellung von Texten und Navigation, Sitemap, Kontaktformulare, Suchen etc. Module bestehen aus einem Backend- und einem Frontend Bereich. Im Backendbereich kann der Administrator Einstellungen vornehmen, bspw. an welche e-Mail Adresse nach Ausf&uuml;llen eines Kontaktformulars eine Benachrichtigung versendet werden soll. Im Frontend Bereich wird die Ausgabe und Verarbeitung von Daten auf dem Onlinedienst gesteuert. Contrexx Templates f&uuml;hren Layouts und Module zusammen. Durch Auswahl eines Layouts im Template ist die Anzahl und Position von Platzhaltern vorbestimmt. F&uuml;r jeden dieser Container kann nun der Administrator ein Modul definieren, das dort ausgef&uuml;hrt werden soll.</p>','Ihre ersten Schritte mit dem Contrexx® WCMS','','n',201,'on','system',1,'1');
INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`, `lang`) VALUES (204,0,'Die Erfahrung zeigt, dass Contrexx zuverl&auml;ssig funktioniert. Sollten Sie trotzdem Unterst&uuml;tzung ben&ouml;tigen, stehen wir Ihnen mit den folgenden Instrumenten sehr gerne zur Verf&uuml;gung.<br />\r\n<ul>\r\n    <li><a target=\"_blank\" href=\"http://www.contrexx.com/wiki/de/\">Dokumentation</a>: Das Dokumentationsportal f&uuml;r Contrexx Anwender und Entwickler.</li>\r\n    <li><a target=\"_blank\" href=\"http://www.contrexx.com/forum/forumdisplay.php?f=68\">Contrexx - Forum</a>: Weiterf&uuml;hrende Unterst&uuml;tzung erhalten Sie von der Contrexx Community im Open Source Forum.</li>\r\n    <li><a target=\"_blank\" href=\"http://www.contrexx.com/bugtracker/\">Bugtracker</a>: Hier wird eine aktuelle Liste der bekannten Fehler, sowie der geplanten/gew&uuml;nschten Verbesserungen und Erweiterungen der Contrexx Software gef&uuml;hrt.</li>\r\n    <li><a href=\"http://support.comvation.com/index.php\" target=\"_blank\">Support - Ticket f&uuml;r Contrexx - Kunden</a>: Contrexx - Kunden k&ouml;nnen unter diesem Link ein Support-Ticket er&ouml;ffnen. Die Anfrage wird in das Ticketing-System geleitet und damit wird gew&auml;hrleistet, dass Ihre Anfrage rasch und zuverl&auml;ssig beantwortet wird. Nutzer der kostenlosen Opensource Version stehen die obigen M&ouml;glichkeiten zur Verf&uuml;gung.</li>\r\n</ul>','Contrexx Support','','n',201,'on','system',2,'1');
INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`, `lang`) VALUES (523,11,'<div id=\"sitemap\">\r\n<ul>\r\n	<!-- BEGIN sitemap -->\r\n	<li class=\"{STYLE}\">{SPACER}<a title=\"{NAME}\" href=\"{URL}\">{NAME}</a></li>\r\n	<!-- END sitemap -->\r\n</ul>\r\n</div>','Sitemap','','y',0,'off','system',555,'1');
