INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`, `lang`) VALUES (426,8,'<h3>Online Einbindung mittels RSS-Newsfeed</h3>\n<p><a href=\"{NEWS_RSS_FEED_URL}\" title=\"RSS Newsfeed von {NEWS_HOSTNAME}\" class=\"rssfeed\"><img width=\"48\" height=\"74\" border=\"0\" style=\"margin: 5px 20px 20px; float: right;\" src=\"images/content/icons/rss_bg.gif\" alt=\"Der Feed dieser Website kann &uuml;ber diesen Link aufgerufen werden\" /></a>RSS ist ein elektronisches Nachrichtenformat, das dem Nutzer erm&ouml;glicht, die Inhalte einer Website &ndash; oder Teile davon &ndash; als sogenannte RSS-Feeds zu abonnieren oder in andere Websites zu integrieren.</p>\n<h3>Online Einbindung mittels Javascript</h3>\nDer Feed dieser Website kann auch ganz einfach auf Ihrer Website dargestellt werden. Dazu m&uuml;ssen Sie lediglich den folgenden Code in Ihre eigene Webseite einf&uuml;gen:<br />\n<br />\n<form action=\"\">\n    <textarea style=\"width: 98%; font-size: 95%;\" rows=\"18\" cols=\"30\" name=\"code\">{NEWS_RSS2JS_CODE}</textarea>     <br />\n    <br />\n    <input type=\"button\" value=\"Alles markieren\" onclick=\"javascript:this.form.code.focus();this.form.code.select();\" name=\"button\" />\n</form>\n','News Feed','feed','n',424,'on','system',20,'1');
INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`, `lang`) VALUES (427,8,'<font color=\"#ff0000\">{NEWS_STATUS_MESSAGE}&nbsp;</font> \r\n<!-- BEGIN news_submit_form -->\r\n<form method=\"post\" action=\"index.php?section=news&amp;cmd=submit\">\r\n    <fieldset> \r\n    <legend>{TXT_NEWS_MESSAGE}</legend>\r\n    <p><label for=\"newsTitle\">{TXT_TITLE}</label><input id=\"newsTitle\" name=\"newsTitle\" value=\"{NEWS_TITLE}\" type=\"text\"/></p>\r\n    <p><label for=\"newsCat\">{TXT_CATEGORY}</label><select id=\"newsCat\" name=\"newsCat\"><!-- BEGIN news_category_menu --><option value=\"{NEWS_CATEGORY_ID}\">{NEWS_CATEGORY_TITLE}</option><!-- END news_category_menu --></select></p>\r\n    <p><label for=\"newsSource\">{TXT_EXTERNAL_SOURCE}</label><input id=\"newsSource\" name=\"newsSource\" value=\"{NEWS_SOURCE}\" type=\"text\"/></p>\r\n    <p><label for=\"newsUrl1\">{TXT_LINK} #1</label><input id=\"newsUrl1\" name=\"newsUrl1\" value=\"{NEWS_URL1}\" type=\"text\"/></p>\r\n    <p><label for=\"newsUrl2\">{TXT_LINK} #2</label><input id=\"newsUrl2\" name=\"newsUrl2\" value=\"{NEWS_URL2}\" type=\"text\"/></p>\r\n    <p><label for=\"newsTeaserText\">{TXT_NEWS_TEASER_TEXT} #2</label><textarea id=\"newsTeaserText\" name=\"newsTeaserText\">{NEWS_TEASER_TEXT}</textarea></p>\r\n    </fieldset> \r\n    <fieldset> \r\n    <legend>{TXT_NEWS_NEWS_CONTENT}</legend>\r\n    {TXT_NEWS_REDIRECT}<br /><br />\r\n    <p><label for=\"newsRedirect\">{TXT_NEWS_NEWS_URL}</label><input id=\"newsRedirect\" name=\"newsRedirect\" value=\"{NEWS_REDIRECT}\" type=\"text\" /></p>\r\n    {NEWS_TEXT}\r\n    <p><label>Captcha</label><img style=\"padding-top: 5px; padding-bottom: 5px;\" src=\"{IMAGE_URL}\" alt=\"{IMAGE_ALT}\" title=\"{IMAGE_ALT}\" /><br />{TXT_CAPTCHA}</p>\r\n    <p><input type=\"text\" name=\"captcha\" id=\"captcha\"/><input type=\"hidden\" name=\"offset\" value=\"{CAPTCHA_OFFSET}\" /></p>\r\n    <p><input type=\"reset\" onclick=\"FCKeditorAPI.GetInstance(\'newsText\').SetHTML(\'\')\" /> <input type=\"submit\" name=\"submitNews\" value=\"Anmelden\" /></p>\r\n    </fieldset> \r\n</form>\r\n<!-- END news_submit_form --> \r\n<!-- BEGIN news_submitted --> \r\n<a title=\"weitere Newsmeldung hinzuf&uuml;gen\" href=\"?section=news&amp;cmd=submit\">Klicken Sie hier um eine weitere Newsmeldung zu erfassen.</a> \r\n<!-- END news_submitted -->','Newsanmelden','submit','y',424,'on','system',30,'1');
INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`, `lang`) VALUES (445,21,'<div id=\"calendar\"></div>\r\n    <!-- BEGIN signForm -->\r\n    {CALENDAR_FIELD_JS}\r\n    <fieldset>\r\n        <legend>An- \\ Abmeldeformular</legend>\r\n        <form name=\"signForm\" id=\"signForm\" onsubmit=\"return checkFields();\" method=\"post\" action=\"index.php?section=calendar&amp;cmd=sign\">\r\n        <input type=\"hidden\" name=\"id\" value=\"{CALENDAR_NOTE_ID}\" />\r\n        <input type=\"hidden\" name=\"userid\" value=\"{CALENDAR_USER_ID}\" />\r\n            <p><label>An- \\ Abmeldung<font color=\"red\"> *</font></label><input type=\"radio\" name=\"type\" value=\"1\" checked=\"checked\" /><label class=\"description\">Anmeldung</label></p>\r\n            <p><input type=\"radio\" name=\"type\" value=\"0\" /><label class=\"description\">Abmeldung</label></p>\r\n            <!-- BEGIN calendarRegFields -->\r\n                <p><label>{CALENDAR_FIELD_NAME}</label>{CALENDAR_FIELD_INPUT}</p>\r\n            <!-- END calendarRegFields -->\r\n            <p><input type=\"submit\" name=\"submit\" value=\"Absenden\" /></p>\r\n        </form>\r\n    </fieldset>\r\n    <!-- END signForm -->\r\n    \r\n    <!-- BEGIN signStatus -->\r\n    {CALENDAR_REGISTRATIONS_STATUS}<br />\r\n    <br />\r\n    <a href=\"index.php?section=calendar\">{TXT_CALENDAR_BACK}</a>\r\n    <!-- END signStatus -->\r\n</div>','An- \\ Abmelden','sign','y',441,'off','system',99,'1');
