INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`, `lang`) VALUES (504,3,'<script language=\"javascript\" type=\"text/javascript\">\r\nShadowbox.loadSkin(\'classic\',\'lib/javascript/shadowbox/src/skin/\');\r\nShadowbox.loadLanguage(\'en\', \'lib/javascript/shadowbox/src/lang\');\r\nShadowbox.loadPlayer([\'flv\', \'html\', \'iframe\', \'img\', \'qt\', \'swf\', \'wmp\'], \'lib/javascript/shadowbox/src/player\');\r\n</script>\r\n\r\n<script language=\"javascript\" type=\"text/javascript\">\r\nwindow.onload = function(){\r\n    Shadowbox.init();\r\n};\r\n</script>\r\n\r\n<div id=\"gallery\">\r\n    <!-- BEGIN galleryCategories -->\r\n    <div class=\"category_tree\">Sie sind hier: {GALLERY_CATEGORY_TREE}</div>\r\n    \r\n    <!-- BEGIN galleryCategoryList -->\r\n    <div class=\"category row_{GALLERY_STYLE}\">\r\n        <div class=\"category_image\">{GALLERY_CATEGORY_IMAGE}</div>\r\n        <div class=\"category_informations\">\r\n            <h2>{GALLERY_CATEGORY_NAME}</h2>\r\n            {GALLERY_CATEGORY_DESCRIPTION}<br />\r\n            <!-- <br />\r\n            {GALLERY_CATEGORY_INFO} -->\r\n        </div>\r\n    </div>\r\n    <!-- END galleryCategoryList --> 	\r\n    \r\n    <!-- BEGIN galleryImageBlock -->  	\r\n    {GALLERY_JAVASCRIPT}\r\n    <div class=\"category_comment\">{GALLERY_CATEGORY_COMMENT}</div>\r\n    <!-- BEGIN galleryShowImages -->\r\n    <div class=\"category_images\">\r\n        <div class=\"image\">{GALLERY_IMAGE1}<br />{GALLERY_IMAGE_LINK1}</div>\r\n        <div class=\"image\">{GALLERY_IMAGE2}<br />{GALLERY_IMAGE_LINK2}</div>\r\n        <div class=\"image\">{GALLERY_IMAGE3}<br />{GALLERY_IMAGE_LINK3}</div>\r\n    </div>\r\n    <!-- END galleryShowImages -->\r\n\r\n    <div class=\"category_paging\">{GALLERY_PAGING}</div>\r\n    <!-- END galleryImageBlock --> \r\n     	\r\n    <!-- END galleryCategories -->   	\r\n    \r\n    <!-- BEGIN ga