INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`, `lang`) VALUES (439,47,'Eine <strong>Tag Cloud</strong> ist eine Methode zur Informationsvisualisierung, bei der eine Liste aus Schlagworten zweidimensional alphabetisch sortiert angezeigt wird, wobei einzelne unterschiedlich gewichtete Worte gr&ouml;sser oder auf andere Weise hervorgehoben dargestellt werden. Wortwolken werden zunehmend beim gemeinschaftlichen Indexieren und in Blogs eingesetzt. [<a target=\"_blank\" href=\"http://de.wikipedia.org/wiki/Tag_cloud\">Quelle</a>]\r\n<br /><br /><fieldset><legend>Beliebteste Tags</legend>{BLOG_TAG_HITLIST}</fieldset>\r\n<fieldset><legend>Tag Cloud</h2>{BLOG_TAG_CLOUD}</fieldset>','Tag Cloud','cloud','y',437,'on','system',1,'1');
INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`, `lang`) VALUES (260,0,'<div id=\"blog\">\r\n	<div class=\"text\">Das Contrexx Blog-Modul erzeugt, falls in den Moduleinstellungen aktiviert, automatisch RSS-Feeds von Ihren Meldungen und den dazu verfassten Kommentaren. Dabei wird pro aktivierte Systemsprache im Ordner <strong>feed/</strong> eine eigene XML-Datei nach folgendem Schema erstellt:</div>\r\n	<div class=\"code\">\r\n<pre>blog_messages_XX.xml\r\nblog_comments_XX.xml\r\nblog_category_ID_XX.xml</pre>\r\n	</div>\r\n	<div class=\"text\">Dabei steht der Platzhalter XX f&uuml;r die Kurzform der jeweiligen Sprache und ID f&uuml;r die eindeutige Bezeichnungs-ID der jeweiligen Kategorie. F&uuml;r die Sprache Deutsch (Kurzform: de) lauten der komplette Link beispielsweise wie folgt:</div>\r\n	<div class=\"code\">\r\n<pre>http://www.Ihre-Website.ch/feed/blog_messages_de.xml\r\nhttp://www.Ihre-Website.ch/feed/blog_comments_de.xml</pre>\r\n	</div>\r\n	<div class=\"text\">F&uuml;r die Kategorie 1 (ID = 1) in der Sprache deutsch w&uuml;rde der Pfad demnach wie folgt lauten:</div>\r\n	<div class=\"code\">\r\n<pre>http://www.Ihre-Website.ch/feed/blog_category_1_de.xml</pre>\r\n	</div>\r\n	<div class=\"text\">Eine komplette &Uuml;bersicht aller Kurzformen finden Sie in Ihrem Administrationsbereich unter <strong>Administration &gt; Spracheinstellungen</strong>.</div>\r\n</div>','RSS-Feed','','y',257,'on','system',3,'1');
