INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`, `lang`) VALUES (483,33,'<script type=\"text/javascript\">\r\n<!--\r\nfunction toggle(target){\r\n    obj = document.getElementById(target);\r\n    obj.style.display = (obj.style.display==\'none\') ? \'inline\' : \'none\';  \r\n    if (obj.style.display==\'none\' && target == \'hiddenSearch\'){\r\n         document.getElementById(\'searchCheck\').value = \'norm\';\r\n    }else if(obj.style.display==\'inline\' && target == \'hiddenSearch\'){\r\n         document.getElementById(\'searchCheck\').value = \'exp\';\r\n    }\r\n}\r\n\r\nfunction CheckForm() {\r\n  var errorMsg = \"\";\r\n  with( document.sendMessage) {\r\n    if (title.value == \"\") {\r\n    	errorMsg = errorMsg + \"- {TXT_MARKET_TITLE} <n\";\r\n    }\r\n    if (message.value == \"\") {\r\n        errorMsg = errorMsg + \"- {TXT_MARKET_MSG}\\n\";\r\n    } \r\n    if (email.value == \"\") {\r\n        errorMsg = errorMsg + \"- {TXT_MARKET_EMAIL}\\n\";\r\n    }else{\r\n    	if (!matchType(\'^[_a-zA-Z0-9-]+(.[_a-zA-Z0-9-]+)*@[a-zA-Z0-9-]+(.[a-zA-Z0-9-]+)*.(([0-9]{1,3})|([a-zA-Z]{2,3})|(aero|coop|info|museum|name))$\', email.value)){\r\n    		errorMsg = errorMsg + \"- n\";\r\n    	}\r\n    }\r\n\r\n    if (errorMsg != \"\") \r\n    {\r\n        alert (\"{TXT_FIELDS_REQUIRED}n{TXT_THOSE_FIELDS_ARE_EMPTY}\\n\" + errorMsg);       \r\n        return false;\r\n    }else{\r\n    	return true;\r\n    }\r\n  }\r\n}\r\n\r\n\r\nfunction matchType(pattern, value) {\r\n	var reg = new RegExp(pattern);\r\n	if (value.match(reg)) {\r\n		return true;\r\n	}\r\n	return false;\r\n} \r\n-->\r\n</script>\r\n<div id=\"market\">\r\n	<div class=\"search\">\r\n		<form action=\"index.php?\" method=\"get\" name=\"marketSearch\" id=\"marketSearch\">\r\n			<input name=\"term\" value=\"{TXT_MARKET_SEARCHTERM}\" size=\"25\" maxlength=\"100\" /><input type=\"hidden\" id=\"searchCheck\" name=