INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`, `lang`) VALUES (546,56,'<div id=\"knowledge\">\r\n<script type=\'text/javascript\'>\r\n/* <![CDATA[ */\r\n\r\nvar baseOptions = {\r\n	titleRowPrefix : \'title_row_\', \r\n	className : \'question_active\', \r\n	openedIcon : \'modules/knowledge/frontend/close.png\', \r\n	closedIcon : \'modules/knowledge/frontend/open.png\',\r\n	divPrefix : \'answer_\',\r\n	slideAll : true,\r\n	imgPrefix : \'img_\'\r\n};\r\n\r\nfunction submitSearch(obj)\r\n{\r\n	var searchinput = $(\'searchinput\');\r\n	var val = searchinput.value;\r\n	$(\'searchHidden\').value = val;\r\n	searchinput.value = \"\";\r\n	searchinput.name = \"\";\r\n	return true;\r\n}\r\n\r\n\r\n\r\n/* ]]> */\r\n</script>\r\n\r\n<div id=\"content_search\"  style=\"margin-bottom: 2em;\">\r\n	<div id=\"search_bg\">\r\n		<script type=\"text/javascript\" src=\"modules/knowledge/frontend/search.js\"></script>\r\n		<form action=\"index.php?section=knowledge&amp;cmd=search\" method=\"get\" id=\"form\" onsubmit=\"return submitSearch(this)\">\r\n			<div>\r\n				<input type=\"text\" class=\"searchinput_search\" maxlength=\"100\" value=\" {TXT_SEARCH_INPUT}\" \r\n					 onfocus=\"this.value=\'\'\" id=\"searchinput\" name=\"{SEARCH_HASH}\"/>\r\n				<input type=\"hidden\" name=\"section\" value=\"knowledge\" />\r\n				<input type=\"hidden\" name=\"cmd\" value=\"search\" />\r\n				<input type=\"hidden\" name=\"term\" value=\"\" id=\"searchHidden\" />\r\n				<input type=\"submit\" value=\"{TXT_SEARCH}\" class=\"search_button\" />\r\n			</div>\r\n		</form>\r\n	</div>    \r\n	<div id=\"resultbox\" style=\"display: none;\">\r\n	</div>\r\n	<script type=\"text/javascript\">\r\n	/* <![CDATA[ */\r\n	Search.assign(\'searchinput\', \'resultbox\'); \r\n	/* ]]> */\r\n	</script>\r\n</div>\r\n\r\n<h2 class=\"article_list_title\">{TXT_ARTICLELIST}</h2>\r\n<ul class=\"articlelist\">\r\n	<!-- BEGIN article -->\r\n		<li class=\"clearfix\">\r\n			<div class=\"title_row clearfix\" id=\"title_row_{ARTICLE_ID}{COUNTER}\">\r\n				<div class=\"opener\">\r\n					<a href=\"javascript: void(0);\" onclick=\"slider_{ARTICLE_ID}{COUNTER}.toggle();\">\r\n						<img src=\"modules/knowledge/frontend/open.png\" style=\"border: none;\" id=\"img_{ARTICLE_ID}{COUNTER}\" />\r\n					</a>\r\n				</div>\r\n				<div class=\"question\"><div><a href=\"index.php?section=knowledge{MODULE_INDEX}&cmd=article&id={ARTICLE_ID}\">{QUESTION}</a></div></div>\r\n			</div>\r\n			<div class=\"answer\" id=\"answer_{ARTICLE_ID}{COUNTER}\" style=\"display: none;\">\r\n			{ANSWER}<br /><br />\r\n			{TXT_RATING}: <span id=\"rating_elem_{ARTICLE_ID}{COUNTER}\">&nbsp;</span><br /><br />\r\n  				<!-- BEGIN tags -->\r\n				<div style=\"padding-bottom: 5px;\">Stichworte:\r\n				   <ul class=\"tags\"> 	<!-- BEGIN tag -->\r\n				   <li><a href=\"index.php?section=knowledge{MODULE_INDEX}&amp;tid={TAGID}\">{TAG}</a>,</li>\r\n				   <!-- END tag --> 	<!-- BEGIN lasttag -->\r\n				   <li><a href=\"index.php?section=knowledge{MODULE_INDEX}&amp;tid={TAGID}\">{TAG}</a></li>\r\n				<!-- END lasttag --> </ul>\r\n				</div>\r\n				<!-- END tags -->\r\n				\r\n				Erstellt: {DATE_CREATED} letzte Änderung: {DATE_UPDATED}\r\n			</div>\r\n			<script type=\"text/javascript\">\r\n			/* <![CDATA[ */\r\n			\r\n			var sliderOp = baseOptions;\r\n			baseOptions.counter = \'{COUNTER}\';\r\n			slider_{ARTICLE_ID}{COUNTER} = new Slider(\"{ARTICLE_ID}\", sliderOp);\r\n			delete sliderOp;\r\n			\r\n			\r\n			rating_{ARTICLE_ID}{COUNTER} = new Rating({ARTICLE_ID}{COUNTER}, {AVERAGE}, { \r\n			stars : {MAX_RATING}, \r\n			starPath : \'images/modules/knowledge/\',\r\n			elemPrefix : \'rating_elem_\',\r\n			onRate : function(data, rating) {\r\n				var foo = new Ajax.Request(\'index.php?section=knowledge&act=rate\', {\r\n					method: \"post\",\r\n					parameters : {\r\n						id : data.nr,\r\n						rated : rating\r\n					}\r\n				});\r\n			},\