INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`, `lang`) VALUES (369,54,'<script language=\"JavaScript\" src=\"cadmin/javascript/set_checkboxes.js\" type=\"text/javascript\"></script> <script language=\"Javascript\" type=\"text/javascript\">\r\nfunction selectMultiAction() {\r\n\r\n	with (document.frmShowNotificationEntries) {\r\n		switch (frmShowEntries_MultiAction.value) {\r\n			case \'delete\':\r\n				if (confirm(\'{TXT_ENTRIES_SUBMIT_DELETE_JS}\')) {\r\n					action=\'index.php?section=u2u&cmd=notification\';\r\n					submit();\r\n				}\r\n				else{\r\n                frmShowEntries_MultiAction.value=0;\r\n                }\r\n			break;\r\n			default: //do nothing\r\n		}\r\n	}\r\n}\r\n</script>\r\n<div class=\"Entries\">\r\n<form method=\"post\" name=\"frmShowNotificationEntries\">\r\n    <table width=\"70%\" cellspacing=\"0\" cellpadding=\"0\">\r\n        <tbody>\r\n            <!-- BEGIN noEntries -->\r\n            <tr>\r\n                <td colspan=\"5\">{PRIVATE_MESSAGE_NO_ENTRIES}</td>\r\n            </tr>\r\n            <!-- END noEntries -->\r\n            <tr>\r\n                <td>&nbsp;</td>\r\n            </tr>\r\n            <!-- BEGIN headSection -->\r\n            <tr>\r\n                <td colspan=\"5\" style=\"color: #029202;\">{PRIVATE_MESSAGE_DELETE_SUCCESS}</td>\r\n            </tr>\r\n            <tr>\r\n                <td colspan=\"5\">{TXT_NOTIFICATION_MESSAGE}</td>\r\n            </tr>\r\n            <tr>\r\n                <td>#</td>\r\n                <td width=\"500\">\r\n                <table width=\"500\" border=\"0\">\r\n                    <tbody>\r\n                        <tr>\r\n                            <td align=\"left\"><b>Betreff</b></td>\r\n                            <td align=\"right\"><b>Datum</b></td>\r\n                        </tr>\r\n                        <tr>\r\n                            <td align=\"lef