tion();\">\r\n                <option value=\"0\">{TXT_ENTRIES_SUBMIT_SELECT}</option>\r\n                <option value=\"delete\">{TXT_ENTRIES_SUBMIT_DELETE}</option>\r\n                </select></td>\r\n            </tr>\r\n        </tbody>\r\n    </table>\r\n    <!-- END DeleteSection -->\r\n</form>\r\n</div>\r\n<br />\r\n{NOTIFICATION_PAGING}','Benachrichtigung','notification','n',368,'off','system',1,'1');
INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`, `lang`) VALUES (370,54,'<script>\r\nfunction check()\r\n{\r\n  if(document.fwd_or_rep.check1.checked)\r\n{\r\n   return true;\r\n}\r\nelse\r\n{\r\n  return false;\r\n}\r\n  \r\n}\r\n</script>\r\n<form method=\"post\" name=\"fwd_or_rep\" action=\"index.php?section=u2u&amp;act=forwardandreply&amp;msg_id={PRIVATE_MESSAGE_ID}\">\r\n    <table width=\"75%\" cellspacing=\"0\" cellpadding=\"0\" border=\"0\">\r\n        <tbody>\r\n            <tr>\r\n                <td><strong>{TXT_PRIVATE_MESSAGE}</strong></td>\r\n            </tr>\r\n            <tr>\r\n                <td align=\"right\"><strong>{TXT_U2U_MESSAGE_SENT_DATE}:</strong>{MESSAGE_SENT_DATE}</td>\r\n            </tr>\r\n            <tr>\r\n                <td width=\"700\">\r\n                <table width=\"700\" cellspacing=\"0\" cellpadding=\"0\" border=\"0\">\r\n                    <tbody>\r\n                        <tr>\r\n                            <td width=\"350\"><strong>{TXT_U2U_AUTHOR}</strong>: {MESSAGE_AUTHOR_NAME}</td>\r\n                            <td width=\"350\" align=\"right\"><strong>{TXT_U2U_USER_JOINED_DATE}:</strong>{U2U_USER_JOINED}</td>\r\n                        </tr>\r\n                    </tbody>\r\n                </table>\r\n                </td>\r\n            </tr>\r\n            <tr>\r\n                <td><strong>{PRIVATE_MESSAGE_TITLE}</strong></td>\r\n            </tr>\r\n            <tr>\r\n                <td><hr />\r\n                </td>\r\n            </tr>\r\n            <tr>\r\n                <td>{PRIVATE_MESSAGE_TEXT}</td>\r\n            </tr>\r\n            <tr>\r\n                <td>\r\n                <table cellspacing=\"0\" cellpadding=\"0\" border=\"0\">\r\n                    <tbody>\r\n                        <tr>\r\n                            <td>&nbsp;</td>\r\n                            <td align=\