INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`, `lang`) VALUES (418,58,'<script type=\"text/javascript\">\r\n<!--\r\nfunction toggle(target){\r\n    obj = document.getElementById(target);\r\n    obj.style.display = (obj.style.display==\'none\') ? \'inline\' : \'none\';  \r\n    if (obj.style.display==\'none\' && target == \'hiddenSearch\'){\r\n         document.getElementById(\'searchCheck\').value = \'norm\';\r\n    }else if(obj.style.display==\'inline\' && target == \'hiddenSearch\'){\r\n         document.getElementById(\'searchCheck\').value = \'exp\';\r\n    }\r\n}\r\n\r\nfunction CheckForm() {\r\n  var errorMsg = \"\";\r\n  with( document.formEntry ) {\r\n  	if (type.value == \"\") {\r\n    	errorMsg = errorMsg + \"- {TXT_AUCTION_TYPE}\\n\";\r\n    }\r\n    if (title.value == \"\") {\r\n        errorMsg = errorMsg + \"- {TXT_AUCTION_TITLE_ENTRY}\\n\";\r\n    } \r\n    if (description.value == \"\") {\r\n        errorMsg = errorMsg + \"- {TXT_AUCTION_DESCRIPTION}\\n\";\r\n    }   \r\n    if (cat.value == \"\") {\r\n        errorMsg = errorMsg + \"- {TXT_AUCTION_CATEGORIE}\\n\";\r\n    } \r\n    if (userdetails.value == \"\") {\r\n        errorMsg = errorMsg + \"- {TXT_AUCTION_USER_DETAIL}\\n\";\r\n    }\r\n    if (name.value == \"\") {\r\n        errorMsg = errorMsg + \"- {TXT_AUCTION_NAME}\\n\";\r\n    }\r\n    if (email.value == \"\") {\r\n        errorMsg = errorMsg + \"- {TXT_AUCTION_EMAIL}\\n\";\r\n    }else{\r\n    	if (!matchType(\'^[_a-zA-Z0-9-]+(.[_a-zA-Z0-9-]+)*@[a-zA-Z0-9-]+(.[a-zA-Z0-9-]+)*.(([0-9]{1,3})|([a-zA-Z]{2,3})|(aero|coop|info|museum|name))$\', email.value)){\r\n    		errorMsg = errorMsg + \"- {TXT_AUCTION_EMAIL}\\n\";\r\n    	}\r\n    }\r\n    \r\n    if (errorMsg != \"\") \r\n    {\r\n        alert (\"{TXT_AUCTION_FIELDS_REQUIRED}n{TXT_AUCTION_THOSE_FIELDS_ARE_EMPTY}\\n\" + errorMsg);       \r\n        return false;\r\n    }else{\r\n    	return true;\r\n    }\r\n  }\r\n}\r\n\r\nfunction matchType(pattern, value) {\r\n	var reg = new RegExp(pattern);\r\n	if (value.match(reg)) {\r\n		return true;\r\n	}\r\n	return false;\r\n} \r\n\r\n-->\r\n</script>\r\n<div id=\"auction\">\r\n	<form name=\"formEntry\" enctype=\"multipart/form-data\" method=\"post\" action=\"?section=auction&cmd=edit\" onsubmit=\"return CheckForm()\">\r\n	<input type=\"hidden\" name=\"id\" value=\"{AUCTION_ENTRY_ID}\" >\r\n	<input type=\"hidden\" name=\"picOld\" value=\"{AUCTION_ENTRY_PICTURE_OLD}\" >\r\n		<table width=\"100%\" cellspacing=\"0\" cellpadding=\"2\" border=\"0\" align=\"top\" class=\"adminlist\">\r\n			<tr>\r\n				<td nowrap=\"nowrap\">{TXT_AUCTION_TYPE}<font color=\"red\"> *</font></td>\r\n				<td>\r\n					<input type=\"radio\" name=\"type\" value=\"offer\" {AUCTION_ENTRY_TYPE_OFFER} />&nbsp;{TXT_AUCTION_OFFER}\r\n					<input type=\"radio\" name=\"type\" value=\"search\" {AUCTION_ENTRY_TYPE_SEARCH} />&nbsp;{TXT_AUCTION_SEARCH}\r\n				</td>\r\n			</tr>\r\n			<tr valign=\"top\">\r\n				<td nowrap=\"nowrap\" width=\"120\">{TXT_AUCTION_TITLE_ENTRY}<font color=\"red\"> *</font></td>		\r\n				<td><input type=\"text\" name=\"title\" style=\"width: 300px;\" maxlength=\"100\" value=\"{AUCTION_ENTRY_TITLE}\" ></td>\r\n			</td>		\r\n			</tr>\r\n			<tr valign=\"top\">\r\n				<td nowrap=\"nowrap\" width=\"120\">{TXT_AUCTION_DESCRIPTION}<font color=\"red\"> *</font></td>\r\n				<td><textarea name=\"description\" rows=\"5\"  style=\"width: 300px; overflow: auto;\">{AUCTION_ENTRY_DESCRIPTION}</textarea></td>\r\n			</tr>\r\n			<tr valign=\"top\">\r\n				<td nowrap=\"nowrap\" width=\"120\">{TXT_AUCTION_PICTURE}</td>		\r\n				<td>{AUCTION_ENTRY_PICTURE}<input type=\"file\" name=\"pic\" size=\"38\" style=\"width: 304px;\" maxlength=\"100\"></td>\r\n			</td>	\r\n			<tr valign=\"top\">\r\n				<td nowrap=\"nowrap\" width=\"120\">{TXT_AUCTION_CATEGORIE}<font color=\"red\"> *</font></td>		\r\n				<td><select name=\"cat\"  style=\"width: 305px;\">{AUCTION_CATEGORIES}</select></td>\r\n			</td>	\r\n			<tr valign=\"top\">\r\n				<td nowrap=\"nowrap\" width=\"120\">{TXT_AUCTION_PRICE}</td>		\r\n				<td><input type=\"text\" name=\"price\" style=\"width: 98px;\" maxlength=\"100\" value=\"{AUCTION_ENTRY_PRICE}\">&nbsp;&nbsp;<input type=\"checkbox\" name=\"forfree\" value=\"1\"  {AUCTION_ENTRY_FORFREE} />&nbsp;{TXT_AUCTION_FOR_FREE}&nbsp;<input type=\"checkbox\" name=\"agreement\" value=\"1\" {AUCTION_ENTRY_AGREEMENT} />&nbsp;{TXT_AUCTION_AGREEMENT}</td>\r\n			</td>\r\n			<tr valign=\"top\">\r\n				<td nowrap=\"nowrap\" width=\"120\">{TXT_AUCTION_NAME}<font color=\"red\"> *</font></td>		\r\n				<td><input type=\"text\" name=\"name\" style=\"width: 300px;\" maxlength=\"100\" value=\"{AUCTION_ENTRY_NAME}\"></td>\r\n			</td>\r\n			<tr valign=\"top\">\r\n				<td nowrap=\"nowrap\" width=\"120\">{TXT_AUCTION_EMAIL}<font color=\"red\"> *</font></td>		\r\n				<td><input type=\"text\" name=\"email\" style=\"width: 300px;\" maxlength=\"100\" value=\"{AUCTION_ENTRY_EMAIL}\"></td>\r\n			</td>\r\n			<tr valign=\"top\">\r\n				<td nowrap=\"nowrap\" width=\"120\">{TXT_AUCTION_USER_DETAIL}<font color=\"red\"> *</font></td>		\r\n				<td>\r\n					<input type=\"radio\" name=\"userdetails\" value=\"1\" {AUCTION_ENTRY_USERDETAILS_ON} />&nbsp;{TXT_AUCTION_DETAIL_SHOW}\r\n					<input type=\"radio\" name=\"userdetails\" value=\"0\" {AUCTION_ENTRY_USERDETAILS_OFF} />&nbsp;{TXT_AUCTION_DETAIL_HIDE}\r\n				</td>\r\n			</td>\r\n					<!-- BEGIN spez_fields -->\r\n			<tr valign=\"top\" >\r\n				<td nowrap=\"nowrap\" width=\"120\">{TXT_AUCTION_SPEZ_FIELD_NAME}</td>\r\n				<td>{AUCTION_SPEZ_FIELD_INPUT}</td>\r\n			</td>\r\n			<!-- END spez_fields -->\r\n			<tr valign=\"top\">\r\n				<td nowrap=\"nowrap\" width=\"120\"><br /></td>		\r\n				<td>\r\n					<input type=\"submit\" name=\"submitEntry\" value=\"{TXT_AUCTION_SAVE}\" />\r\n				</td>\r\n			</td>\r\n	   </table>\r\n	</form>\r\n</div>','Auktion editieren','edit','y',415,'off','system',99,'1');
INSERT INTO `contrexx_module_repository` (`id`, `moduleid`, `content`, `title`, `cmd`, `expertmode`, `parid`, `displaystatus`, `username`, `displayorder`, `lang`) VALUES (419,58,'<script type=\"text/javascript\">\r\n<!--\r\nfunction toggle(target){\r\n    obj = document.getElementById(target);\r\n    obj.style.display = (obj.style.display==\'none\') ? \'inline\' : \'none\';  \r\n    if (obj.style.display==\'none\' && target == \'hiddenSearch\'){\r\n         document.getElementById(\'searchCheck\').value = \'norm\';\r\n    }else if(obj.style.display==\'inline\' && target == \'hiddenSearch\'){\r\n         document.getElementById(\'searchCheck\').value = \'exp\';\r\n    }\r\n}\r\n\r\nfunction CheckForm() {\r\n  var errorMsg = \"\";\r\n  with( document.sendMessage) {\r\n    if (title.value == \"\") {\r\n    	errorMsg = errorMsg + \"- {TXT_AUCTION_TITLE} <n\";\r\n    }\r\n    if (message.value == \"\") {\r\n        errorMsg = errorMsg + \"- {TXT_AUCTION_MSG}\\n\";\r\n    } \r\n    if (email.value == \"\") {\r\n        errorMsg = errorMsg + \"- {TXT_AUCTION_EMAIL}\\n\";\r\n    }else{\r\n    	if (!matchType(\'^[_a-zA-Z0-9-]+(.[_a-zA-Z0-9-]+)*@[a-zA-Z0-9-]+(.[a-zA-Z0-9-]+)*.(([0-9]{1,3})|([a-zA-Z]{2,3})|(aero|coop|info|museum|name))$\', email.value)){\r\n    		errorMsg = errorMsg + \"- n\";\r\n    	}\r\n    }\r\n\r\n    if (errorMsg != \"\") \r\n    {\r\n        alert (\"{TXT_FIELDS_REQUIRED}n{TXT_THOSE_FIELDS_ARE_EMPTY}\\n\" + errorMsg);       \r\n        return false;\r\n    }else{\r\n    	return true;\r\n    }\r\n  }\r\n}\r\n\r\n\r\nfunction matchType(pattern, value) {\r\n	var reg = new RegExp(pattern);\r\n	if (value.match(reg)) {\r\n		return true;\r\n	}\r\n	return false;\r\n} \r\n\r\nfunction update_countdown() {\r\n	new Ajax.PeriodicalUpdater(\'countdown\', \'modules/auction/lib/counter.class.php?mode=countdown&id={AUCTION_ID}\', {method: \'get\', frequency: 1, showProgress: 0});\r\n}\r\n\r\nfunction QuestionDiv(loged){\r\n	if(loged==1){\r\n		if(document.getElementById(\"question_div\") && document.getElementById(\"question_div\").style.display != \'block\'){\r\n			document.getElementById(\"question_div\").style.display = \'block\';\r\n		}else{\r\n			document.getElementById(\"question_div\").style.display = \'none\';\r\n		}\r\n	}else{\r\n		alert(\"{TXT_AUCTION_NOT_LOGED_ERROR}\");\r\n	}\r\n}\r\n\r\nfunction SendQuestion(){\r\n	objFrm = document.frmQuestion;\r\n	if(objFrm.Name.value==\'\' || objFrm.Email.value==\'\' || objFrm.Frage.value==\'\'){\r\n		alert(\"{TXT_AUCTION_ADD_ALL_FIELDS}\");\r\n	}else{\r\n		objFrm.submit();\r\n	}\r\n}\r\n\r\nfunction MakeOffer(){\r\n	objFrm = document.frmMakeOffer;\r\n	if(objFrm.loged.value!=1){\r\n		alert(\"{TXT_AUCTION_NOT_LOGED_ERROR}\");\r\n	}else{\r\n		objFrm.submit();\r\n	}\r\n}\r\n-->\r\n</script>\r\n<div id=\"auction\">\r\n	<div class=\"search\">\r\n		<h1>&nbsp;{TXT_AUCTION_SEARCH_TITLE}</h1>\r\n		<form action=\"index.php?\" method=\"get\" name=\"auctionSearch\" id=\"auctionSearch\">\r\n			<input name=\"term\" value=\"{TXT_AUCTION_SEARCHTERM}\" size=\"25\" maxlength=\"100\" /><input type=\"hidden\" id=\"searchCheck\" name=\"check\" value=\"norm\" size=\"10\" /><input type=\"hidden\" name=\"section\" value=\"auction\" size=\"10\" /><input type=\"hidden\" name=\"cmd\" value=\"search\" size=\"10\" />&nbsp;<input type=\"submit\" value=\"{TXT_AUCTION_SEARCH}\" name=\"search\" /> \r\n			\r\n		</form>\r\n	</div>\r\n	<br />\r\n	<div class=\"navtree\"><a href=\"?section=auction\">{TXT_AUCTION}</a>{AUCTION_CATEGORY_NAVI}</div>\r\n	<br />\r\n	<div class=\"detail\">\r\n	\r\n		<h1>&nbsp;{AUCTION_TITLE}</h1>\r\n		\r\n		<table cellpadding=\"2\" cellspacing=\"0\" border=\"0\">\r\n			<tr>\r\n				<td valign=\"top\" width=\"200\">{AUCTION_TMBS}</td>\r\n				<td valign=\"top\">\r\n					<table cellpadding=\"2\" cellspacing=\"0\" border=\"0\">\r\n						<tr>\r\n							<td width=\"150\"><b>{TXT_AUCTION_ID}</b></td>\r\n							<td>{AUCTION_ID}</td>\r\n						</tr>\r\n						<tr>\r\n							<td><b>{TXT_AUCTION_ENDTIME}</b></td>\r\n							<td>{AUCTION_ENDTIME}</td>\r\n						</tr>\r\n						<tr>\r\n							<td><b>{TXT_AUCTION_NR_BIDS}</b></td>\r\n							<td>{AUCTION_NR_BIDS}</td>\r\n						</tr>\r\n					</table>\r\n					<br />\r\n					<table cellpadding=\"2\" cellspacing=\"0\" border=\"0\">\r\n						<tr>\r\n							<td width=\"150\"><b>{TXT_AUCTION_STARTPRICE}</b></td>\r\n							<td>{AUCTION_STARTPRICE}</td>\r\n						</tr>\r\n						<tr>\r\n							<td><b>{TXT_AUCTION_INCREASE_STEP}</b></td>\r\n							<td>{AUCTION_INCREASE_STEP}</td>\r\n						</tr>\r\n						<tr>\r\n							<td><b>{TXT_AUCTION_NEXT_BID}</b></td>\r\n							<td>{AUCTION_NEXT_BID}</td>\r\n						</tr>\r\n						<tr>\r\n							<td><b>{TXT_AUCTION_DURATION}</b></td>\r\n							<td><div id=\"countdown\">Countdown</div></td>\r\n						</tr>\r\n					</table>\r\n					<br />\r\n					<!-- Make offer begin-->\r\n					<div id=\"login_offer\" style=\"display: {DISPLAY_LOGIN};\">\r\n						<form action=\"index.php?section=login\" method=\"post\">\r\n						<input name=\"redirect\" value=\"{REDIRECT_URL}\" type=\"hidden\" />\r\n						<table cellpadding=\"2\" cellspacing=\"0\" border=\"0\" style=\"background-color: #E4E4E4;\">\r\n							<tr>\r\n								<td width=\"150\"><b>{TXT_AUCTION_USENAME}</b></td>\r\n								<td><input type=\"text\" value=\"\" name=\"USERNAME\" style=\"width: 100px;\" /></td>\r\n							</tr>\r\n							<tr>\r\n								<td width=\"150\"><b>{TXT_AUCTION_PASSWORD}</b></td>\r\n								<td><input type=\"password\" value=\"\" name=\"PASSWORD\" style=\"width: 100px;\" /></td>\r\n							</tr>\r\n							<tr>\r\n								<td width=\"150\">&nbsp;</td>\r\n								<td><input type=\"submit\" value=\"{TXT_LOGIN}\" name=\"login\" /></td>\r\n							</tr>\r\n						</table>\r\n						</form>\r\n					</div>\r\n					<form name=\"frmMakeOffer\" method=\"post\" action=\"{TXT_AUCTION_URL}\">\r\n					<input type=\"hidden\" name=\"loged\" value=\"{LOGED}\" />\r\n					<input type=\"hidden\" name=\"exe\" value=\"makeOffer\" />\r\n					<table cellpadding=\