INSERT INTO `contrexx_content` VALUES (14, '<script src="lib/datepickercontrol/datepickercontrol.js" type="text/javascript"></script>\r\n<script type="text/javascript">\r\n/* <![CDATA[ */\r\nfields = new Array();\r\nfields[1] = Array(\r\n	''Name'',\r\n	0,\r\n	''.*'',\r\n	''text'');\r\nfields[2] = Array(\r\n	''Firmenname'',\r\n	0,\r\n	''.*'',\r\n	''text'');\r\nfields[3] = Array(\r\n	''Strasse'',\r\n	0,\r\n	''.*'',\r\n	''text'');\r\nfields[4] = Array(\r\n	''PLZ'',\r\n	0,\r\n	''.*'',\r\n	''text'');\r\nfields[5] = Array(\r\n	''Ort'',\r\n	0,\r\n	''.*'',\r\n	''text'');\r\nfields[6] = Array(\r\n	''Land'',\r\n	0,\r\n	''.*'',\r\n	''text'');\r\nfields[7] = Array(\r\n	''Telefon'',\r\n	0,\r\n	''.*'',\r\n	''text'');\r\nfields[8] = Array(\r\n	''Fax'',\r\n	0,\r\n	''.*'',\r\n	''text'');\r\nfields[9] = Array(\r\n	''E-Mail'',\r\n	1,\r\n	''^[_a-zA-Z0-9-]+(\\\\.[_a-zA-Z0-9-]+)*@[a-zA-Z0-9-]+(\\\\.[a-zA-Z0-9-]+)*\\\\.(([0-9]{1,3})|([a-zA-Z]{2,3})|(aero|coop|info|museum|name))$'',\r\n	''text'');\r\nfields[10] = Array(\r\n	''Bemerkungen'',\r\n	0,\r\n	''.*'',\r\n	''textarea'');\r\nfields[11] = Array(\r\n	''Datei'',\r\n	0,\r\n	''.*'',\r\n	''file'');\r\nfunction checkAllFields() {\r\n	var isOk = true;\r\n\r\n	for (var field in fields) {\r\n		var type = fields[field][3];\r\n		if (type == ''text'' || type == ''file'' || type == ''password'' || type == ''textarea'') {\r\n			value = document.getElementsByName(''contactFormField_'' + field)[0].value;\r\n			if (value == "" && isRequiredNorm(fields[field][1], value)) {\r\n				isOk = false;\r\n				document.getElementsByName(''contactFormField_'' + field)[0].style.border = "red 1px solid";\r\n			} else if (value != "" && !matchType(fields[field][2], value)) {\r\n				isOk = false;\r\n				document.getElementsByName(''contactFormField_'' + field)[0].style.border = "red 1px solid";\r\n			} else {\r\n				document.getElementsByName(''contactFormField_'' + field)[0].style.borderColor = '''';\r\n			}\r\n		} else if (type == ''checkbox'') {\r\n			if (!isRequi