uttons[i].checked) {\r\n				checked = true;\r\n			}\r\n		}\r\n		if (checked) {\r\n			setListBorder(''contactFormField_'' + field, false);\r\n			return true;\r\n		} else {\r\n			setListBorder(''contactFormField_'' + field, ''1px red solid'');\r\n			return false;\r\n		}\r\n	} else {\r\n		return true;\r\n	}\r\n}\r\nfunction setListBorder(field, borderColor) {\r\n	var boxes = document.getElementsByName(field);\r\n	for (var i = 0; i < boxes.length; i++) {\r\n		if (borderColor) {\r\n			boxes[i].style.border = borderColor;\r\n		} else {\r\n			boxes[i].style.borderColor = '''';\r\n		}\r\n	}\r\n}\r\n/* ]]> */\r\n</script>\r\n{CONTACT_FEEDBACK_TEXT}\r\nDies ist ein Beispiel Kontaktformular, welches mit dem Kontaktemanager erstellt worden ist.<br /><br />\r\n<div id="contactFormError" style="color: red; display: none;">Bitte überprüfen Sie ihre Eingabe!</div>\r\n<br /><!-- BEGIN contact_form -->\r\n<form action="index.php?section=contact&amp;cmd=1" method="post" enctype="multipart/form-data" onsubmit="return checkAllFields();" id="contactForm">\r\n<table border="0">\r\n<tr>\r\n<td style="width:100px;">Name </td>\r\n<td><input style="width:300px;" type="text" name="contactFormField_1" value="" />\r\n</td>\r\n</tr>\r\n<tr>\r\n<td style="width:100px;">Firmenname </td>\r\n<td><input style="width:300px;" type="text" name="contactFormField_2" value="" />\r\n</td>\r\n</tr>\r\n<tr>\r\n<td style="width:100px;">Strasse </td>\r\n<td><input style="width:300px;" type="text" name="contactFormField_3" value="" />\r\n</td>\r\n</tr>\r\n<tr>\r\n<td style="width:100px;">PLZ </td>\r\n<td><input style="width:300px;" type="text" name="contactFormField_4" value="" />\r\n</td>\r\n</tr>\r\n<tr>\r\n<td style="width:100px;">Ort </td>\r\n<td><input style="width:300px;" type="text" name="contactFormField_5" value="" />\r\n</td>\r\n</tr>\r\n<tr>\r\n<td style="width:100px;">Land </td>\r\n<td><input style="width:300px;" type="text" name="contactFormField_6" value="" />\r\n</td>\r\n</tr>\r\n<tr>\r\n<td style=